INSERT IGNORE `achievement_reward`(`ID`, `TitleA`, `TitleH`, `ItemID`, `Sender`, `Subject`, `Body`, `MailTemplateID`) VALUES (2137, 0, 0, 44175, 26917, 'Glory of the Raider (10 player)', 'Champion, $B$BWord has traveled to Wyrmrest Temple of the great deeds you have accomplished since arriving in Northrend. $B$BYour bravery should not go unrecognized. Please accept this gift on behalf of the Aspects. Together we shall rid Azeroth of evil, once and for eternity. $B$BAlexstrasza the Life-Binder.', 0);
