INSERT IGNORE `achievement_reward`(`ID`, `TitleA`, `TitleH`, `ItemID`, `Sender`, `Subject`, `Body`, `MailTemplateID`) VALUES (2398, 0, 0, 0, 32842, 'WoW\'s 4th anniversary!', 'Do you know that the World of Warcraft is now four years old? $B$BWe wanted to send you something to say thanks for sharing this game with us. Sure, the 4th anniversary present is traditionally \"flowers\"... but in WoW, it\'s \"bear\". $B$BWith that said, please accept this furry little friend! Just remember to hold his fish by the tail, not the body... that mouth is bigger than it looks. $B$BThe WoW Dev Team.', 0);
INSERT IGNORE `achievement_reward`(`ID`, `TitleA`, `TitleH`, `ItemID`, `Sender`, `Subject`, `Body`, `MailTemplateID`) VALUES (3636, 0, 0, 0, 24380, 'Jade Tiger', NULL, 0);
INSERT IGNORE `achievement_reward`(`ID`, `TitleA`, `TitleH`, `ItemID`, `Sender`, `Subject`, `Body`, `MailTemplateID`) VALUES (4400, 0, 0, 0, 32842, 'WoW\'s 5th anniversary!', 'Wow, another year has gone by for WoW! $B$BWe wanted to thank you again for continuing to play World of Warcraft with us. In celebration of our 5th year online, we decided to bring back our first raid encounter, the black dragon Onyxia. $B$BTo help remember the event, you are now the proud owner of your very own Onyxian Whelpling. We suggest you bring the little guy out whenever you can and say, \"Many whelps! Handle it!\" Your friends won\'t mind. Honest. $B$BThe WoW Dev Team.', 0);
