INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (26392, 3, 0, 'The tabard was pink when I got it back, Buckets. Pink! Think about that...The whole squad laughed at me for a week! Don\'t let it happen again!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 4');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (26392, 4, 0, 'What\'s wrong with you? That was dinner!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 5');
