INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27178, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'combat Flee');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27191, 0, 0, 'I\'m gonna jump this time Brote, I swear it!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 0, 0, 'If I get out of here, I will CRUSH every clock I come into contact with...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 1, 0, 'And you know what pisses me off more than anything, Skrotee? No, not that we\'ve been made into a living cuckoo clock for the amusement of the Forsaken. And not that we have to endure being spat upon and mocked every waking minute of our time here... What pisses me off the most is being made to wear these damned short-shorts...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 2');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 2, 0, 'I hope one of us dies. I hope that one is you, Skrotee.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 3');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 3, 0, 'Of all the hair-brained, idiotic, BRAINLESS ideas... \"Yeah Brote, it\'ll be great... The Forsaken will rain gold coins down on our heads!\"', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 4');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 4, 0, 'This is all your fault, you know...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 5');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 5, 0, 'Pray Scrotee... Find religion... Because if we ever get out of here, you will be wishing you were back in here!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 6');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27198, 6, 0, 'Has it really been an hour?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Text 7');
