INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27440, 0, 0, 'Saurfang is here, Sagai! Saurfang! The brother of Broxigar himself, here to aid our efforts!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Text 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (27441, 0, 0, 'Yah. Hellscream, he be a fierce one, but too eager to prove himself, I be thinkin. It be good Saurfang be here, for sure', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Text 1');
