INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28610, 8, 1, 'Tell me what you know about \"Crimson Dawn\" or the beatings will continue!', 12, 0, 100, 0, 0, 0, 0, 29134, 0, 'player SAY_PERSUADE2');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28610, 8, 2, 'I\'m through being courteous with your kind, human! What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 0, 29135, 0, 'player SAY_PERSUADE3');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28610, 8, 3, 'Is your life worth so little? Just tell me what I need to know about \"Crimson Dawn\" and I\'ll end your suffering quickly.', 12, 0, 100, 0, 0, 0, 0, 29139, 0, 'player SAY_PERSUADE4');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28610, 8, 4, 'I can keep this up for a very long time, Scarlet dog! Tell me about the \"Crimson Dawn!\"', 12, 0, 100, 0, 0, 0, 0, 29137, 0, 'player SAY_PERSUADE5');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (28610, 8, 5, 'What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 0, 29133, 0, 'player SAY_PERSUADE6');
