INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40950, 1, 0, 'Most of our steam tanks are out of commission and we\'re losing mountaineers quickly.', 12, 0, 100, 5, 0, 0, 0, 43236, 0, 'Captain Tharran');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40950, 2, 0, 'We can\'t keep up the operation much longer. Delber and his team must launch their attack from Steelgrill\'s Depot as soon as possible!', 12, 0, 100, 6, 0, 0, 0, 43243, 0, 'Captain Tharran');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40950, 3, 0, 'Glad to hear it. I\'ll send Delber any forces I can spare.', 12, 0, 100, 273, 0, 0, 0, 43253, 0, 'Captain Tharran');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40950, 4, 0, 'There\'s no substitute for live observers, but I need those mountaineers here. We\'ll have to rely on those little gnomish observer bots for now...', 12, 0, 100, 6, 0, 0, 0, 43522, 0, 'Captain Tharran to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40951, 0, 0, 'It won\'t be long before we\'re out of supplies too, Captain. Between the trolls and the yeti raids, we\'ll be down to half rations within a week.', 12, 0, 100, 1, 0, 0, 0, 43242, 0, 'Quartermaster Glynna to Captain Tharran');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40991, 0, 0, 'Tell Captain Tharran that I\'ll be back in Kharanos as soon as I\'ve verified that the bot is working correctly.', 12, 0, 100, 66, 0, 0, 0, 41005, 0, 'Mountaineer Dunstan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (40994, 0, 0, 'I can\'t wait to get out there and help in the fight against those trolls.', 12, 0, 100, 66, 0, 0, 0, 41006, 0, 'Mountaineer Lewin to Player');
