INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 1, 0, 'As it is, there are barely enough of us to keep them from taking everything.', 12, 0, 100, 5, 0, 0, 0, 41526, 0, 'Sergeant Flinthammer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 2, 0, 'They just take the rams and go. The herd can\'t take any more.', 12, 0, 100, 1, 0, 0, 0, 41527, 0, 'Sergeant Flinthammer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41578, 3, 0, 'One of the mountaineers has seen something. Keep your eyes open!', 14, 0, 100, 25, 0, 0, 0, 41528, 0, 'Sergeant Flinthammer to Player');
