INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41611, 3, 0, 'To arms, mountaineers! Take back those rams!', 14, 0, 100, 25, 0, 0, 0, 41524, 0, 'Amberstill Mountaineer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41671, 1, 0, 'I can\'t wait to get home to my family. Thank you, stranger.', 12, 0, 100, 2, 0, 0, 0, 41542, 0, 'Trapped Miner to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (41671, 2, 0, 'Light bless you, stranger. You\'ve saved me!', 12, 0, 100, 2, 0, 0, 0, 41540, 0, 'Trapped Miner to Player');
