INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55564, 0, 0, 'Anub\'Arak has fallen, my brothers! Rise from the depths and reclaim our birthright!', 14, 0, 100, 0, 0, 14069, 0, 0, 0, 'Reclaimer A\'zak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55595, 0, 0, 'Dafeng! What\'s wrong? Why are you hiding back here?', 12, 0, 100, 1, 0, 27403, 0, 54362, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55658, 0, 0, 'Make haste and secure the area! Much work remains to be done.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Seer Ixit');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55658, 1, 0, 'The rebirth of the Empire of Azjol-Nerub is upon us! Hear my call, brothers, and rise to reclaim what is rightfully ours!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Seer Ixit');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55672, 0, 0, 'And here we are. Help me with a couple small tasks while I prepare, if you would.', 12, 0, 100, 1, 0, 27805, 0, 56485, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55694, 0, 0, 'Hey $p! This way to Morning Breeze Village.', 12, 0, 100, 0, 0, 27294, 0, 54196, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55744, 0, 0, 'Wait!', 12, 0, 100, 5, 0, 27400, 0, 54355, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55744, 1, 0, 'We need to wait for the winds to settle, then make a break for the cover of the far hallway.', 12, 0, 100, 1, 0, 27401, 0, 54356, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55744, 2, 0, 'Wait for another opening. I\'ll meet you on the far side.', 12, 0, 100, 1, 0, 27402, 0, 54357, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55943, 0, 0, 'Never met them before in my life. Pandaren don\'t really come in any bad flavors though.', 12, 0, 100, 0, 0, 28090, 0, 59554, 0, 'Wei Palerage to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 0, 0, 'We need more healers at the tents. Recall the priests from the west.', 14, 0, 100, 22, 0, 27508, 0, 60625, 0, 'Delora Lionheart');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 1, 0, 'Hold the line, sailors! Your brothers depend on you!', 14, 0, 100, 22, 0, 27505, 0, 59721, 0, 'Delora Lionheart');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 2, 0, 'Tend to the wounded! This isn\'t over yet.', 14, 0, 100, 22, 0, 27506, 0, 59722, 0, 'Delora Lionheart');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (55944, 3, 0, 'We have men in that wreckage! Get more men out there recovering the wounded.', 14, 0, 100, 22, 0, 27507, 0, 60624, 0, 'Delora Lionheart');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56013, 0, 0, 'You\'ve all accomplished a great thing in saving Shen-zin Su. You do us all proud.', 12, 0, 100, 1, 0, 27784, 0, 60632, 0, 'Spirit of Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56013, 1, 0, 'It seems your journeys are not done. These new allies of ours come from a broken world that could use our help.', 12, 0, 100, 1, 0, 27785, 0, 60633, 0, 'Spirit of Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56013, 2, 0, 'What of you, Ji? Will you join the Horde in their journey back?', 12, 0, 100, 6, 0, 27786, 0, 60629, 0, 'Spirit of Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56013, 3, 0, 'And you, my young student? Where will you go?', 12, 0, 100, 6, 0, 27787, 0, 60631, 0, 'Spirit of Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56041, 0, 0, '|cFFFF0000BEWARE:|r Moonfang Dreadhowls now prowl the woods!', 41, 0, 100, 0, 0, 0, 0, 74594, 0, 'Rona Greenteeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56159, 0, 0, 'Come child. We have one final journey to take together before your training is complete.', 12, 0, 100, 1, 0, 27797, 0, 55520, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56159, 1, 0, 'Beyond the Elders\' Path lies the Wood of Staves, a sacred place that only the worthy may enter.', 12, 0, 100, 1, 0, 27800, 0, 55521, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56159, 2, 0, 'Of the many ways to prove your worth, I require the simplest of you now. I must know that you will fight for our people. I must know that you can keep them safe.', 12, 0, 100, 1, 0, 27801, 0, 55522, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56159, 3, 0, 'Defeat the Guardian of the Elders, and we may pass.', 12, 0, 100, 1, 0, 27802, 0, 55523, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56159, 4, 0, 'You\'ve become strong indeed, child. This is good. You will need that strength soon.', 12, 0, 100, 1, 0, 27803, 0, 56484, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 0, 'I... I am in your debt.', 12, 0, 100, 0, 0, 0, 0, 55139, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 1, 'I\'m... alive?  Thank you... thank you so much!', 12, 0, 100, 18, 0, 0, 0, 63840, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 2, 'I can... I can barely breathe.  I\'m alive thanks to you.', 12, 0, 100, 18, 0, 0, 0, 64337, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 3, 'I\'m alive thanks only to you... I won\'t forget this.', 12, 0, 100, 18, 0, 0, 0, 64342, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 4, 'I thought that was it... I thought I was going to die.  Thank you, stranger.', 12, 0, 100, 18, 0, 0, 0, 64344, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 5, 'I owe you my life.', 12, 0, 100, 2, 0, 0, 0, 64348, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56236, 0, 6, 'Thank you for the timely rescue, hero.', 12, 0, 100, 2, 0, 0, 0, 64355, 0, 'Injured Sailor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56416, 0, 0, 'Thank Shen-zin Su that you\'re here.  I could use your help!', 12, 0, 100, 0, 0, 27384, 0, 64848, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56416, 1, 0, 'Get clear when it when it raises its weapon, it\'s even stronger than it looks.', 12, 0, 100, 0, 0, 27385, 0, 64849, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56416, 2, 0, 'It\'s calling allies!  I\'ll keep him busy while you handle them.', 12, 0, 100, 0, 0, 27386, 0, 64850, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56416, 3, 0, 'We\'re wearing him down.  Keep it up!', 12, 0, 100, 0, 0, 27387, 0, 64852, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56416, 4, 0, 'I don\'t know what I would have done without you.  It\'s good that you came.', 12, 0, 100, 0, 0, 27388, 0, 64853, 0, 'Aysa Cloudsinger to Vordraka, the Deep Sea Nightmare');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56418, 0, 0, 'There are healers under some of the wreckage!  Get them free.', 14, 0, 100, 0, 0, 27285, 0, 66060, 0, 'Ji Firepaw');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56418, 0, 1, 'Find the healers and get them free of the fight.  We need them here at the wound!', 14, 0, 100, 0, 0, 27286, 0, 66061, 0, 'Ji Firepaw');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56418, 0, 2, 'Stay close and keep these healers safe.  Our lives depend on it.', 14, 0, 100, 0, 0, 27287, 0, 66062, 0, 'Ji Firepaw');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56418, 0, 3, 'If we lose the healers, Shen-zin Su dies, and all of us with him!  Keep them safe.', 14, 0, 100, 0, 0, 27288, 0, 66063, 0, 'Ji Firepaw');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56503, 0, 0, 'Master Shang has trained you well. Thank you, friend!', 12, 0, 100, 2, 0, 0, 0, 53265, 0, 'Min Dimwind to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56503, 1, 0, 'I couldn\'t have fought them off alone. Now, if you\'ll excuse me, I should go find my cart.', 12, 0, 100, 1, 0, 0, 0, 56452, 0, 'Min Dimwind to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56503, 2, 0, 'Cart!', 14, 0, 100, 22, 0, 0, 0, 56453, 0, 'Min Dimwind to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56503, 3, 0, 'Hello, cart. Still upside-downed, I see.', 12, 0, 100, 2, 0, 0, 0, 56454, 0, 'Min Dimwind to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 0, 0, '$p, where\'s Master Shang?', 12, 0, 100, 6, 0, 27297, 0, 55554, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 1, 0, 'Bah, let a pandaren hope, would you?  I\'m going to miss the old man.', 12, 0, 100, 1, 0, 27298, 0, 55556, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 2, 0, 'When am I not respectful?  You hurt me, Aysa.', 12, 0, 100, 6, 0, 27299, 0, 55561, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 3, 0, 'A thorn?  And I left my tweezers at home.', 12, 0, 100, 6, 0, 27300, 0, 55574, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 4, 0, 'How could such a thing cause pain to something so large?', 12, 0, 100, 6, 0, 27301, 0, 55575, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 5, 0, 'Are you seeing what I\'m seeing?!  Is that a boat?!', 12, 0, 100, 5, 0, 27302, 0, 55577, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56660, 6, 0, 'And those aren\'t pandaren down there.  They\'ve got no fur.', 12, 0, 100, 1, 0, 27303, 0, 55579, 0, 'Ji Firepaw to Aysa Cloudsinger');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 0, 0, '...Ji, they were in the Wood of Staves.  You know where Master Shang is now.', 12, 0, 100, 1, 0, 27431, 0, 55555, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 1, 0, 'Ji, be respectful when we speak to Shen-zin Su.', 12, 0, 100, 1, 0, 27432, 0, 55560, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 2, 0, 'I might if you embarrass us.', 12, 0, 100, 1, 0, 27433, 0, 55562, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 3, 0, 'Shen-zin Su, we are the descendants of Liu Lang.  We\'ve sensed your pain, and we want to help.', 14, 0, 100, 1, 0, 27434, 0, 55563, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 4, 0, 'What ails you Shen-zin Su?  What can we do?', 14, 0, 100, 6, 0, 27435, 0, 55564, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 5, 0, 'Of course, Shen-zin Su!  But your shell is large, and I do not know where this thorn could be.', 14, 0, 100, 1, 0, 27436, 0, 55571, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 6, 0, 'We will find it, and we will remove it.  You have our word!', 14, 0, 100, 1, 0, 27437, 0, 55573, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 7, 0, 'We\'ll know soon enough.', 12, 0, 100, 1, 0, 27438, 0, 55576, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 8, 0, 'It is a boat... a whole airship!  That\'s a bigger thorn than I was expecting.', 12, 0, 100, 1, 0, 27439, 0, 55578, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 9, 0, 'Someone has crashed into our island.  Removing this thorn may be more complicated than we thought.', 12, 0, 100, 1, 0, 27440, 0, 55581, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56661, 10, 0, 'We should let Elder Shaopai know and then plan our next move.', 12, 0, 100, 1, 0, 27441, 0, 55582, 0, 'Aysa Cloudsinger to Shang Xi\'s Hot Air Balloon');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56662, 0, 0, 'We\'re ready to go whenever you are, $n.', 12, 0, 100, 1, 0, 27428, 0, 55549, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56662, 1, 0, 'Don\'t listen to him.  We\'ve got work to do.', 12, 0, 100, 1, 0, 27430, 0, 55553, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56662, 2, 0, 'Hop in.', 12, 0, 100, 1, 0, 27429, 0, 55551, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56663, 0, 0, 'Or if you want to wander the woods a while, Aysa and I can hang out here.  You know... just the two of us.', 12, 0, 100, 1, 0, 27296, 0, 55552, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 0, 0, 'I am in pain, but it warms my heart that Liu Lang\'s grandchildren have not forgotten me.', 12, 0, 100, 0, 0, 27822, 0, 55550, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 1, 0, 'There is a thorn in my side.  I cannot remove it.', 12, 0, 100, 0, 0, 27823, 0, 55568, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 2, 0, 'The pain is unbearable, and I can no longer swim straight.', 12, 0, 100, 0, 0, 27824, 0, 55569, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 3, 0, 'Please grandchildren, can you remove this thorn?  I cannot do so on my own.', 12, 0, 100, 0, 0, 27825, 0, 55570, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 4, 0, 'It is in the forest where your feet do not walk.  Continue along the mountains and you will find it.', 12, 0, 100, 0, 0, 27826, 0, 55572, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56676, 5, 0, 'Thank you, grandchildren.', 12, 0, 100, 0, 0, 27827, 0, 63407, 255, 'Shen-zin Su to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 0, 0, 'For 3,000 years, we have passed the knowledge of our people down. Elder to youth. Master to student.', 12, 0, 100, 1, 0, 27807, 0, 55528, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 1, 0, 'Every elder reaches the day where he must pass on and plant his stave with the staves of his ancestors. Today is the day when my staff joins these woods.', 12, 0, 100, 1, 0, 27808, 0, 55529, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 2, 0, '$p, our people have lived the wholes of their lives on this great turtle, Shen-zin Su, but not in hundreds of years has anyone spoken to him.', 12, 0, 100, 1, 0, 27809, 0, 55530, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 3, 0, 'Now Shen-zin Su is ill, and we are all in danger. With the help of the elements, you will break the silence. You will speak to him.', 12, 0, 100, 1, 0, 27810, 0, 55531, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 4, 0, 'Aysa and Ji have retrieved the spirits and brought them here. You are to go with them, speak to the great Shen-zin Su, and do what must be done to save our people.', 12, 0, 100, 1, 0, 27811, 0, 55532, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (56686, 5, 0, 'You\'ve come far, my young student. I see within you a great hero. I leave the fate of this land to you.', 12, 0, 100, 396, 0, 27812, 0, 55533, 0, 'Master Shang Xi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57638, 0, 0, 'The reeds of the Singing Pools are the hardest in all of the land, but they are as air before my mighty brow.', 12, 0, 100, 1, 0, 0, 0, 56333, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57638, 1, 0, 'Many have tested my claim, and I yet stand proven.', 12, 0, 100, 2, 0, 0, 0, 56334, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57669, 0, 0, 'Our crafters build the finest wooden planks.  They would weather the most brutal of storms.  But they are as water before the storm of my crushing skull.', 12, 0, 100, 1, 0, 0, 0, 56343, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57669, 1, 0, 'Nothing made by pandaren hands can withstand me.', 12, 0, 100, 0, 0, 0, 0, 56344, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57670, 0, 0, 'These are the densest stones across all of Shen-zin Su, yet they are as nothing before my mighty head.', 12, 0, 100, 1, 0, 0, 0, 56355, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57670, 1, 0, 'Any who yet doubt my strength are simply foolish.', 12, 0, 100, 2, 0, 0, 0, 56356, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57692, 0, 0, 'This is magnificent!  I\'ve never seen craftsmanship so fine.', 12, 0, 100, 1, 0, 0, 0, 56363, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57692, 1, 0, 'There is only one thing to do.  I must smash it!  Once and for all, all shall know the power of my mighty brow.', 12, 0, 100, 1, 0, 0, 0, 56361, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57692, 2, 0, 'Oh... my head... it hurts so badly!  I\'ve... the pillar must contain enormous power... perhaps stronger even than my brow...', 12, 0, 100, 18, 0, 0, 0, 56362, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57692, 3, 0, 'I\'m... I\'m just going to lie down a while... until this headache goes away.', 12, 0, 100, 0, 0, 0, 0, 56364, 0, 'Jojo Ironbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57712, 0, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Dai-Lo Farmstead.', 12, 0, 100, 3, 0, 0, 0, 56406, 0, 'Delivery Cart Tender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57712, 1, 0, 'Hello friend!  You\'re welcome to use my cart if you like.  It will take you to the Temple of Five Dawns.', 12, 0, 100, 3, 0, 0, 0, 56415, 0, 'Delivery Cart Tender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57720, 0, 0, 'Yes... perhaps that is for the best. I like what I\'ve seen of them, and it sounds as though their world could use my help.', 12, 0, 100, 273, 0, 27305, 0, 60630, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (57721, 0, 0, 'Master Shang Xi, I would like to accompany the Alliance back to their home.', 12, 0, 100, 1, 0, 27405, 0, 60628, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 0, 0, 'I...won\'t let you...', 14, 0, 100, 0, 0, 29464, 0, 58423, 1, 'Lilian Voss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 1, 0, 'I can\'t...fight him...', 12, 0, 100, 0, 0, 29465, 0, 58424, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 2, 0, 'END IT! NOW! Before he can...', 14, 0, 100, 0, 0, 29463, 0, 58425, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 3, 0, 'DIE, NECROMANCER!', 14, 0, 100, 0, 0, 29466, 0, 58527, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 4, 0, 'Leave me...to die...alone...please...', 12, 0, 100, 0, 0, 29467, 0, 58528, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 5, 0, 'It burns...my soul...it burns!!!!', 14, 0, 100, 0, 0, 29468, 0, 58492, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58722, 6, 0, '...', 12, 0, 100, 0, 0, 0, 0, 58522, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58791, 0, 0, 'Lilian\'s Soul fixates on $n!', 41, 0, 100, 0, 0, 0, 0, 59467, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58791, 1, 0, 'Lilian\'s Soul fades into the spirit realm! It can no longer be harmed!', 41, 0, 100, 0, 0, 0, 0, 66157, 1, 'Lilian Voss to 0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58951, 0, 0, 'The |cFFFF8000Orange|r orb has been returned!', 41, 0, 100, 0, 0, 9431, 0, 0, 0, 'Trigger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58951, 1, 0, 'The |cFF01DF01Green|r orb has been returned!', 41, 0, 100, 0, 0, 9431, 0, 0, 0, 'Trigger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58951, 2, 0, 'The |cFFBF00FFPurple|r orb has been returned!', 41, 0, 100, 0, 0, 12007, 0, 0, 0, 'Trigger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58951, 3, 0, 'The |cFF01DFD7Blue|r orb has been returned!', 41, 0, 100, 0, 0, 9431, 0, 0, 0, 'Trigger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (58951, 4, 0, 'The Alliance wins!', 36, 0, 100, 0, 0, 151586, 0, 0, 0, '0');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59642, 0, 0, 'Keep those creatures at bay while I meditate. We\'ll soon have the answers we seek.', 12, 0, 100, 1, 0, 27398, 0, 59161, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59652, 0, 0, 'Meet me up in the cave if you would, friend.', 12, 0, 100, 396, 0, 27397, 0, 59147, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59986, 0, 0, '$n\'s here. Let\'s go.', 12, 0, 100, 432, 0, 27420, 0, 59481, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59986, 1, 0, 'Wha...', 12, 0, 100, 432, 0, 27421, 0, 59495, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59986, 2, 0, 'The gate is jammed.', 12, 0, 100, 274, 0, 27422, 0, 59511, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59986, 3, 0, 'The Alliance? If the crashed ship is theirs, we need to contact them. Jojo, would you come with me?', 12, 0, 100, 6, 0, 27419, 0, 59583, 0, 'Aysa Cloudsinger to Ji Firepaw');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59988, 0, 0, 'Let me try.', 12, 0, 100, 5, 0, 27339, 0, 59513, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59988, 1, 0, 'Did they prop this door up against a boulder?  It won\'t budge!', 12, 0, 100, 5, 0, 27340, 0, 59515, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59988, 2, 0, 'Well done, Jojo!', 12, 0, 100, 0, 0, 27341, 0, 59521, 0, 'Ji Firepaw to Jojo Ironbrow');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59988, 3, 0, 'We need to dislodge that ship, Korga.  If we help your crewmates, can you help us?', 12, 0, 100, 1, 0, 27333, 0, 59584, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (59988, 4, 0, 'Well, we\'ve got a master person-finder here, eh $p?  We\'re on it.', 12, 0, 100, 1, 0, 27334, 0, 59586, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60042, 0, 0, 'Wei, are these friends of yours?', 12, 0, 100, 6, 0, 28120, 0, 54956, 0, 'Korga Strongmane to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60042, 1, 0, 'Korga Strongmane, once prisoner of the Alliance, now free and woefully unarmed tauren. Good to meet you, pandaren.', 12, 0, 100, 66, 0, 28121, 0, 59555, 0, 'Korga Strongmane to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60042, 2, 0, 'Gladly, my new friend. Our engineer is still in the wilds though - we\'ll need to rescue him first.', 12, 0, 100, 1, 0, 28122, 0, 59585, 0, 'Korga Strongmane to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60176, 0, 0, 'The fire spirit is upset. It\'s dangerous to enter the shrine now...', 12, 0, 100, 396, 0, 0, 0, 59680, 0, 'Huojin Monk to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60183, 0, 0, 'I hope you\'re ready, $p. Jaomin Ro awaits you just over the bridge.', 12, 0, 100, 113, 0, 0, 0, 59710, 0, 'Trainee Nim to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60244, 0, 0, 'You\'re departing so soon?  I am envious of your prowess, $c. Good luck to you.', 12, 0, 100, 2, 0, 0, 0, 59711, 0, 'Trainee Guang to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60248, 0, 0, 'Is that... is that Huo?', 12, 0, 100, 25, 0, 0, 0, 59751, 0, 'Chia-hui Autumnleaf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60249, 0, 0, 'Was he hot?  I bet he was hot!', 12, 0, 100, 6, 0, 0, 0, 59876, 0, 'Deng to Cai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60249, 1, 0, 'I bet you\'re really strong, huh?  You could probably kick that bridge right in half if you wanted to!', 12, 0, 100, 6, 0, 0, 0, 59879, 0, 'Deng to Cai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60249, 2, 0, 'Bye!', 12, 0, 100, 3, 0, 0, 0, 59884, 0, 'Deng to Cai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60250, 0, 0, 'Hey there! Are you the one that brought back the fire spirit?', 12, 0, 100, 3, 0, 0, 0, 59875, 0, 'Cai to Deng');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60250, 1, 0, 'Of course he was hot! Don\'t be dumb, Deng.', 12, 0, 100, 5, 0, 0, 0, 59877, 0, 'Cai to Deng');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60250, 2, 0, 'Nuh uh. Bet they could for sure kick you over that hill, though! Hah!', 12, 0, 100, 274, 0, 0, 0, 59880, 0, 'Cai to Deng');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60250, 3, 0, 'Okay bye.', 12, 0, 100, 3, 0, 0, 0, 59878, 0, 'Cai to Deng');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60253, 0, 0, 'It is! Well done, $p!', 12, 0, 100, 71, 0, 0, 0, 59752, 0, 'Brewer Lin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 0, 0, 'Ji may kill Shen-zin Su if we let him go through with this! Come with me, $p, quickly!', 12, 0, 100, 5, 0, 27412, 0, 55140, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 1, 0, 'Ji, stop! This is reckless and stupid.', 12, 0, 100, 5, 0, 27413, 0, 60188, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 2, 0, 'If you stop this, then maybe we can come up with one.', 12, 0, 100, 274, 0, 27414, 0, 60190, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 3, 0, 'Doing THIS risks everything!', 12, 0, 100, 5, 0, 27415, 0, 60192, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 4, 0, 'Ji... if this is what you must do, I will not stop you, but we could lose everything. I hope you\'re right.', 12, 0, 100, 1, 0, 27416, 0, 60194, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60729, 5, 0, 'Aysa sighs.', 16, 0, 100, 274, 0, 0, 0, 60211, 0, 'Aysa Cloudsinger to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60735, 0, 0, 'Not running this time, Jung Duk?', 12, 0, 100, 0, 0, 28126, 0, 60259, 0, 'Katak - Jung Duk After Aggro.');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60735, 1, 0, 'Where are your lackeys, great chief? They cannot help you this time!', 12, 0, 100, 0, 0, 28127, 0, 60260, 0, 'Katak - Jung Duk After Aggro.');
