INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60889, 0, 0, 'Stand down, tauren, before I put you back in chains.', 12, 0, 100, 396, 0, 27522, 0, 60623, 0, 'Delora Lionheart to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (60900, 0, 0, 'Let\'s go find this engineer.  Maybe getting this thorn removed won\'t be as hard as we thought.', 12, 0, 100, 396, 0, 27335, 0, 60379, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (61126, 0, 0, 'Yes master.', 12, 0, 100, 2, 0, 27406, 0, 60607, 0, 'Aysa Cloudsinger to Fire Central Temple Turn-in Controller');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (61127, 0, 0, 'On it!', 12, 0, 100, 0, 0, 27306, 0, 60606, 0, 'Ji Firepaw to Fire Central Temple Turn-in Controller');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62081, 0, 0, 'Look at this!', 12, 0, 100, 5, 0, 0, 0, 61630, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62081, 1, 0, 'Well, that\'s good news. Once we join the Horde, we certainly won\'t run out of things to do.', 12, 0, 100, 1, 0, 0, 0, 61631, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62081, 2, 0, 'Emperor Hellscream, I am Ji...', 12, 0, 100, 1, 0, 27289, 0, 61633, 0, 'Ji Firepaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62081, 3, 0, 'Yes, warchief.', 12, 0, 100, 2, 0, 27290, 0, 61595, 0, 'Ji Firepaw to Garrosh Hellscream');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62081, 4, 0, 'I... I believe so. Yes.', 12, 0, 100, 273, 0, 27291, 0, 61603, 0, 'Ji Firepaw to Garrosh Hellscream');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 0, 0, 'You\'ve made a wise decision to join my Horde.', 12, 0, 100, 273, 0, 30746, 0, 61586, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 1, 0, 'There are some that would call us barbarians... the \"mongrel races\" of Azeroth.', 12, 0, 100, 396, 0, 30747, 0, 61587, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 2, 0, 'They are ignorant, and blind to reality. Look around you, pandaren.', 12, 0, 100, 274, 0, 30748, 0, 61588, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 3, 0, 'You will find no better partner in battle than an orc.', 12, 0, 100, 397, 0, 30749, 0, 61589, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 4, 0, 'The tauren have made themselves useful. Look - this one is a shaman.', 12, 0, 100, 6, 0, 30750, 0, 61590, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 5, 0, 'Even a blood elf can hold a sword.', 12, 0, 100, 396, 0, 30751, 0, 61592, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 6, 0, 'Do you see it? Everyone in my Horde earns their keep. You and your friends will be no exception.', 12, 0, 100, 396, 0, 30752, 0, 61593, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 7, 0, 'Do you understand this, pandaren?', 12, 0, 100, 6, 0, 30753, 0, 61594, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 8, 0, 'Hm. Good.', 12, 0, 100, 0, 0, 30754, 0, 61596, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 9, 0, 'Moving on. I know that you are not the only pandaren to escape from that island.', 12, 0, 100, 396, 0, 30755, 0, 61597, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 10, 0, 'Some of your kind chose to join the Alliance.', 12, 0, 100, 14, 0, 30756, 0, 61598, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 11, 0, 'They are now your enemies.', 12, 0, 100, 397, 0, 30757, 0, 61599, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 12, 0, 'You may have had friends and kin who chose to cast their lot with the enemies of the Horde. They are no longer your friends... no longer your family.', 12, 0, 100, 396, 0, 30758, 0, 61600, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 13, 0, 'The minute they put on that Alliance tabard, they died. I will NOT tolerate any lingering ties across enemy lines. Traitors to the Horde will die a traitor\'s death!', 12, 0, 100, 274, 0, 30759, 0, 61601, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 14, 0, 'Do I make myself clear?', 12, 0, 100, 5, 0, 30760, 0, 61602, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 15, 0, 'Good.', 12, 0, 100, 0, 0, 30761, 0, 61604, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62087, 16, 0, 'Meet me in the Valley of Honor. I have a gift for you.', 12, 0, 100, 396, 0, 30762, 0, 61605, 0, 'Garrosh Hellscream to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62195, 0, 0, 'Look! It\'s Ji Firepaw and $p!', 14, 0, 100, 66, 0, 0, 0, 61628, 0, 'Shang\'gok to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (62195, 1, 0, 'They\'re the ones who helped Korga Strongmane escape from the Alliance!', 12, 0, 100, 66, 0, 0, 0, 61629, 0, 'Shang\'gok to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64367, 0, 0, 'La esencia de Yu\'lon te sonrÃ­e con amabilidad y desaparece en el cielo.', 16, 0, 100, 0, 0, 0, 0, 72870, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64367, 1, 0, 'La esencia de Xuen ruge triunfalmente mientras se dispersa en polvo celestial.', 16, 0, 100, 0, 0, 0, 0, 72876, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64367, 2, 0, 'La esencia de Chi-Ji se disipa, dejando solo un rastro de polvo de estrellas.', 16, 0, 100, 0, 0, 0, 0, 72869, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64367, 3, 0, 'La esencia de Niuzao muge cuando acepta tu ayuda.', 16, 0, 100, 0, 0, 0, 0, 72871, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64506, 0, 0, 'That\'s the way to do it!', 12, 0, 100, 0, 0, 27380, 0, 64766, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64506, 1, 0, 'You\'ve done it!  Now quickly, lets attack it while it\'s grounded.', 12, 0, 100, 0, 0, 27381, 0, 64767, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64506, 2, 0, 'It\'s down!  Let\'s finish it off.', 12, 0, 100, 0, 0, 27382, 0, 64768, 0, 'Aysa Cloudsinger to Zhao-Ren');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 0, 0, 'This is the Song of Liu Lang, the first pandaren explorer.', 12, 0, 100, 1, 0, 0, 0, 65583, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 1, 0, 'The song is written in the old tongue, the language of emperors and scholars. Hardly anyone speaks it anymore.', 12, 0, 100, 6, 0, 0, 0, 65584, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 2, 0, 'It is all about his adventures.', 12, 0, 100, 273, 0, 0, 0, 65585, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 3, 0, 'Brave Liu Lang set out to explore the world on the back of a sea turtle.', 12, 0, 100, 1, 0, 0, 0, 65586, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 4, 0, 'Does anyone remember the turtle\'s name? Yin?', 12, 0, 100, 25, 0, 0, 0, 65587, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 5, 0, 'That\'s right, the turtle was called Shen-zin Su. At first, he was only big enough for Liu Lang to sit on.', 12, 0, 100, 1, 0, 0, 0, 65588, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 6, 0, 'He grew and grew and grew, SO big, that now some people call him \"The Wandering Isle.\"', 12, 0, 100, 1, 0, 0, 0, 65589, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 7, 0, 'Question, Hao?', 12, 0, 100, 25, 0, 0, 0, 65590, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 8, 0, 'Yes, Hao - our home is on Shen-zin Su\'s back.', 12, 0, 100, 273, 0, 0, 0, 65591, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 9, 0, 'Liu Lang discovered many things as he explored the world - oh - yes, Hao?', 12, 0, 100, 6, 0, 0, 0, 65592, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 10, 0, 'That\'s a good question. Nobody has spoken to Shen-zin Su for many generations. He only ever spoke to Liu Lang.', 12, 0, 100, 1, 0, 0, 0, 65593, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 11, 0, 'But I am certain that Shen-zin Su knows we are here. And he cares for every one of us!', 12, 0, 100, 66, 0, 0, 0, 65594, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 12, 0, 'Especially Hao! Liu Lang once said, \"Noble is he who always asks questions.\"', 12, 0, 100, 6, 0, 0, 0, 65595, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 13, 0, '\"We should all be like children, for the world is our elder, and has many things to teach us.\"', 12, 0, 100, 1, 0, 0, 0, 65596, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 14, 0, 'Students! Students! Pay attention now.', 12, 0, 100, 22, 0, 0, 0, 65597, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 15, 0, 'Does anyone know how often Liu Lang went back to Pandaria?', 12, 0, 100, 1, 0, 0, 0, 65598, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 16, 0, 'Haha, no Yin. Liu Lang would eat wherever he explored.', 12, 0, 100, 11, 0, 0, 0, 65599, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 17, 0, 'He returned to the main continent of Pandaria once every five years.', 12, 0, 100, 1, 0, 0, 0, 65600, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 18, 0, 'You are right, Yin. It IS hidden away, behind a cloak of mists.', 12, 0, 100, 274, 0, 0, 0, 65601, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 19, 0, 'Does anyone remember how Liu Lang was always able to find it? Nan?', 12, 0, 100, 6, 0, 0, 0, 65602, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 20, 0, 'That\'s right! Liu Lang always had a way back home. Every five years, he would return, to pick up more explorers.', 12, 0, 100, 1, 0, 0, 0, 65603, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 21, 0, 'Yes Hao! Only the BRAVEST pandaren joined Liu Lang on his turtle to explore the world.', 12, 0, 100, 273, 0, 0, 0, 65604, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 22, 0, 'Shen-zin Su hasn\'t made landfall for a long time. But that hasn\'t stopped some pandaren from exploring, anyway.', 12, 0, 100, 1, 0, 0, 0, 65605, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 23, 0, 'Yes, like Chen and Li Li! They\'re out, exploring the world. Maybe someday you will, too.', 12, 0, 100, 21, 0, 0, 0, 65606, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 24, 0, 'I\'m glad you asked, Yin. Liu Lang explored the world his entire life, and raised many children here on the Wandering Isle.', 12, 0, 100, 1, 0, 0, 0, 65607, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 25, 0, 'Eventually he grew very old, and visited Pandaria one final time.', 12, 0, 100, 1, 0, 0, 0, 65608, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 26, 0, 'But nobody else wanted to go exploring with him. So he left.', 12, 0, 100, 6, 0, 0, 0, 65609, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 27, 0, 'Shen-zin Su has never returned to Pandaria.', 12, 0, 100, 274, 0, 0, 0, 65610, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 28, 0, 'Tired now, Liu Lang said goodbye to Shen-zin Su, his oldest friend.', 12, 0, 100, 1, 0, 0, 0, 65611, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 29, 0, 'Then, Liu Lang went up to the Wood of Staves, carrying with him a bamboo umbrella that he always took on his adventures.', 12, 0, 100, 1, 0, 0, 0, 65612, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 30, 0, 'He opened his umbrella, planted it in the ground, and sat underneath its cool shade.', 12, 0, 100, 6, 0, 0, 0, 65613, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 31, 0, 'He closed his eyes, and became one with the land. And then - do you know what happened?', 12, 0, 100, 2, 0, 0, 0, 65614, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 32, 0, 'His umbrella... sprouted! It grew roots, flowered, and became a tree!', 12, 0, 100, 5, 0, 0, 0, 65615, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 33, 0, 'Well, it\'s true. If you ever go to the Wood of Staves, you can see it.', 12, 0, 100, 1, 0, 0, 0, 65616, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 34, 0, 'Along with the budding staves of all the elders who came before us, growing now into giant trees.', 12, 0, 100, 274, 0, 0, 0, 65617, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 35, 0, 'It\'s not a sad story! Liu Lang himself said, \"Never mourn a life well-lived.\"', 12, 0, 100, 1, 0, 0, 0, 65618, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 36, 0, 'And I think he lived a very very good life. We owe everything we have to Liu Lang, the first pandaren explorer.', 12, 0, 100, 273, 0, 0, 0, 65619, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 37, 0, 'We tell his story, so that he will always be remembered.', 12, 0, 100, 2, 0, 0, 0, 65620, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 38, 0, 'Thank you, Ruolin! That was beautiful.', 12, 0, 100, 21, 0, 0, 0, 65621, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64875, 39, 0, 'Could you sing it once more, for the students who just arrived?', 12, 0, 100, 6, 0, 0, 0, 65622, 0, 'Lorewalker Amai to Lorewalker Ruolin');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 0, 0, 'Shen-zin Su!', 12, 0, 100, 1, 0, 0, 0, 65635, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 1, 0, '...Whenever he got hungry?', 12, 0, 100, 1, 0, 0, 0, 65645, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 2, 0, 'But isn\'t Pandaria hidden somewhere? Nobody can find it!', 12, 0, 100, 1, 0, 0, 0, 65646, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 3, 0, 'But we don\'t explore very much anymore, do we?', 12, 0, 100, 1, 0, 0, 0, 65649, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 4, 0, 'Lorewalker! What ever happened to Liu Lang?', 12, 0, 100, 1, 0, 0, 0, 65654, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64879, 5, 0, 'That\'s a sad story!', 12, 0, 100, 1, 0, 0, 0, 65658, 0, 'Yin to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 0, 0, 'What is the song about?', 12, 0, 100, 1, 0, 0, 0, 65634, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 1, 0, 'Sheesh, Hao. Haven\'t you ever paid attention?', 12, 0, 100, 1, 0, 0, 0, 65638, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 2, 0, 'Even Hao?', 12, 0, 100, 6, 0, 0, 0, 65641, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 3, 0, 'Blerch! I\'d rather jump off this bridge.', 12, 0, 100, 1, 0, 0, 0, 65644, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 4, 0, 'Because sea turtles always return to the beach where they were born!', 12, 0, 100, 1, 0, 0, 0, 65647, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 5, 0, 'Like Chen and Li Li Stormstout!', 12, 0, 100, 1, 0, 0, 0, 65650, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 6, 0, 'No you don\'t. It\'s full of trolls.', 12, 0, 100, 1, 0, 0, 0, 65652, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64880, 7, 0, 'What? What?', 12, 0, 100, 1, 0, 0, 0, 65656, 0, 'Nan to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 0, 0, 'Why can\'t I understand any of the words?', 12, 0, 100, 1, 0, 0, 0, 65631, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 1, 0, 'We live on the back of a big turtle?!', 12, 0, 100, 5, 0, 0, 0, 65637, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 2, 0, 'I just thought it was weird that the mountains had flippers!', 12, 0, 100, 1, 0, 0, 0, 65639, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 3, 0, 'Does the turtle know we\'re here?', 12, 0, 100, 1, 0, 0, 0, 65640, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 4, 0, 'Hey!', 12, 0, 100, 1, 0, 0, 0, 65642, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 5, 0, 'Yeah, you should all be more like me.', 12, 0, 100, 1, 0, 0, 0, 65643, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 6, 0, 'Does that mean our moms and dads and grandparents were all explorers, too?', 12, 0, 100, 1, 0, 0, 0, 65648, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 7, 0, 'I want to explore the world!', 12, 0, 100, 1, 0, 0, 0, 65651, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 8, 0, 'Oh. Well, forget it then.', 12, 0, 100, 1, 0, 0, 0, 65653, 0, 'Hao to Lorewalker Amai');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (64881, 9, 0, 'What?', 12, 0, 100, 1, 0, 0, 0, 65655, 0, 'Hao to Lorewalker Amai');
