INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 1, 0, 'Hearts of Fire, will of Iron, back on your feet, grind them to dust!', 14, 0, 100, 0, 0, 37189, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Final 02');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 2, 0, 'Shimmer Ridge. I know it well. The Frostmane trolls breed and plot above the snow-blown mesa.', 12, 0, 100, 0, 0, 37190, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 3, 0, 'We\'ve lost many mountaineers to this freakish storm, or the trolls slithering about in it.', 12, 0, 100, 0, 0, 37191, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 2');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 4, 0, 'Aye. I am loathe to send my men into that. It reeks of ambush and bloody disaster.', 12, 0, 100, 0, 0, 37192, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 3');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 5, 0, 'If they can stop that storm, I\'ll lead the Forgeguard to the top myself.', 12, 0, 100, 0, 0, 37193, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 4');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 6, 0, 'Indeed. With the aid of your champions, the Dark Irons will put an end to the Zandalari threat in Dun Morogh.', 12, 0, 100, 0, 0, 37194, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 5');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 7, 0, 'We will prove our loyalty - to the Council, Ironforge, and the Alliance - here, today, and with our own blood.', 12, 0, 100, 0, 0, 37195, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 6');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 8, 0, 'I\'ll see you when it\'s done, King of Stormwind.', 12, 0, 100, 0, 0, 37196, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Start 7');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 9, 0, 'The king\'s champions have done it. The storm has ended! Forward Dark Irons! TO BATTLE WE MARCH!', 14, 0, 100, 0, 0, 37197, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Storm 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 10, 0, 'That they are, King Varian. And at the service of the Alliance, I might add.', 12, 0, 100, 0, 0, 37198, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Victory 1');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70279, 11, 0, 'Better they\'re grateful for the clansmen spared death on this miserable hilllock. We sought to protect and impress,', 12, 0, 100, 0, 0, 37199, 0, 0, 0, 'Moira Thaurissan, Blood on the Snow Victory 2');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70602, 0, 0, 'Oh ho ho ho ho! By my beard. I thought I was done for! Oh, thank ye. Thank ye!', 12, 0, 100, 0, 0, 37296, 0, 0, 0, 'Scout Stonebeard, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70602, 1, 0, 'Oh... right. Now: Did the human king send ye? Oh, his spies are all dead... and most of us are, too. I saw \'em draggin\' Boldbrew, kickin\' and bitin\', towards some hut south o\' here. Now, if ye hurry, ye can help her, and she might know more. BUT STAY AWAY FROM THE BIG VILLAGE! There\'s dozens o\' mad ice trolls in there! Oh, that Zandalari priest has \'em whipped up into a great frenzy.', 12, 0, 100, 0, 0, 37297, 0, 0, 0, 'Scout Stonebeard, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70602, 2, 0, '', 12, 0, 100, 0, 0, 37298, 0, 0, 0, 'Scout Stonebeard, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70744, 0, 0, 'Oh, that was a CLOSE one, thank ye!', 12, 0, 100, 0, 0, 37292, 0, 0, 0, 'Scout Boldbrew, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70744, 1, 0, 'There\'s only a few of us left; the ones they don\'t eat, they butcher at some big altar. It\'s just northwest o\' here; if ye hurry, ye might be able ta save some! Sorry I don\'t know anythin\' more...', 12, 0, 100, 0, 0, 37293, 0, 0, 0, 'Scout Boldbrew, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70745, 0, 0, 'Filthy mosters! I owe ye me life.', 12, 0, 100, 0, 0, 37294, 0, 0, 0, 'Scout Forgefellow, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70745, 1, 0, 'Listen: The storm. We figured out what\'s makin\' it. That sneaky Zandalari has these ice trolls makin\' blood sacrifices ta elemental spirits. There\'s a cave north o\' here, across a bridge. If ye can slay the elemental within, the storm ought ta stop.', 12, 0, 100, 0, 0, 37295, 0, 0, 0, 'Scout Forgefellow, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 0, 0, 'This is where my agents tracked the Zandalari.', 12, 0, 100, 0, 0, 37328, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 1, 0, 'I can only imagine what foul business the Zandalari have with them...', 12, 0, 100, 0, 0, 37329, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 2, 0, 'Yes. This weather is not natural.', 12, 0, 100, 0, 0, 37330, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 3, 0, 'My champions will go. They will see what can be done, and rescue who they can.', 12, 0, 100, 0, 0, 37331, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 4, 0, 'It\'s heartening to see you here, Moira. You have my thanks.', 12, 0, 100, 0, 0, 37332, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 5, 0, 'Hrmmm. Farewell, Queen Regent.', 12, 0, 100, 0, 0, 37333, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 6, 0, 'Well fought. I see the Dark Irons are as formidable as ever.', 12, 0, 100, 0, 0, 37334, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 7, 0, 'Hah! For that, I\'m glad. The Bronzebeards and Wildhammers will regret not having been here to share in your glory.', 12, 0, 100, 0, 0, 37335, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70799, 8, 0, 'Well, you\'ve done just that. Come: let us return to Ironforge, and announce your victory to the Council.', 12, 0, 100, 0, 0, 37336, 0, 0, 0, 'Varian Wrynn, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70801, 0, 0, 'There ye are. Here\'s the lay of it: The king\'s spies are dead to a man. Only a handful o\' dwarven scouts are left.', 12, 0, 100, 0, 0, 37262, 0, 0, 0, 'Mountaineer Grimbolt, Blood on the Snow ');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (70801, 1, 0, 'The nearest is just ahead. If ye can save him, he may know a thing or two. We\'ll cover ye from the brush: Point out a target, and we\'ll open fire.', 12, 0, 100, 0, 0, 37263, 0, 0, 0, 'Mountaineer Grimbolt, Blood on the Snow ');
