INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97136, 0, 0, 'Votre réputation vous précède, $n. Je suis ravie de voir que mes enseignements vous ont été profitables.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Dame Alistra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97140, 0, 0, 'I thought you were only interested in lining your pockets.', 12, 0, 100, 1, 0, 67302, 0, 118640, 0, 'First Arcanist Thalyssra to Player');
