INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97140, 4, 0, 'What could make the leyline flare up?', 12, 0, 100, 0, 0, 67323, 0, 118622, 0, 'First Arcanist Thalyssra to Player');
