INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97140, 6, 0, 'Dismiss these memories so we can proceed.', 12, 0, 100, 0, 0, 67450, 0, 111813, 0, 'First Arcanist Thalyssra to Player');
