INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97140, 8, 0, 'I, too have felt it...', 12, 0, 100, 0, 0, 67453, 0, 111662, 0, 'First Arcanist Thalyssra to Player');
