INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97617, 0, 0, 'Ahhhh, it feels good to MOVE again. Thank you, $n.', 12, 0, 100, 1, 0, 57287, 0, 0, 0, 'Asha Ravensong to Player');
