INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97619, 0, 0, 'I sense demons nearby. After all that time in stasis, I am ready to hunt.', 12, 0, 100, 1, 0, 57344, 0, 0, 0, 'Marius Felbane to Player');
