INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (97622, 0, 0, '$n, it has been a long time since Mardum. I am ready for another fight.', 12, 0, 100, 1, 0, 57288, 0, 0, 0, 'Kor\'vas Bloodthorn to Player');
