INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (98206, 1, 0, 'Good! Some entertainment! I was getting restless!', 14, 0, 100, 0, 0, 58645, 0, 0, 0, 'XAKAL - AGGRO');
