INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (98206, 7, 0, 'No... NO! None can best... the great Xakal...', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'XAKAL - DEAD');
