INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (98208, 2, 0, 'I have not survived for ten thousand years just to fall to a swarm of rats.', 14, 0, 100, 0, 0, 58646, 0, 0, 0, 'VANDROS - AGGRO');
