INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100888, 3, 0, 'Val\'kyr take me!', 12, 0, 100, 0, 0, 0, 0, 104831, 0, 'Jandvik Splintershield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100889, 0, 0, 'I will cast you down!', 12, 0, 100, 0, 0, 0, 0, 104835, 0, 'Jandvik Runecaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100889, 1, 0, 'Judgment awaits...', 12, 0, 100, 0, 0, 0, 0, 105252, 0, 'Jandvik Runecaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100949, 0, 0, 'The sssilence of the deep...', 12, 0, 100, 0, 0, 0, 0, 105280, 0, 'Sashj\'tar Lancer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100982, 0, 0, 'I will meet you down at the gateway on the Molten Shore.', 12, 0, 100, 0, 0, 55352, 0, 0, 0, 'Sevis Brightflame to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100993, 0, 0, 'This world will... perish...', 12, 0, 100, 0, 0, 0, 0, 101192, 0, 'Felblade Assassin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100993, 1, 0, 'Your soul will burn!', 12, 0, 100, 0, 0, 0, 0, 101191, 0, 'Felblade Assassin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (100993, 2, 0, 'We are... infinite...', 12, 0, 100, 0, 0, 0, 0, 101193, 0, 'Felblade Assassin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101013, 0, 0, 'Runes curse you. I will send you to the bottom of the sea where you belong!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Captured Runecarver to Seacursed Slaver');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 0, 0, 'My, my. You look rather gaunt, honored teacher. Are you eating enough? Ahahaha!', 12, 0, 100, 396, 0, 67293, 0, 104159, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 1, 0, 'You should be proud! Your handiwork is going to help us reclaim every corner of our ancient lands.', 12, 0, 100, 397, 0, 67294, 0, 104158, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 2, 0, 'Face it, old man. I have surpassed you.', 12, 0, 100, 0, 0, 67282, 0, 104232, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 3, 0, 'My warp field is not working! Where have you taken us?!', 12, 0, 100, 0, 0, 67290, 0, 104248, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 4, 0, 'No! This place... it must bend to my will!', 12, 0, 100, 0, 0, 67288, 0, 104270, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101116, 5, 0, 'An\'ratha... an\'tal-thandros!', 12, 0, 100, 0, 0, 67289, 0, 104271, 0, 'Warpcaster Thwen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 0, 0, 'That is far enough.', 12, 0, 100, 0, 0, 67418, 0, 103939, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 1, 0, 'Well, you are not from the palace... and you are no demon, at least not entirely.', 12, 0, 100, 274, 0, 67419, 0, 103940, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 2, 0, 'Identify yourself, before I teleport you to the depths of the Great Sea.', 12, 0, 100, 25, 0, 67420, 0, 103943, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 3, 0, 'What is this? A coin...', 12, 0, 100, 0, 0, 67421, 0, 103949, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 4, 0, 'The sign of the dusk lily... This is the First Arcanist\'s seal! Is she alive?!', 12, 0, 100, 1, 0, 67422, 0, 103950, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 5, 0, 'Then there is still a chance... Come quickly! I need your help.', 12, 0, 100, 1, 0, 67423, 0, 103969, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 6, 0, 'Right. The trap.', 12, 0, 100, 1, 0, 67424, 0, 103951, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 7, 0, 'N\'eth ana!', 12, 0, 100, 0, 0, 67425, 0, 103967, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 8, 0, 'The teleporter is just outside. Activate it first, then step on the pad.', 12, 0, 100, 0, 0, 67484, 0, 103970, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101499, 9, 0, 'Go ahead, I am right behind you.', 12, 0, 100, 1, 0, 67287, 0, 104149, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101508, 0, 0, '¡No volverán a ver la luz del día!', 12, 0, 100, 0, 0, 57385, 0, 0, 0, 'Guardia apocalíptico to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101513, 0, 0, 'Votre attention s’il vous plaît ! Je voudrais porter un toast.', 12, 0, 100, 396, 0, 63616, 0, 0, 0, 'Seigneur Jorach Ravenholdt to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101765, 0, 0, 'There!', 12, 0, 100, 0, 0, 66358, 0, 129805, 0, 'Syrana Starweaver to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101765, 1, 0, 'Lothrius\' magic called to me. He must still be alive! Quickly- you can help us rejoin the others.', 12, 0, 100, 0, 0, 66359, 0, 109666, 0, 'Syrana Starweaver to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101765, 2, 0, 'We will move when the way is clear. Your bravery is worth more than you know, friend.', 12, 0, 100, 0, 0, 66357, 0, 109667, 0, 'Syrana Starweaver to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101765, 3, 0, 'It is Nightborne magic... but it called to us.', 12, 0, 100, 0, 0, 0, 0, 120199, 0, 'Syrana Starweaver to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 0, 0, 'Thank you for that welcome distraction.', 12, 0, 100, 2, 0, 66367, 0, 108941, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 1, 0, 'I will take this opportunity to lay traps for the intruders. I am certain we will meet again!', 12, 0, 100, 2, 0, 66367, 0, 117046, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 2, 0, 'We meet again! Lothrius and Syrana have just informed me of your deeds.', 12, 0, 100, 1, 0, 66387, 0, 117107, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 3, 0, 'They will be upon us shortly. I can teleport us somewhere safe.', 12, 0, 100, 1, 0, 66388, 0, 117145, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 4, 0, 'Here we go!', 12, 0, 100, 603, 0, 66389, 0, 118556, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101766, 5, 0, 'I detected a strange signal coming from this place.', 12, 0, 100, 603, 0, 0, 0, 120198, 0, 'Thalrenus Rivertree to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101767, 0, 0, 'I didn\'t make it in time...', 12, 0, 100, 0, 0, 0, 0, 105980, 0, 'Lyrea Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101767, 1, 0, 'Do not come back for us. We are already doomed by the Nightborne\'s betrayal.', 12, 0, 100, 0, 0, 0, 0, 109645, 0, 'Lyrea Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 0, 0, 'Sometimes I forget how talented Serena is. Welcome, as it were. It is high time we dealt with these intruders.', 12, 0, 100, 603, 0, 68991, 0, 109661, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 1, 0, 'Manafiend!', 12, 0, 100, 0, 0, 66255, 0, 117035, 0, 'Lothrius Mooncaller to Imperial Spellbreaker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 2, 0, 'Be wary, my friend- their constructs do not seem at all effected by my runes.', 12, 0, 100, 1, 0, 68992, 0, 109405, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 3, 0, 'You found them! I will have them properly preserved and sent somewhere safe immediately.', 12, 0, 100, 603, 0, 66257, 0, 117056, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 4, 0, 'I have laid out a safe path for you. Touching the crystals will summon my guardians to you. Make good use of it and go swiftly!', 12, 0, 100, 603, 0, 66264, 0, 109664, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 5, 0, 'We have also decided that it is time to leave this place.', 12, 0, 100, 1, 0, 66386, 0, 117109, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101768, 6, 0, 'Come. I see an opening in the rock.', 12, 0, 100, 1, 0, 0, 0, 120200, 0, 'Lothrius Mooncaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 0, 0, 'Urgh!', 12, 0, 100, 482, 0, 67281, 0, 104162, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 1, 0, 'Good trick... <cough> Warping the space around your body like that.', 12, 0, 100, 0, 0, 67283, 0, 104233, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 2, 0, 'But what if that space is already unstable?', 12, 0, 100, 0, 0, 67284, 0, 104235, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 3, 0, '<cough> Unstable... space...', 12, 0, 100, 0, 0, 67291, 0, 104249, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 4, 0, 'Thwen always took magic too lightly... She was a terrible student... <cough>', 12, 0, 100, 1, 0, 67285, 0, 104268, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101782, 5, 0, 'I haven\'t much left... <cough> I will get us as close as I can... Come close.', 12, 0, 100, 0, 0, 67286, 0, 104269, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 0, 0, 'Arash-falas...', 12, 0, 100, 0, 0, 66018, 0, 116791, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 1, 0, 'The shal\'dorei will rule this world!', 12, 0, 100, 0, 0, 66019, 0, 116430, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 2, 0, 'We are chosen to rule!', 12, 0, 100, 0, 0, 66025, 0, 116693, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 3, 0, 'Our magic will break you!', 12, 0, 100, 0, 0, 66022, 0, 116704, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 4, 0, 'How DARE you!!', 12, 0, 100, 0, 0, 0, 0, 109129, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101783, 5, 0, 'My eyes!', 12, 0, 100, 0, 0, 68513, 0, 109131, 0, 'Nightborne Siegecaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 0, 0, 'Lowborn scum!', 12, 0, 100, 0, 0, 65666, 0, 116685, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 1, 0, 'No mercy for outlanders!', 12, 0, 100, 0, 0, 65869, 0, 116680, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 2, 0, 'I cannot lose to a lowborn!', 12, 0, 100, 0, 0, 65857, 0, 116756, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 3, 0, 'Agh!', 12, 0, 100, 0, 0, 0, 0, 109120, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 4, 0, 'What is this filth?!', 12, 0, 100, 0, 0, 0, 0, 105984, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101784, 5, 0, 'What is the meaning of this?!', 12, 0, 100, 0, 0, 0, 0, 105985, 0, 'Nightborne Infiltrator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 0, 0, 'My magic... defeated?', 12, 0, 100, 0, 0, 66016, 0, 116433, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 1, 0, 'Your age has ended!', 12, 0, 100, 0, 0, 65799, 0, 116705, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 2, 0, 'Arash-falas...', 12, 0, 100, 0, 0, 65811, 0, 116791, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 3, 0, 'Tor\'theras falar!', 12, 0, 100, 0, 0, 66017, 0, 116790, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 4, 0, 'None may challenge us!', 12, 0, 100, 0, 0, 66026, 0, 116701, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 5, 0, 'No! It is not my time....', 12, 0, 100, 0, 0, 66015, 0, 116778, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 6, 0, 'Anath\'ashar!', 12, 0, 100, 0, 0, 65818, 0, 116709, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101821, 7, 0, 'Your death is fated!', 12, 0, 100, 0, 0, 65814, 0, 116703, 0, 'Nightborne Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101825, 0, 0, 'Feel the might of the shal\'dorei!', 12, 0, 100, 0, 0, 65905, 0, 116690, 0, 'Nightborne Enforcer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101848, 0, 0, 'The darkness lifts... thank you.', 12, 0, 100, 0, 0, 0, 0, 104167, 0, 'Absolon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101848, 1, 0, 'I left the City to find my daughter. She was one of those who dared speak against Elisande...', 12, 0, 100, 0, 0, 0, 0, 119837, 0, 'Absolon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101848, 2, 0, 'She is gone. I thought I would join her in the ruins as a mindless husk, but you...', 12, 0, 100, 0, 0, 0, 0, 119838, 0, 'Absolon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101848, 3, 0, 'You saved me.', 12, 0, 100, 0, 0, 0, 0, 119839, 0, 'Absolon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101848, 4, 0, 'I will seek shelter now. I hope we meet again, friend.', 12, 0, 100, 0, 0, 0, 0, 119840, 0, 'Absolon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 0, 0, 'Here we are - my old sanctuary!', 12, 0, 100, 1, 0, 67275, 0, 104154, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 1, 0, 'Strange... the defenses are offline...', 12, 0, 100, 1, 0, 67276, 0, 104155, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 2, 0, 'Intruders! How did they get in here?!', 12, 0, 100, 1, 0, 67277, 0, 104156, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 3, 0, 'This coil taps into the ancient leylines. Crank it up to full power!', 12, 0, 100, 1, 0, 67278, 0, 104221, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 4, 0, 'Good. If we overcharge enough coils, the entire system will failsafe into a lockdown.', 12, 0, 100, 0, 0, 67280, 0, 104224, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 5, 0, 'There are more coils in the chamber below - and more intruders, I imagine.', 12, 0, 100, 0, 0, 67279, 0, 104223, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 6, 0, 'Thwen... I should have recognized your uniquely oafish meddling on my network.', 12, 0, 100, 25, 0, 67292, 0, 104157, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101852, 7, 0, 'I can deal with Thwen. You overload the coils!', 12, 0, 100, 1, 0, 67296, 0, 104161, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (101878, 0, 0, 'You cannot hide from me.', 12, 0, 100, 0, 0, 55374, 0, 102155, 0, 'Felsoul Inquisitor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102034, 0, 0, 'Wild Moonfall Hippogryph claws defiantly at the air and remains unmoved.', 16, 0, 100, 377, 0, 0, 0, 109644, 0, 'Wild Moonfall Hippogryph to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102108, 0, 0, 'You may take my soul, cursed, but you will never take my will.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'Captured Shieldmaiden to Seacursed Slaver');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102127, 0, 0, 'What do you think you\'re doing?', 12, 0, 100, 0, 0, 68252, 0, 119572, 0, 'Menagerie Keeper to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102127, 1, 0, 'Stop that at once!', 12, 0, 100, 0, 0, 68251, 0, 119571, 0, 'Menagerie Keeper to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102309, 0, 0, 'I warn you, my kin do not like outsiders. You would be wise to avoid unnecessary confrontation.', 12, 0, 100, 0, 0, 0, 0, 104672, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102309, 1, 0, 'Calder is a headstrong fool...', 14, 0, 100, 0, 0, 0, 0, 105263, 0, 'Toryl to Toryl');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102334, 0, 0, 'May your soul find its way to Valhalas.', 12, 0, 100, 0, 0, 0, 0, 105681, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102334, 1, 0, 'Hail Jarl Throndyr!', 12, 0, 100, 66, 0, 0, 0, 105685, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102365, 0, 0, 'Your human friend was taken by the demons. Grant me my freedom and I will tell you where he was taken!', 12, 0, 100, 0, 0, 0, 0, 106088, 0, 'Selthaes Starsong to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102365, 1, 0, 'Your friend was taken to the felstalker pits beneath the great Burning Legion command ship.', 12, 0, 100, 0, 0, 0, 0, 106191, 0, 'Selthaes Starsong to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102365, 2, 0, 'Sadly, it\'s probably too late for him... don\'t foolishly throw your lives away for a dead man!', 12, 0, 100, 0, 0, 0, 0, 106192, 0, 'Selthaes Starsong to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 0, 0, 'Come $n! I will fight by your side as we cut a bloody swath into these naga!', 12, 0, 100, 0, 0, 0, 0, 105173, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 1, 0, 'For Jandvik!', 14, 0, 100, 0, 0, 0, 0, 104920, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 2, 0, 'Let us slay these serpents, $n!', 14, 0, 100, 15, 0, 0, 0, 104922, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 3, 0, 'Share my strength, $n!', 14, 0, 100, 15, 0, 68513, 0, 104924, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 4, 0, 'For Throndyr!', 14, 0, 100, 0, 0, 0, 0, 104919, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 5, 0, 'Fight for Jandvik, $n!', 14, 0, 100, 15, 0, 0, 0, 104923, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102381, 6, 0, 'I must return to Jandvik.', 12, 0, 100, 0, 0, 0, 0, 105241, 0, 'Brandolf to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 0, 0, 'I ssshould thank you for doing my dirty work, $n. But now you are proving too troublesome to keep alive.', 12, 0, 100, 0, 0, 0, 0, 105966, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 1, 0, 'You will die in the name of the Sashj\'tar!', 12, 0, 100, 0, 0, 0, 0, 101739, 0, 'Tidemistress Sashj\'tar to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 2, 0, 'Without its leaders, I will easily destroy Jandvik from within!', 12, 0, 100, 0, 0, 0, 0, 105967, 0, 'Tidemistress Sashj\'tar');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 3, 0, 'My naga will wash over Jandvik in a tidal wave of destruction!', 12, 0, 100, 0, 0, 0, 0, 105968, 0, 'Tidemistress Sashj\'tar');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 4, 0, 'Enough!', 12, 0, 100, 0, 0, 0, 0, 121025, 0, 'Tidemistress Sashj\'tar');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102388, 5, 0, 'You will never ssstop usss. Jandvik will fall!', 12, 0, 100, 1, 0, 0, 0, 106795, 0, 'Tidemistress Sashj\'tar');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102391, 0, 0, '$n, the creature\'s destructive beam is focused upon me. Redirect the final mirror to shatter its shield!', 12, 0, 100, 0, 0, 57307, 0, 0, 0, 'Altruis the Sufferer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102391, 1, 0, 'The shield is broken! Quickly, everyone out while the creature is stunned!', 12, 0, 100, 0, 0, 57306, 0, 0, 0, 'Altruis the Sufferer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102393, 0, 0, 'Hold fast, Illidari! We must trap him in this chamber!', 14, 0, 100, 0, 0, 57331, 0, 0, 0, 'Kayn Sunfury');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102393, 1, 0, 'He is too strong! Our only hope is to reactivate the orbs and seal him inside the chamber!', 12, 0, 100, 0, 0, 57305, 0, 0, 0, 'Kayn Sunfury to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102393, 2, 0, 'Well done! The monster is contained. Let\'s meet up with the others.', 12, 0, 100, 603, 0, 57333, 0, 0, 0, 'Kayn Sunfury to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 0, 0, 'Citizens of Jandvik! Gather round the fire and hear the glorious news!', 14, 0, 100, 1, 0, 0, 0, 105614, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 1, 0, 'We have lost much at the hands of the Sashj\'tar. Calder, Eynar, and Jarl Throndyr have all fallen to their treachery.', 14, 0, 100, 1, 0, 0, 0, 105618, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 2, 0, 'Hail the glorious dead!', 14, 0, 100, 1, 0, 0, 0, 105626, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 3, 0, 'We all owe Jandvik\'s salvation to one brave $c. Glory to $n! Slayer of Tidemistress Sashj\'tar!', 14, 0, 100, 1, 0, 0, 0, 105620, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 4, 0, 'As sole remaining elder of Jandvik, it is my duty to lead Jandvik into prosperity.', 14, 0, 100, 1, 0, 0, 0, 105621, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102410, 5, 0, 'For this, I proclaim that $n is the new Jarl of Jandvik. All hail $n!', 14, 0, 100, 1, 0, 0, 0, 105622, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102425, 0, 0, 'We need to - Beakbuck, no!', 12, 0, 100, 0, 0, 0, 0, 109646, 0, 'Serena Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102425, 1, 0, 'Ignore him. I need you to find Lothrius. We were separated in the attack- I think he is still holding out at the Athenaeum.', 12, 0, 100, 2, 0, 0, 0, 109650, 0, 'Serena Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102425, 2, 0, 'Thanks for everything, Beakbuck. I hope we meet again.', 12, 0, 100, 2, 0, 0, 0, 109149, 0, 'Serena Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102425, 3, 0, 'Don\'t worry about me. Just focus on finding Lothrius.', 12, 0, 100, 1, 0, 0, 0, 109150, 0, 'Serena Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102426, 0, 0, 'Beakbuck turns and charges toward you!', 16, 0, 100, 0, 0, 0, 0, 109647, 0, 'Beakbuck to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102527, 0, 0, 'Watch out! This is an imposter!', 14, 0, 100, 0, 0, 0, 0, 105468, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102527, 1, 0, 'Jandvik will never fall to your vile naga! Never!', 14, 0, 100, 0, 0, 0, 0, 105469, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102527, 2, 0, 'Get me out of this cage!', 14, 0, 100, 0, 0, 0, 0, 105389, 0, 'Toryl to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (102534, 0, 0, 'On raconte que je suis morte dans les Mortemines. Les gens sont d’une crédulité ! Rien n’est plus facile que de simuler sa propre mort quand l’esprit de ses assaillants est engourdi par des neurotoxines.', 12, 0, 100, 1, 0, 63626, 0, 0, 0, 'Vanessa VanCleef to Player');
