INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 1, 0, 'Which fist do ye want? How about both!', 12, 0, 100, 0, 0, 0, 0, 106072, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 2, 0, 'Who ordered a knuckle sandwich?!', 12, 0, 100, 0, 0, 0, 0, 106067, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 3, 0, 'Och, what happened to yer face? MAH FISTS!', 12, 0, 100, 0, 0, 0, 0, 106071, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 4, 0, 'Aye, let\'s get him the key... but be on your guard. This place is crawling with demons.', 12, 0, 100, 0, 0, 0, 0, 105917, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 5, 0, 'Don\'t worry, I\'ll go easy on ye!', 12, 0, 100, 0, 0, 0, 0, 106069, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 6, 0, 'Ye wanna fight, eh? I\'ll give ye a fight!', 12, 0, 100, 0, 0, 0, 0, 106064, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 7, 0, 'Now, THAT\'S a face worth punching!', 12, 0, 100, 0, 0, 0, 0, 106068, 0, 'Angus Stormbrew');
