INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 9, 0, 'Och! This one fell off the ugly tree an\' hit my fists on the way down!', 12, 0, 100, 0, 0, 0, 0, 106066, 0, 'Angus Stormbrew');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 10, 0, 'Look! That\'s Brambley\'s helm, I\'d recognize it anywhere!', 12, 0, 100, 0, 0, 0, 0, 105919, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 11, 0, 'Brambley! No... no it canna\' be true!', 12, 0, 100, 0, 0, 0, 0, 105918, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 12, 0, 'Those demons musta\' ate ol\' Brambley... look at him! He\'s just bones, now!', 12, 0, 100, 18, 0, 0, 0, 105920, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 13, 0, 'Curses upon ye, demons! CURSES UPON YE!', 12, 0, 100, 20, 0, 0, 0, 105924, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 14, 0, 'Don\'t worry Brambley, I\'ll carry yer wee head to safety!', 12, 0, 100, 381, 0, 0, 0, 106438, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 15, 0, 'Here lies his mighty arm that defended the realm in so many battles!', 12, 0, 100, 381, 0, 0, 0, 106442, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 16, 0, 'I\'ll take ye home Brambley! Piece by piece, I swear it!', 12, 0, 100, 381, 0, 0, 0, 106445, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 17, 0, 'Och, looks like the demons chewed on this one. Aye, it\'s still moist...', 12, 0, 100, 381, 0, 0, 0, 106446, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 18, 0, 'Good eye! That\'s his alright... I\'d know it a mile away!', 12, 0, 100, 381, 0, 0, 0, 106444, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 19, 0, 'It\'s Brambley\'s Dagger! We\'ll take it home an\' bury it with the rest o\' him.', 12, 0, 100, 381, 0, 0, 0, 106447, 0, 'Angus Stormbrew to Player');
