INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103437, 21, 0, 'I will laddy! I\'ll stay alive for the both of us!', 12, 0, 100, 0, 0, 0, 0, 106515, 0, 'Angus Stormbrew to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103458, 0, 0, 'Si une bête sauvage flaire votre peur, c’en est fini de vous. Ne montrez jamais votre peur. Ni vos saucisses, d’ailleurs. Si elle flaire une saucisse dans votre poche, c’en est fini d’une de vos jambes.', 12, 0, 100, 396, 0, 0, 0, 0, 0, 'Belluaire Tagh to Player');
