INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103569, 1, 0, 'What is this...?', 12, 0, 100, 0, 0, 0, 0, 111018, 0, 'Mythandros Irongrove to Player');
