INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103569, 4, 0, 'This moonwell will nourish and protect everything around it. We can finally call this place home.', 12, 0, 100, 2, 0, 0, 0, 120016, 0, 'Mythandros Irongrove to Player');
