INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103570, 3, 0, 'Thank you, your highness.', 12, 0, 100, 2, 0, 0, 0, 110920, 0, 'Mardranel Forestheart to Player');
