INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (103571, 1, 0, 'I will bring the blessing to Mythandros directly. See you there!', 12, 0, 100, 0, 0, 0, 0, 120002, 0, 'Solendra Featherdown to Player');
