INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106375, 1, 0, 'Help! Baddies here!', 12, 0, 100, 0, 0, 55202, 0, 96655, 0, 'Felsoul Trickster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106375, 2, 0, 'Hmm... medium or well-done?', 12, 0, 100, 0, 0, 55202, 0, 102137, 0, 'Felsoul Trickster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106375, 3, 0, 'Tasty meats for me to eats.', 12, 0, 100, 0, 0, 55207, 0, 96658, 0, 'Felsoul Trickster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106375, 4, 0, 'Hey! You\'re not supposed to be here.', 12, 0, 100, 0, 0, 55206, 0, 96657, 0, 'Felsoul Trickster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106468, 0, 0, 'You\'ve done well so far, but we must ensure you blend in at the party. I\'ll enhance your disguise, infusing it with latent magic to make you appear as one of the nobles.', 12, 0, 100, 396, 0, 70450, 0, 108585, 0, 'Ly\'leth Lunastre');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106482, 0, 0, 'I sense the touch of Cenarius upon this Iris. It is a portal that leads to Moonglade, but the way has been sealed by dark magic. We must sever the tendrils of power that bind it.', 14, 0, 100, 0, 0, 61373, 0, 0, 0, 'Malfurion Stormrage to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106482, 1, 0, 'From each of these pathways, a palpable evil emanates. Choose a course, and know that I will stand with you.', 14, 0, 100, 0, 0, 61374, 0, 0, 0, 'Malfurion Stormrage to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106495, 0, 0, 'Dusk lily pods? I see...', 12, 0, 100, 0, 0, 67258, 0, 109660, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106495, 1, 0, 'Please excuse me. I must address this immediately.', 12, 0, 100, 0, 0, 67259, 0, 109662, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106616, 0, 0, 'Hearts are full of pity...', 12, 0, 100, 18, 0, 65979, 0, 112859, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106616, 1, 0, 'Crashing! Clashing!', 12, 0, 100, 18, 0, 65978, 0, 112860, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106616, 2, 0, 'Crashing! Clashing!', 12, 0, 100, 1, 0, 65978, 0, 112860, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106617, 0, 0, 'Outside lies doom.', 12, 0, 100, 0, 0, 65777, 0, 112861, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106617, 1, 0, 'Ring around the city...', 12, 0, 100, 0, 0, 65778, 0, 112858, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106617, 2, 0, 'No fair, I never get to be the Grand Magistrix!', 12, 0, 100, 0, 0, 65771, 0, 112863, 0, 'Nightborne Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106696, 0, 0, 'It is done. I will dispose of the ashes and find Mayruna back at camp. Thank you, friend.', 12, 0, 100, 0, 0, 0, 0, 110907, 0, 'Kalendros Irongrove to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106843, 0, 0, 'It has been ages since I have seen the Tidestone whole, but there it stands.', 12, 0, 100, 0, 0, 63265, 0, 0, 0, 'Prince Farondis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106843, 1, 0, 'I and my people owe you a debt of gratitude that we can never repay.', 12, 0, 100, 0, 0, 63266, 0, 0, 0, 'Prince Farondis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106843, 2, 0, 'Please return to Azsuna as soon as you can. There is much yet left to do, $n.', 12, 0, 100, 0, 0, 63264, 0, 0, 0, 'Prince Farondis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106848, 0, 0, 'Activate counter-measures. Repel intruder.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Sentry-bot 57-K to 106848');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106848, 1, 0, 'Warning: $r incursion detected!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Sentry-bot 57-K to 106848');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (106897, 0, 0, 'I believe our wounds have healed enough. I will continue my scouting. Be well, Lilryia.', 12, 0, 100, 0, 0, 0, 0, 122610, 0, 'Nighteyes to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107114, 0, 0, 'The Tormenting Orb stares at all players inquisitively.', 16, 0, 100, 0, 0, 0, 0, 113561, 0, 'Tormenting Orb to Tormenting Orb');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 0, 0, 'Behold. The gift of the ancient magi.', 12, 0, 100, 0, 0, 67330, 0, 118723, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 1, 0, 'The arcan\'dor will not merely sustain you. By the fruit of its bough, you will be made whole again.', 12, 0, 100, 0, 0, 67332, 0, 118725, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 2, 0, 'A pure life essence... I only hope it is enough.', 12, 0, 100, 0, 0, 0, 0, 119827, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 3, 0, '[Relieved Sigh] ... Balance is restored. The Arcan\'dor will not fail.', 12, 0, 100, 0, 0, 0, 0, 119828, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 4, 0, 'The arcan\'dor is at a critical stage.', 12, 0, 100, 0, 0, 67313, 0, 118611, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 5, 0, 'Without enough power to mature into its final form, it will die.', 12, 0, 100, 0, 0, 67314, 0, 118612, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107126, 6, 0, 'It is not enough...', 12, 0, 100, 0, 0, 67319, 0, 118617, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107134, 0, 0, 'Well well, the little elf returns. You are most... persistent.', 12, 0, 100, 0, 0, 0, 0, 111300, 0, 'Projection of Aargoss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107134, 1, 0, 'You have fulfilled your end of the bargain. As promised, I give you a taste of the Legion\'s power. Earn more by proving your strength!', 12, 0, 100, 25, 0, 0, 0, 111301, 0, 'Projection of Aargoss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107225, 0, 0, 'I see! You are the one I was waiting for. All I need is a full boat and I\'ll be on my way.', 12, 0, 100, 0, 0, 0, 0, 111360, 0, 'Deline to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107225, 1, 0, 'Ah! This cask contains a large amount of arcwine. Go ahead and put it into the gondola.', 12, 0, 100, 0, 0, 0, 0, 111356, 0, 'Deline to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 0, 0, 'You\'ll find Lorin in the square. Sylessa frequents the canals. Just search the bazaar for Cyrille. They\'ll catch your eye.', 12, 0, 100, 0, 0, 66207, 0, 117194, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 1, 0, 'Is that what she\'s up to? Brave girl. I\'d be delighted to help, but...', 12, 0, 100, 0, 0, 68043, 0, 119243, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 2, 0, 'Always a pleasure. Do things my way and she\'ll be advisor before dinner.', 12, 0, 100, 0, 0, 66192, 0, 116295, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 3, 0, 'Arluin beckons you over.', 16, 0, 100, 0, 0, 0, 0, 121788, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 4, 0, 'I... will go home. I have no business among such esteemed individuals.', 12, 0, 100, 1, 0, 66188, 0, 116511, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107253, 5, 0, 'I accept your gratitude, by the way!', 12, 0, 100, 603, 0, 66189, 0, 116512, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 0, 0, 'Pearlhunter Phin stares at you in disbelief.', 16, 0, 100, 0, 0, 0, 0, 111594, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 1, 0, 'Pearlhunter Phin waves a hand in front of its nose and groans loudly.', 16, 0, 100, 61, 0, 0, 0, 111596, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 2, 0, 'Pearlhunter Phin chuckles, then shakes their head in shame.', 16, 0, 100, 0, 0, 0, 0, 111595, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 3, 0, 'Pearlhunter Phin points at the Leyscar Scuttler.', 16, 0, 100, 0, 0, 0, 0, 114473, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 4, 0, 'Mrglrgr! Mrlgrlr mrlgrl grlrglr. Mrglrgrl?', 12, 0, 100, 0, 0, 416, 0, 113697, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 5, 0, 'Pearlhunter Phin\'s jaw drops as it stares at you, bewildered.', 16, 0, 100, 0, 0, 0, 0, 113701, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 6, 0, 'Pearlhunter Phin\'s eyes roll back in its head as it sighs heavily.', 16, 0, 100, 0, 0, 0, 0, 113703, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 7, 0, 'Pearlhunter Phin hops around in frustration.', 16, 0, 100, 35, 0, 0, 0, 113702, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 8, 0, 'Pearlhunter Phin excitedly grabs the sand and begins scrubbing the flaw on the pearl.', 16, 0, 100, 0, 0, 0, 0, 113704, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 9, 0, 'Pearlhunter Phin presents the pearl to Mr. Mrglglrm.', 16, 0, 100, 0, 0, 0, 0, 113863, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 10, 0, 'Grlmgrl, mgrlgrg. Glrgrlmrgl mrglrlgr lrglr! Rlmgrlm mgrglrmg mrglrglrm. Mrlgrgmrlg gmrlgrl mlrglrg?', 12, 0, 100, 0, 0, 23615, 0, 113855, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 11, 0, 'Mrg, mrlgmrlg...', 12, 0, 100, 0, 0, 419, 0, 113856, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107302, 12, 0, 'Glmrg! Gmlrgm mglrmgrl mrmglr!', 12, 0, 100, 0, 0, 18075, 0, 114794, 0, 'Pearlhunter Phin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107318, 0, 0, 'Keep it down, brats. You will join the others soon.', 12, 0, 100, 0, 0, 66315, 0, 115406, 0, 'Guard Morgaine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107324, 0, 0, 'Intruders!', 12, 0, 100, 0, 0, 0, 0, 121052, 0, 'Suramar Child');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107333, 0, 0, 'Intruder! Face my blade!', 12, 0, 100, 0, 0, 69824, 0, 115553, 0, 'Overseer Durant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107333, 1, 0, 'She sealed her fate, as you have sealed yours!', 12, 0, 100, 0, 0, 69825, 0, 115554, 0, 'Overseer Durant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107333, 2, 0, 'You cannot defy me!', 12, 0, 100, 0, 0, 69827, 0, 115597, 0, 'Overseer Durant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107333, 3, 0, 'Elisande will have your heads for this!', 12, 0, 100, 0, 0, 69828, 0, 115598, 0, 'Overseer Durant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107333, 4, 0, 'T-traitors...', 12, 0, 100, 0, 0, 69829, 0, 111870, 0, 'Overseer Durant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107342, 0, 0, 'You will bow to us!', 12, 0, 100, 0, 0, 65667, 0, 116700, 0, 'Duskwatch Spell-Fencer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107348, 0, 0, 'Understood.', 12, 0, 100, 0, 0, 0, 0, 132592, 0, 'Sylessa to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107349, 0, 0, 'Very well.', 12, 0, 100, 0, 0, 0, 0, 114217, 0, 'Lorin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107350, 0, 0, 'Say no more.', 12, 0, 100, 0, 0, 0, 0, 111644, 0, 'Cyrille to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107350, 1, 0, 'Vengeance for Margaux!', 12, 0, 100, 0, 0, 69825, 0, 115599, 0, 'Cyrille to Overseer Durant');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107350, 2, 0, 'I can see why Arluin put his faith in you. Give him our regards.', 12, 0, 100, 0, 0, 0, 0, 111927, 0, 'Cyrille to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107350, 3, 0, 'Cyrille places a Dusk Lily atop Overseer Durant\'s corpse.', 16, 0, 100, 0, 0, 0, 0, 120682, 0, 'Cyrille to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 0, 0, 'You insult my honor, $n. Who exactly are you?', 12, 0, 100, 463, 0, 0, 0, 111881, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 1, 0, '$n, you think I would spy on my own? Maybe we should look closer at you instead!', 12, 0, 100, 463, 0, 0, 0, 111882, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 2, 0, 'Now now, let\'s not be hasty $n. Why don\'t you follow me so we can talk about this in a more private setting...', 12, 0, 100, 463, 0, 0, 0, 111875, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 3, 0, 'How dare you accuse me of such a thing, $n? Do you know who I am? I don\'t recognize you at all...', 12, 0, 100, 463, 0, 0, 0, 111874, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 4, 0, 'I don\'t know how you found me out, but now it\'s time to die!', 12, 0, 100, 11, 0, 0, 0, 111886, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107435, 5, 0, 'I don\'t know how you found me out, but now it\'s time to die!', 12, 0, 100, 274, 0, 0, 0, 111886, 0, 'Suspicious Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 0, 0, 'It will be easier for me to travel on the road.', 12, 0, 100, 0, 0, 65938, 0, 116542, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 1, 0, 'You must protect me!', 12, 0, 100, 0, 0, 66396, 0, 118677, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 2, 0, 'Look at them... they are not even people any more. I cannot turn into one of them. We must reach Astoril quickly. Clear the path faster!', 12, 0, 100, 0, 0, 65951, 0, 104334, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 3, 0, 'We must hurry.', 12, 0, 100, 0, 0, 66391, 0, 118672, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 4, 0, 'I am too weak to defend myself. Help!', 12, 0, 100, 0, 0, 66393, 0, 118674, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 5, 0, 'More exiles... like me. All I did was punish those servants... You would have done the same!', 12, 0, 100, 0, 0, 65931, 0, 104335, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107481, 6, 0, 'You must be Astoril! Finally! The first thing I shall do when I get back in the city is drink a cool, tall glass of arcwine.', 12, 0, 100, 0, 0, 65934, 0, 104336, 0, 'Iadreth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107567, 0, 0, 'Initiating self-destruct procedure.', 12, 0, 100, 0, 0, 0, 0, 115099, 0, 'Suramar Shieldguard');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107574, 0, 0, 'People of Stormwind! Citizens of the Alliance! Your king speaks!', 14, 0, 100, 0, 0, 0, 0, 31675, 0, 'anduin_wrynn_q13188');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107574, 1, 0, 'Today marks the first of many defeats for the Scourge! Death knights, once in service of the Lich King, have broken free of his grasp and formed a new alliance against his tyranny!', 14, 0, 100, 0, 0, 0, 0, 31676, 0, 'anduin_wrynn_q13188');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107574, 2, 0, 'You will welcome these former heroes of the Alliance and treat them with the respect that you would give to any ally of Stormwind!', 14, 0, 100, 0, 0, 0, 0, 31677, 0, 'anduin_wrynn_q13188');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107574, 3, 0, 'Glory to the Alliance!', 14, 0, 100, 0, 0, 0, 0, 31678, 0, 'anduin_wrynn_q13188');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107598, 0, 0, 'Of course. I agree that this whole business is... distasteful. Even still, the sort of influence you would have...', 12, 0, 100, 1, 0, 67586, 0, 116280, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107598, 1, 0, 'It is not much, but please take it.', 12, 0, 100, 1, 0, 67581, 0, 113199, 0, 'Vanthir to Shal\'dorei Civilian');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107601, 0, 0, 'The Afflicted Nightborne Citizen shivers violently while staring blankly into the distance.', 16, 0, 100, 0, 0, 0, 0, 114128, 0, 'Afflicted Citizen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107603, 0, 0, 'I\'m starving!', 12, 0, 100, 0, 0, 0, 0, 114601, 0, 'Shal\'dorei Civilian to Vineyard Enforcer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107603, 1, 0, 'I am shal\'dorei! I will not be denied!', 12, 0, 100, 0, 0, 0, 0, 114780, 0, 'Shal\'dorei Civilian to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107603, 2, 0, 'My daughter is dying! Please, you must help me save her!', 12, 0, 100, 0, 0, 0, 0, 114607, 0, 'Shal\'dorei Civilian to Vineyard Enforcer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107603, 3, 0, 'Please help me... please...', 12, 0, 100, 0, 0, 0, 0, 114606, 0, 'Shal\'dorei Civilian to Vineyard Enforcer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107604, 0, 0, 'S-soon, little one. Soon,  I promise.', 12, 0, 100, 0, 0, 0, 0, 114127, 0, 'Afflicted Citizen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107604, 1, 0, 'Is the shipment in? Please tell me there is more... please...', 12, 0, 100, 0, 0, 0, 0, 114136, 0, 'Afflicted Citizen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107606, 0, 0, 'Father, I\'m thirsty... can I have some mana juice?', 12, 0, 100, 18, 0, 0, 0, 114126, 0, 'Nightborne Child to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 0, 0, 'There is a reason we live on the opposite side of the city, my friend.', 12, 0, 100, 274, 0, 67567, 0, 116281, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 1, 0, 'I could change much for our people... yes...', 12, 0, 100, 1, 0, 67568, 0, 116282, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 2, 0, 'Meet me within the Sanctum of Order. I must prepare for the ceremony.', 12, 0, 100, 1, 0, 68065, 0, 119291, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 3, 0, 'I humbly accept this appointment. It is an honor beyond words.', 12, 0, 100, 0, 0, 66267, 0, 116515, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 4, 0, 'My lord, you have lost your sense of decorum. It is already decided.', 12, 0, 100, 1, 0, 66265, 0, 116519, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 5, 0, 'Tal\'ashar it is. I name my friend here as my champion.', 12, 0, 100, 25, 0, 68147, 0, 119381, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 6, 0, 'I am modifying your disguise to allow you to fight like one of us.', 15, 0, 100, 0, 0, 68150, 0, 119388, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 7, 0, 'Win me a title, won\'t you?', 12, 0, 100, 1, 0, 68151, 0, 119389, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 8, 0, 'I wish this had not been necessary, but you have done well.', 12, 0, 100, 0, 0, 68152, 0, 119395, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107632, 9, 0, 'I will send you back to the tavern when you are ready.', 12, 0, 100, 0, 0, 68153, 0, 119396, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107712, 0, 0, 'Oh where could she have gone...', 12, 0, 100, 0, 0, 66374, 0, 113899, 0, 'Verene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107712, 1, 0, 'Do you think Korine could be with the other children?', 12, 0, 100, 0, 0, 66377, 0, 113185, 0, 'Verene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107712, 2, 0, 'Oh my sweet little girl! Are you hurt? Were they mean to you?', 12, 0, 100, 0, 0, 66378, 0, 113268, 0, 'Verene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107712, 3, 0, 'You\'re home now. Thank goodness.', 12, 0, 100, 0, 0, 66379, 0, 113271, 0, 'Verene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107712, 4, 0, 'Thank -you-. I cannot begin to express my gratitude.', 12, 0, 100, 0, 0, 66376, 0, 114305, 0, 'Verene to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 0, 0, 'Foolish surface-dwellers! Be gone!', 12, 0, 100, 0, 0, 67498, 0, 113069, 0, 'Orathiss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 1, 0, 'Come, my children! Feast on their bones!', 12, 0, 100, 0, 0, 67494, 0, 118589, 0, 'Orathiss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 2, 0, 'Grr... Persistent vermin...', 12, 0, 100, 0, 0, 67500, 0, 113076, 0, 'Orathiss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 3, 0, 'The arcan\'dor will live again! With its power, we shall return to the surface!', 12, 0, 100, 0, 0, 67493, 0, 118590, 0, 'Orathiss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 4, 0, 'Nothing escapes from my web!', 12, 0, 100, 0, 0, 67495, 0, 118591, 0, 'Orathiss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107959, 5, 0, 'Hgnh... my prize... it grows heavy...', 12, 0, 100, 0, 0, 67496, 0, 118592, 0, 'Orathiss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107997, 0, 0, 'Let me see...', 12, 0, 100, 0, 0, 67572, 0, 113182, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107997, 1, 0, 'Silgryn quickly shuffles through the stack of letters.', 16, 0, 100, 0, 0, 0, 0, 113183, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107997, 2, 0, 'Hm. They\'ve been rounding up a lot of people lately. Children, too.', 12, 0, 100, 0, 0, 67573, 0, 113184, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (107997, 3, 0, 'Very possible. I\'ll have our friend here follow a guard in and find out.', 12, 0, 100, 0, 0, 67574, 0, 113186, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108008, 0, 0, 'There she is!', 12, 0, 100, 0, 0, 67499, 0, 113189, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108008, 1, 0, 'She must not open the seed!', 12, 0, 100, 0, 0, 67501, 0, 113190, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108008, 2, 0, 'This passage will lead us out.', 12, 0, 100, 0, 0, 67534, 0, 113652, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108008, 3, 0, 'Not far now.', 12, 0, 100, 0, 0, 67535, 0, 113654, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108008, 4, 0, 'I know you have been rebuilding the old teleporters. The beacon here will connect us to your shal\'dorei friends.', 12, 0, 100, 0, 0, 67536, 0, 113655, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108063, 0, 0, 'Mommy!', 12, 0, 100, 0, 0, 66227, 0, 113824, 0, 'Korine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108063, 1, 0, 'They yelled and put me in a cage! I missed you...', 12, 0, 100, 0, 0, 66251, 0, 113269, 0, 'Korine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108068, 0, 0, 'I did not know if I would make it out of this alive. Thank you.', 12, 0, 100, 0, 0, 0, 0, 114054, 0, 'Accused Suramar Citizen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108075, 0, 0, 'I want my mommy...', 12, 0, 100, 0, 0, 66239, 0, 113217, 0, 'Korine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108075, 1, 0, 'AH! NO!', 12, 0, 100, 0, 0, 66240, 0, 113218, 0, 'Korine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108075, 2, 0, 'Waaaah!', 12, 0, 100, 0, 0, 66241, 0, 113219, 0, 'Korine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108075, 3, 0, '.... Waaaaah!!', 12, 0, 100, 0, 0, 66250, 0, 113246, 0, 'Korine to Player');
