INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108872, 2, 0, 'The courtyard you just left is open to visitors. From here forward, one of my staff must accompany you.', 12, 0, 100, 0, 0, 66288, 0, 114978, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108872, 3, 0, 'You can purchase souvenirs and gifts from Sylverin, my assistant. Perhaps you could do that on your way out?', 12, 0, 100, 0, 0, 66289, 0, 114979, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108872, 4, 0, 'This is our tasting pavilion. We serve only the finest varietals here - many of them enjoyed in the palace!', 12, 0, 100, 0, 0, 66290, 0, 114980, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108872, 5, 0, 'Feel free to try the samples we have available today. We will continue the tour when you are finished.', 12, 0, 100, 0, 0, 66291, 0, 114982, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 0, 0, 'The Vineyards are open to special guests only. You must leave immediately.', 12, 0, 100, 0, 0, 0, 0, 114600, 0, 'Vineyard Enforcer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 1, 0, 'You are not authorized to enter the Vineyard.', 12, 0, 100, 0, 0, 0, 0, 114598, 0, 'Vineyard Enforcer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 2, 0, 'Stand back!', 12, 0, 100, 0, 0, 0, 0, 114763, 0, 'Vineyard Enforcer to Shal\'dorei Civilian');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 3, 0, 'Very good.', 12, 0, 100, 0, 0, 0, 0, 121508, 0, 'Vineyard Enforcer to Vineyard Laborer');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 4, 0, 'You there! Back to work!', 12, 0, 100, 6, 0, 0, 0, 115111, 0, 'Vineyard Enforcer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 5, 0, 'I will hear none of your excuses. Back to work!', 12, 0, 100, 0, 0, 0, 0, 115113, 0, 'Vineyard Enforcer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108875, 6, 0, 'The Overseer will hear of this. Mark my words, Margaux.', 12, 0, 100, 0, 0, 0, 0, 115115, 0, 'Vineyard Enforcer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108877, 0, 0, 'This is a heavy blend of barely-ripe shadowberry and manafruit, triple infused with Nightwell essence. It is best appreciated in small sips.', 12, 0, 100, 0, 0, 66178, 0, 114934, 0, 'Sacheverell Leburque to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108877, 1, 0, 'This three year Infusion incorporates delightful berry elements with a scintillating melon bouquet. Refreshing, yes?', 12, 0, 100, 0, 0, 66176, 0, 114932, 0, 'Sacheverell Leburque to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108877, 2, 0, 'Ah, this is a fine vintage. Seventh generation manafruit entangled with slippery nightsap flavors and a delicate walnut finish.', 12, 0, 100, 0, 0, 66177, 0, 114933, 0, 'Sacheverell Leburque to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108930, 0, 0, 'Legion, come to our aid!', 12, 0, 100, 0, 0, 65978, 0, 114783, 0, 'Nightborne Envoy');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108931, 0, 0, 'I was just resting. I-', 12, 0, 100, 0, 0, 0, 0, 115112, 0, 'Vineyard Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (108931, 1, 0, 'Agh!', 12, 0, 100, 0, 0, 66279, 0, 115114, 0, 'Vineyard Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109048, 0, 0, 'To me, servant!', 12, 0, 100, 0, 0, 0, 0, 114785, 0, 'Arcane Engineer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109144, 0, 0, 'I\'ve procured a palanquin for you. Try to look imperious- it helps with the illusion.', 12, 0, 100, 0, 0, 67565, 0, 114863, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109144, 1, 0, 'The woman you are looking for is named Margaux. She has shown interest in our cause.', 12, 0, 100, 0, 0, 67566, 0, 114864, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 0, 0, 'The building we approach now is called the Aging Chamber.', 12, 0, 100, 1, 0, 66268, 0, 115021, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 1, 0, 'If you are at all familiar with the wine crafting process, you know that it is necessary to allow the juice to ferment.', 12, 0, 100, 1, 0, 66271, 0, 115025, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 2, 0, 'We prefer to shorten the process using time magic. Such power is our birthright!', 12, 0, 100, 603, 0, 66269, 0, 115022, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 3, 0, 'To my left you will see one of our orchards.', 12, 0, 100, 1, 0, 66270, 0, 115023, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 4, 0, 'This one houses a species of tree that produces extra sweet fruit, which you tasted in the Infusion.', 12, 0, 100, 1, 0, 66272, 0, 115026, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 5, 0, 'The buildings before you house our bottling facilities.', 12, 0, 100, 1, 0, 66273, 0, 115028, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 6, 0, 'Each bottle is sealed with magic to prevent any of the Nightwell\'s essence from fading.', 12, 0, 100, 1, 0, 66274, 0, 115029, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 7, 0, 'A cask of arcwine from our vineyard can be safely stored for hundreds of years!', 12, 0, 100, 603, 0, 66275, 0, 115030, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 8, 0, 'Ahead you will see our distribution facility.', 12, 0, 100, 1, 0, 66276, 0, 115031, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 9, 0, 'Here we arrange to send hundreds of barrels and casks of arcwine out to the city.', 12, 0, 100, 1, 0, 66277, 0, 115032, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 10, 0, 'The Magistrix monitors these activities very-', 12, 0, 100, 1, 0, 66278, 0, 115033, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 11, 0, 'Don\'t hurt him!', 12, 0, 100, 603, 0, 66279, 0, 115034, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 12, 0, 'I am so sorry- please forgive this mistake. I will personally ensure it does not happen again.', 12, 0, 100, 20, 0, 66280, 0, 115035, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 13, 0, 'Where were we?', 12, 0, 100, 6, 0, 66281, 0, 115036, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 14, 0, 'Across this bridge is the most crucial part of our winemaking process.', 12, 0, 100, 1, 0, 66282, 0, 115038, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 15, 0, 'It is here that we instill our wine with Nightwell essence, which gives it the restorative properties our people enjoy.', 12, 0, 100, 1, 0, 66283, 0, 115039, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 16, 0, 'And this is where I leave you. I must prepare the next activity.', 12, 0, 100, 1, 0, 66284, 0, 115040, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109223, 17, 0, 'I will meet you at the next station. My assistant Sylverin will be happy to show you how the infusion process works.', 12, 0, 100, 273, 0, 66285, 0, 115042, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109388, 0, 0, 'As you wish. I will meet the Lieutenant where we met last. Send word for her to expect me.', 12, 0, 100, 2, 0, 0, 0, 115280, 0, 'Nightborne Spy to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109388, 1, 0, 'Keep an eye out for rebels. You can\'t be too careful.', 12, 0, 100, 22, 0, 0, 0, 115284, 0, 'Nightborne Spy to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109388, 2, 0, 'If you see someone stealing arcwine, report them at once!', 12, 0, 100, 0, 0, 0, 0, 115285, 0, 'Nightborne Spy to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109407, 0, 0, 'A list of traitors? You will have to get this to Lieutenant Piet at once.', 12, 0, 100, 1, 0, 0, 0, 115279, 0, 'Nightborne Sergeant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109409, 0, 0, 'So generous! Thank you, I owe you my life!', 12, 0, 100, 7, 0, 0, 0, 115295, 0, 'Nightborne Wretch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109461, 0, 0, 'My hand slipped! Forgive me!', 12, 0, 100, 430, 0, 0, 0, 121692, 0, 'Vineyard Porter to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109461, 1, 0, 'Flee! The Magistrix will kill us for failing her!', 12, 0, 100, 430, 0, 0, 0, 115419, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109461, 2, 0, 'Thank you.', 12, 0, 100, 0, 0, 0, 0, 115479, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109461, 3, 0, 'Take the arcwine. I am getting out of here.', 12, 0, 100, 1, 0, 0, 0, 115481, 0, 'Vineyard Porter to Spellsinger Quiris');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109473, 0, 0, 'Who goes there?!', 12, 0, 100, 0, 0, 66005, 0, 118520, 0, 'Duskwatch Shroud to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109509, 0, 0, 'This shipment is worth more than you are! How dare you treat it so carelessly!', 12, 0, 100, 0, 0, 0, 0, 121691, 0, 'Spellsinger Quiris to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109509, 1, 0, 'You will pay for this delay!', 12, 0, 100, 0, 0, 0, 0, 115487, 0, 'Spellsinger Quiris to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109509, 2, 0, 'Elisande... will hear... of this...', 12, 0, 100, 0, 0, 0, 0, 115488, 0, 'Spellsinger Quiris to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109670, 0, 0, 'Show your identification. COMPLY.', 12, 0, 100, 0, 0, 0, 0, 119796, 0, 'Chronarch Defender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109670, 1, 0, 'Scanning arcane essence...', 12, 0, 100, 0, 0, 65819, 0, 119797, 0, 'Chronarch Defender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109670, 2, 0, 'Stand by for screening.', 12, 0, 100, 0, 0, 66004, 0, 119794, 0, 'Chronarch Defender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109759, 0, 0, 'Hiciste lo que el Kirin Tor no pudo hacer por nosotros. La muerte del Maestro de canes serÃ¡ un duro golpe para las fuerzas de la LegiÃ³n Ardiente.', 12, 0, 100, 1, 0, 66608, 0, 115790, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109759, 1, 0, 'VÃ¡monos de aquÃ­ y celebremos nuestra victoria entre amigos.', 12, 0, 100, 1, 0, 66609, 0, 115791, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109954, 0, 0, 'My illusions will be your end!', 12, 0, 100, 0, 0, 0, 0, 116340, 0, 'Magister Phaedris to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (109954, 1, 0, 'The illusion... is broken.', 12, 0, 100, 0, 0, 0, 0, 116341, 0, 'Magister Phaedris to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110354, 0, 0, 'I will wrestle my title from your champion\'s lifeless hands, Ly\'leth!', 12, 0, 100, 0, 0, 66216, 0, 116532, 0, 'Coryn');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110354, 1, 0, 'You cannot match me! I am your better!', 12, 0, 100, 0, 0, 66217, 0, 116533, 0, 'Coryn');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110354, 2, 0, 'I will have what is owed me!', 12, 0, 100, 0, 0, 66220, 0, 116534, 0, 'Coryn');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110354, 3, 0, 'I was... framed...', 12, 0, 100, 0, 0, 66221, 0, 116535, 0, 'Coryn to Enhanced Illusion');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110365, 0, 0, 'A challenger? Don\'t make me laugh!', 12, 0, 100, 0, 0, 66332, 0, 116536, 0, 'Ruven');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110365, 1, 0, 'The Legion brings us power unending!', 12, 0, 100, 0, 0, 66333, 0, 116537, 0, 'Ruven');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110365, 2, 0, 'I will not be defeated by the likes of you!', 12, 0, 100, 0, 0, 66335, 0, 116539, 0, 'Ruven');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110365, 3, 0, 'They... promised...', 12, 0, 100, 0, 0, 66336, 0, 116540, 0, 'Ruven to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110443, 0, 0, 'Do you expect me to waste time chattering with vapid nobles even as my enemies close in? Look there, you fool! We are not alone.', 12, 0, 100, 0, 0, 67614, 0, 117106, 0, 'Elisande to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110443, 1, 0, 'Yet another failure, Melandrus. Consider this your chance to correct it. Dispose of these outsiders. I must return to the Nighthold.', 12, 0, 100, 0, 0, 67613, 0, 117104, 0, 'Elisande to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110538, 0, 0, 'We must learn why the old arcan\'dor failed. Its shattered bones may have the answers.', 12, 0, 100, 0, 0, 66383, 0, 118555, 0, 'Valewalker Farodin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110654, 0, 0, 'Run!', 12, 0, 100, 0, 0, 66336, 0, 118743, 0, 'Frightened Laborer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110655, 0, 0, 'Get moving!', 12, 0, 100, 0, 0, 0, 0, 118738, 0, 'Duskmere Smuggler to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110656, 0, 0, 'You will not interfere with my research!', 12, 0, 100, 0, 0, 0, 0, 118752, 0, 'Arcanist Lylandre to Lylandre\'s Fire Crystal');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110679, 0, 0, 'You are not leaving my sight until an advisor is appointed. Promise me!', 12, 0, 100, 0, 0, 0, 0, 119494, 0, 'Aurore to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110679, 1, 0, 'Thank you. Do let Ly\'leth know that I stand with her.', 12, 0, 100, 0, 0, 65866, 0, 119496, 0, 'Aurore to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110680, 0, 0, 'I think they meant to kill me!', 12, 0, 100, 603, 0, 0, 0, 118819, 0, 'Ailen Astravar to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110680, 1, 0, 'I am getting off this infernal ship! I will return to my mother at once.', 12, 0, 100, 603, 0, 0, 0, 119490, 0, 'Ailen Astravar to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110698, 0, 0, 'Who is there?', 12, 0, 100, 0, 0, 67306, 0, 118774, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110698, 1, 0, '<Cough.> Arluin, is that you? It is so dark...', 12, 0, 100, 0, 0, 67307, 0, 118775, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110698, 2, 0, 'I understand... He never could stay out of trouble...', 12, 0, 100, 0, 0, 67308, 0, 118776, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110698, 3, 0, 'Thank you... I will remember this.', 12, 0, 100, 0, 0, 67309, 0, 118777, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110799, 0, 0, 'J’ai descendu plus que ma part de potions de mana, à l’époque. Non pas que je m’en glorifie, mais j’ai fait ce que j’avais à faire !', 12, 0, 100, 396, 0, 0, 0, 0, 0, 'Gedrah to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110804, 0, 0, 'Yes, mother.', 12, 0, 100, 0, 0, 0, 0, 119495, 0, 'Ailen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110874, 0, 0, 'That is the symbol of House Stelleris... but that could not be. Coryn was Ruven\'s dearest friend...', 12, 0, 100, 1, 0, 0, 0, 119179, 0, 'Distraught Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110874, 1, 0, 'No, that\'s simply impossible...', 12, 0, 100, 274, 0, 0, 0, 119180, 0, 'Distraught Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110875, 0, 0, 'Preposterous! A joke at my lord\'s expense? Only a madman would attempt it!', 12, 0, 100, 5, 0, 0, 0, 119181, 0, 'Shamed Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110875, 1, 0, '... But if anyone would laugh, it would be THEM.', 12, 0, 100, 1, 0, 0, 0, 119182, 0, 'Shamed Noble to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110876, 0, 0, 'Absolutely NOT! Why would he do such a thing? Has he no honor?!', 12, 0, 100, 5, 0, 0, 0, 119177, 0, 'Disgruntled Servant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110876, 1, 0, '... Why WOULD he do such a thing...?', 12, 0, 100, 1, 0, 0, 0, 119178, 0, 'Disgruntled Servant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110931, 0, 0, 'Come to Furog, tiny elf-monsters!', 12, 0, 100, 0, 0, 0, 0, 119271, 0, 'Furog the Elfbreaker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110931, 1, 0, 'I break your elf-thing!', 12, 0, 100, 0, 0, 0, 0, 119284, 0, 'Furog the Elfbreaker to Withered Exile');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110955, 0, 0, 'Die, beast!', 12, 0, 100, 0, 0, 0, 0, 119459, 0, 'Hulking Shocktrooper to Su\'esh');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110958, 0, 0, 'This wine is terrible. I\'ll wager Melandrus watered it down.', 12, 0, 100, 1, 0, 0, 0, 118921, 0, 'Fancy Noble');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110958, 1, 0, 'I saw Spellblade Aluriel pacing about. She always seems so... angry.', 12, 0, 100, 1, 0, 0, 0, 118920, 0, 'Fancy Noble');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110967, 0, 0, 'Filthy backstabbing cretin! That title should have been mine!', 12, 0, 100, 603, 0, 66003, 0, 116517, 0, 'Coryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110967, 1, 0, 'You impinge upon my honor! You besmirch my name with baseless accusations!', 12, 0, 100, 603, 0, 66214, 0, 116518, 0, 'Coryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110967, 2, 0, 'I will reclaim my honor! I invoke the ancient rite- Tal\'ashar!', 12, 0, 100, 603, 0, 66219, 0, 116524, 0, 'Coryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110967, 3, 0, 'I have no need to hide behind a champion. I will defend my own honor.', 12, 0, 100, 1, 0, 68148, 0, 119382, 0, 'Coryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (110967, 4, 0, 'Meet me atop the Sanctum when you are ready to die, whelp.', 12, 0, 100, 603, 0, 68149, 0, 119383, 0, 'Coryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111002, 0, 0, 'The enemy may be using magic to deceive us! Do not let your guard down!', 14, 0, 100, 0, 0, 0, 0, 119473, 0, 'Doombringer Sinarus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111002, 1, 0, 'Move quickly, and crush any who resist the Legion!', 14, 0, 100, 0, 0, 0, 0, 119469, 0, 'Doombringer Sinarus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111013, 0, 0, 'Kill it! Kill it now!', 12, 0, 100, 0, 0, 0, 0, 119462, 0, 'Felsoul Champion to Su\'esh');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111013, 1, 0, 'Fools! Su\'esh has escaped the menagerie!', 12, 0, 100, 0, 0, 0, 0, 119461, 0, 'Felsoul Champion to Su\'esh');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111013, 2, 0, 'Su\'esh is loose! To arms!', 12, 0, 100, 0, 0, 0, 0, 119460, 0, 'Felsoul Champion to Su\'esh');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111318, 0, 0, 'I was so certain he was dead...', 12, 0, 100, 0, 0, 0, 0, 119885, 0, 'Noressa to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111318, 1, 0, 'Noressa quickly glances over the letter.', 16, 0, 100, 0, 0, 0, 0, 119886, 0, 'Noressa to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111318, 2, 0, 'Oh, Absolon...', 12, 0, 100, 0, 0, 0, 0, 119887, 0, 'Noressa to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111318, 3, 0, 'Thank you. I will hold his words in my heart.', 12, 0, 100, 0, 0, 0, 0, 119891, 0, 'Noressa to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111484, 0, 0, 'Now is the age of the shal\'dorei!', 12, 0, 100, 0, 0, 65707, 0, 116677, 0, 'Duskwatch Spellshield to Hungering Withered');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111485, 0, 0, 'Lowborn scum!', 12, 0, 100, 0, 0, 65907, 0, 116685, 0, 'Duskwatch Spellshield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111485, 1, 0, 'Anath\'ashar!', 12, 0, 100, 0, 0, 65910, 0, 116688, 0, 'Duskwatch Spellshield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111485, 2, 0, 'Die, outlander!', 12, 0, 100, 0, 0, 65909, 0, 116708, 0, 'Duskwatch Spellshield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111485, 3, 0, 'Feel the might of the shal\'dorei!', 12, 0, 100, 6, 0, 65663, 0, 116676, 0, 'Duskwatch Spellshield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111489, 0, 0, 'Your death is fated!', 12, 0, 100, 273, 0, 65864, 0, 116703, 0, 'Loyalist Sycophant to Hungering Withered');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111489, 1, 0, 'Your star is fading!', 12, 0, 100, 0, 0, 65866, 0, 116684, 0, 'Loyalist Sycophant to Hungering Withered');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111489, 2, 0, 'We have... failed.', 12, 0, 100, 0, 0, 65859, 0, 116789, 0, 'Loyalist Sycophant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111490, 0, 0, 'Tremble before our strength!', 12, 0, 100, 273, 0, 65664, 0, 116683, 0, 'Loyalist Sycophant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111523, 0, 0, 'Something\'s not quite right...', 12, 0, 100, 1, 0, 65803, 0, 118519, 0, 'Duskwatch Warpcaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111530, 0, 0, 'Hah! Where are your manafiends now?!', 12, 0, 100, 0, 0, 0, 0, 122538, 0, 'Imperial Arcanist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111530, 1, 0, 'What?!', 12, 0, 100, 0, 0, 0, 0, 120909, 0, 'Imperial Arcanist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111539, 0, 0, 'I will join my fallen brothers and sisters. Goodbye, Lyrea.', 12, 0, 100, 0, 0, 0, 0, 120114, 0, 'Kailea Windfeather to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111619, 0, 0, 'Dissent is forbidden, by order of Grand Magistrix Elisande!', 12, 0, 100, 0, 0, 66005, 0, 124226, 0, 'Duskwatch Shroud');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111619, 1, 0, 'Something\'s not quite right...', 12, 0, 100, 0, 0, 65803, 0, 124630, 0, 'Duskwatch Shroud to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111620, 0, 0, 'Die, outlander!', 12, 0, 100, 0, 0, 65820, 0, 116708, 0, 'Duskwatch Executor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111621, 0, 0, 'Our magic will break you!', 12, 0, 100, 0, 0, 66022, 0, 116704, 0, 'Duskwatch Executor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111621, 1, 0, 'No! It is not my time....', 12, 0, 100, 0, 0, 66015, 0, 116768, 0, 'Duskwatch Executor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111774, 0, 0, 'Val\'kyr!  The Call has gone out!  Prepare yourselves!', 14, 0, 100, 5, 0, 0, 0, 122615, 0, 'Matilda Skoptidottir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 0, 0, 'What is this place?', 12, 0, 100, 0, 0, 67381, 0, 94147, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 1, 0, 'This chamber... I have never seen anything like it.', 12, 0, 100, 0, 0, 67383, 0, 117057, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 2, 0, 'So dark...', 12, 0, 100, 0, 0, 67384, 0, 117539, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 3, 0, 'In\'alah!', 12, 0, 100, 463, 0, 67385, 0, 117454, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 4, 0, 'Much better.', 12, 0, 100, 0, 0, 67386, 0, 117220, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 5, 0, 'Old teleporters. Not so different from the ones in Suramar city. Seems they are inactive.', 12, 0, 100, 0, 0, 67387, 0, 117064, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 6, 0, 'Let us explore downstairs. Watch your step.', 12, 0, 100, 0, 0, 67388, 0, 117219, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 7, 0, 'Strange. The floor looks like... a map?', 12, 0, 100, 0, 0, 67389, 0, 117221, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 8, 0, 'And we are at the center...', 12, 0, 100, 0, 0, 67390, 0, 117551, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 9, 0, 'One thing is certain - leyline energy is being fed into this chamber from elsewhere.', 12, 0, 100, 0, 0, 67391, 0, 117222, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 10, 0, 'What could have required such power? A weapon, perhaps?', 12, 0, 100, 0, 0, 67392, 0, 117557, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 11, 0, 'And those keepers protecting it... How long their vigil must have been...', 12, 0, 100, 0, 0, 67393, 0, 117558, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 12, 0, '<Sigh.> I have more questions now than before!', 12, 0, 100, 0, 0, 67394, 0, 117559, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 13, 0, 'For now, at least, we know the withered have learned to avoid this place.', 12, 0, 100, 0, 0, 67395, 0, 101214, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 14, 0, 'And nobody from the palace would think to search for us here.', 12, 0, 100, 0, 0, 67396, 0, 101215, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 15, 0, 'It is a perfect refuge. A temporary home, until I can return to Suramar.', 12, 0, 100, 0, 0, 67397, 0, 117561, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111863, 16, 0, 'But first, I will need allies.', 12, 0, 100, 0, 0, 67398, 0, 117562, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111871, 0, 0, 'Inconceivable...', 12, 0, 100, 0, 0, 65808, 0, 116788, 0, 'Ley Line Researcher to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111871, 1, 0, 'Tor\'theras falar!', 12, 0, 100, 432, 0, 65809, 0, 116780, 0, 'Ley Line Researcher to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111872, 0, 0, 'No! It is not my time....', 12, 0, 100, 0, 0, 66015, 0, 116778, 0, 'Leyline Researcher to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111900, 0, 0, 'I will follow you through. Thank you for allowing me to accompany you.', 12, 0, 100, 0, 0, 0, 0, 121191, 0, 'Rosaine to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111901, 0, 0, 'Now, as before, we do what we must to carry on.', 12, 0, 100, 11, 0, 0, 0, 125324, 0, 'Suramar Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111902, 0, 0, 'This fruit actually cured Thalyssra? Amazing.', 12, 0, 100, 7, 0, 0, 0, 125323, 0, 'Suramar Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (111903, 0, 0, 'The Lunastre Attendant nods to you and calmly exits the tavern.', 16, 0, 100, 0, 0, 0, 0, 121185, 0, 'Lunastre Attendant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 0, 0, 'Take your place near the table. Once we begin, it is important that we keep up the pace. These delicate ingredients spoil quickly.', 12, 0, 100, 1, 0, 66296, 0, 115231, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 1, 0, 'The juices need a little massaging- step into the vat and jump to squish the mixture around!', 12, 0, 100, 25, 0, 66303, 0, 115242, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 2, 0, 'The arcwine has soured... you must start over!', 42, 0, 100, 0, 0, 66293, 0, 123135, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 3, 0, 'Are you certain you are ready to participate? This batch will have to be destroyed.', 12, 0, 100, 0, 0, 66293, 0, 115228, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 4, 0, 'I feel as though you are losing focus. Is something else on your mind? Step back for a moment and we will try again.', 12, 0, 100, 0, 0, 66294, 0, 115229, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 5, 0, 'Well done. Now let me see...', 12, 0, 100, 1, 0, 66298, 0, 115236, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 6, 0, 'Let us add a little moisture. Just enough to keep the taps flowing.', 12, 0, 100, 0, 0, 66301, 0, 115240, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 7, 0, 'Exactly what it needed. But there is still something missing...', 12, 0, 100, 0, 0, 66299, 0, 115238, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 8, 0, 'You, my friend, are a natural. The blend needs something more, though. Let me think...', 12, 0, 100, 0, 0, 66297, 0, 115237, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 9, 0, 'Ah! It needs something floral, to add a little brightness.', 12, 0, 100, 0, 0, 66300, 0, 115239, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112055, 10, 0, 'Excellent! You did very well- are you certain you have never made wine before? I will have this bottled immediately.', 12, 0, 100, 1, 0, 66297, 0, 115230, 0, 'Margaux to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112145, 0, 0, 'Perhaps there is a new path for us after all...', 12, 0, 100, 0, 0, 67374, 0, 118753, 0, 'Arcanist Valtrois to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112145, 1, 0, 'You have my thanks, outlander.', 12, 0, 100, 0, 0, 67375, 0, 118756, 0, 'Arcanist Valtrois to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112146, 0, 0, 'This tree came at a high cost... You are sure it can sustain us?', 12, 0, 100, 0, 0, 67331, 0, 118739, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112146, 1, 0, 'We are on the brink of a new age for the Nightborne.', 12, 0, 100, 0, 0, 67376, 0, 118771, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112146, 2, 0, 'Without you, none of this would have been possible.', 12, 0, 100, 0, 0, 67377, 0, 118772, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112146, 3, 0, 'For now, we shall gather our strength. When the time comes, I trust we can call upon you to help us liberate Suramar.', 12, 0, 100, 0, 0, 67378, 0, 118773, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112147, 0, 0, 'Hmm... a little tart, with a hint of- oh, my... there it is.', 12, 0, 100, 0, 0, 67372, 0, 118645, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112147, 1, 0, 'Astounding.', 12, 0, 100, 0, 0, 67373, 0, 118742, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112301, 0, 0, 'I fear nothing!', 12, 0, 100, 0, 0, 68513, 0, 117446, 0, 'Finna Bjornsdottir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112301, 1, 0, 'I will crush you!', 12, 0, 100, 0, 0, 68464, 0, 104829, 0, 'Finna Bjornsdottir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112301, 2, 0, 'This should be good!', 12, 0, 100, 0, 0, 68512, 0, 117445, 0, 'Finna Bjornsdottir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112407, 0, 0, '$n ! Si vous avez un instant à m’accorder, j’ai des marchandises susceptibles de vous intéresser.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Falara Chantenuit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (112575, 0, 0, 'Que la Lumière accélère vos pas, $ct.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Elana to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 0, 0, 'Yes. We are close.', 12, 0, 100, 0, 0, 67339, 0, 118660, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 1, 0, 'There! Place the manastorm generator near the conduit.', 12, 0, 100, 0, 0, 67342, 0, 118665, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 2, 0, 'Something is wrong... The temporal warp sequencer-', 12, 0, 100, 0, 0, 67344, 0, 118669, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 3, 0, 'The fuse is broken. We must trigger the manastorm manually.', 12, 0, 100, 0, 0, 67346, 0, 118670, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 4, 0, 'We have no choice. This must be done.', 12, 0, 100, 0, 0, 67348, 0, 118681, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 5, 0, 'Rath, anu\'telar!', 12, 0, 100, 463, 0, 67349, 0, 118682, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 6, 0, 'We just need to hold out until the storm is over!', 12, 0, 100, 0, 0, 67350, 0, 118683, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 7, 0, 'Erath-omnas!', 12, 0, 100, 0, 0, 67353, 0, 118704, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 8, 0, 'It is too dangerous! The arcane torrents would tear us apart!', 12, 0, 100, 0, 0, 67355, 0, 118705, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 9, 0, 'What are you doing?!', 12, 0, 100, 0, 0, 67358, 0, 81078, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 10, 0, 'Arluin! NO!!', 12, 0, 100, 0, 0, 67359, 0, 118709, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 11, 0, 'Oculeth, get us out of here!', 12, 0, 100, 0, 0, 67361, 0, 118711, 0, 'First Arcanist Thalyssra to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 12, 0, 'Ugh... we made it...', 12, 0, 100, 0, 0, 67362, 0, 118712, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 13, 0, 'Where is Arluin?!', 12, 0, 100, 0, 0, 67363, 0, 118713, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 14, 0, 'Oh no...', 12, 0, 100, 0, 0, 67364, 0, 115290, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 15, 0, 'Do not speak! You are wounded-', 12, 0, 100, 0, 0, 67366, 0, 118716, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 16, 0, 'Yes. You saved us. Please... try to rest.', 12, 0, 100, 0, 0, 67368, 0, 118719, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 17, 0, 'He is gone.', 12, 0, 100, 0, 0, 67370, 0, 118720, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113061, 18, 0, 'Come... Let us check on the others.', 12, 0, 100, 0, 0, 67371, 0, 118721, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 0, 0, 'This is not looking real conduit-y. You sure the old man knows his stuff?', 12, 0, 100, 0, 0, 67338, 0, 118661, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 1, 0, 'Whatever you gotta do, do it quick! Lots of guards heading our way.', 12, 0, 100, 0, 0, 67343, 0, 118668, 0, 'Arluin to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 2, 0, 'Small words, please.', 12, 0, 100, 0, 0, 67345, 0, 118679, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 3, 0, 'Wait, aren\'t we supposed to leave BEFORE the storm starts?', 12, 0, 100, 0, 0, 67347, 0, 118680, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 4, 0, 'Easier said than done...', 12, 0, 100, 0, 0, 67351, 0, 118684, 0, 'Arluin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 5, 0, 'The manastorm is only getting stronger! We need to shut that thing down!', 12, 0, 100, 0, 0, 67354, 0, 118700, 0, 'Arluin to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 6, 0, 'Damn it...', 12, 0, 100, 0, 0, 67356, 0, 118706, 0, 'Arluin to First Mate Snellig');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (113062, 7, 0, 'You guys owe me...', 12, 0, 100, 0, 0, 67357, 0, 118707, 0, 'Arluin to First Mate Snellig');
