INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 0, 0, 'Ah, faithful Midnight. For many years, our countless hunts provided game for the master\'s table.', 14, 0, 100, 0, 0, 58644, 0, 0, 0, 'ATTUMEN - INTRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 1, 0, 'These days we hunt for sport... and the glory of the kill!', 14, 0, 100, 0, 0, 58644, 0, 0, 0, 'ATTUMEN - AGGRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 1, 1, 'Well, well! Another group of thieves trying to steal my horse!', 14, 0, 100, 0, 0, 58645, 0, 0, 0, 'ATTUMEN - AGGRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 2, 0, 'My quarry never escapes!', 14, 0, 100, 0, 0, 58646, 0, 0, 0, 'ATTUMEN - INTAGIBLE PRESENCE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 3, 0, 'Rise up, Midnight! Stamp them into dust!', 14, 0, 100, 0, 0, 58639, 0, 0, 0, 'ATTUMEN - MIGHTY_STOMP');
