INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 5, 0, 'We ride, Midnight! To victory!', 14, 0, 100, 0, 0, 58640, 0, 0, 0, 'ATTUMEN - RIDE_MIDNIGHT');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114262, 6, 0, 'Form ranks! Charge!', 14, 0, 100, 0, 0, 58642, 0, 0, 0, 'ATTUMEN - SPECTRAL_CHARGE');
