INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114263, 5, 0, 'ENOUGH! Your worth is proven! With you as my champions, Helya will fall and I will at long last be free of the curse that binds me here.', 14, 0, 100, 0, 0, 77579, 0, 123114, 0, 'Odyn');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114284, 0, 0, 'We all deserve a chance to fly! Hey, do you really think the Wizard will de-greenify me?', 14, 0, 100, 0, 0, 77153, 0, 0, 0, 'ELFYRA - INTRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114284, 1, 0, 'I don\'t have any friends... well, apart from you, Galindre.', 14, 0, 100, 0, 0, 77154, 0, 0, 0, 'ELFYRA - INTRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114284, 2, 1, 'Together, we\'re unlimited!', 14, 0, 100, 0, 0, 77146, 0, 0, 0, 'ELFYRA - AGGRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114284, 3, 0, 'Sorry to bring you down!', 14, 0, 100, 0, 0, 77147, 0, 0, 0, 'ELFYRA - DEFY_GRAVITY');
