INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114284, 4, 0, 'Come see my minions, and let them be heard!', 14, 0, 100, 0, 0, 77149, 0, 0, 0, 'ELFYRA - SUMMON_ASSISTANTS');
