INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 0, 1, 'The master has not been himself for quite some time. Perhaps a feast will brighten his spirit.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - INTRO');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 1, 0, 'I don\'t believe the master is expecting you!', 14, 0, 100, 0, 0, 58644, 0, 0, 0, 'MORES - AGGRO');
