INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 2, 0, 'May I take your jacket?', 14, 0, 100, 0, 0, 58639, 0, 0, 0, 'MOROES - COAT_JACKET');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 2, 1, 'Mind your manners!', 14, 0, 100, 0, 0, 58638, 0, 0, 0, 'MOROES - COAT_JACKET');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 3, 0, 'I insist you dress for dinner!', 14, 0, 100, 0, 0, 58640, 0, 0, 0, 'MOROES - VANISH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 4, 0, 'Stop troubling the master\'s guests!', 14, 0, 100, 0, 0, 58642, 0, 0, 0, 'MOROES - GHASTLY');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 5, 0, 'Nothing a good sprinkling of sawdust can\'t handle.', 14, 0, 100, 0, 0, 58647, 0, 0, 0, 'MOROES - KILL');
