INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 6, 0, 'Perhaps your party would be more comfortable at the children\'s table.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - WIPE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 6, 1, 'The guards really must do a better job of keeping out the ruffians.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - WIPE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 7, 0, 'Seems I\'ll be... retiring... for... the evening...', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - DEATH');
