INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 7, 2, 'I fear that I am currently... indisposed...', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114312, 7, 3, 'I will just need... a moment...', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'MOROES - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114323, 0, 0, '|TInterface\\Icons\\SPELL_FIRE_TWILIGHTFLAMEBREATH.BLP:20|t%s begins to cast |cFFFF0000|Hspell:227573|h[Guardian\'s Breath]|h|r!', 41, 0, 100, 0, 0, 0, 0, 124841, 0, 'Guarm to Player');
