INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114328, 1, 0, 'Quickly now! Get rid of them!', 14, 0, 100, 0, 0, 77094, 0, 0, 0, 'COGGLESTON - KARA_KAZHAM');
