INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114328, 6, 0, 'It\'s working! It\'s working!', 14, 0, 100, 0, 0, 77106, 0, 0, 0, 'COGGLESTON - CONVERSATION');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114329, 0, 0, 'Zee true love, eet burnses!', 14, 0, 100, 0, 0, 77095, 0, 0, 0, 'LUMINORE - HEAT_WAVE');
