INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 6, 0, 'Mortals never learn.', 14, 0, 100, 0, 0, 77539, 0, 0, 0, 'NIGHTBANE - WIPE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 6, 1, 'Pitiful... as expected.', 14, 0, 100, 0, 0, 77538, 0, 0, 0, 'NIGHTBANE - WIPE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 6, 2, 'You called me here only to die at my feet?', 14, 0, 100, 0, 0, 77540, 0, 0, 0, 'NIGHTBANE - WIPE');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 7, 0, 'It is... never... over...', 14, 0, 100, 0, 0, 77541, 0, 0, 0, 'NIGHTBANE - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 7, 1, 'I will... never... be free...', 14, 0, 100, 0, 0, 77542, 0, 0, 0, 'NIGHTBANE - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 8, 0, 'Am I damned to answer the summons of mortals for all eternity?', 14, 0, 100, 0, 0, 77530, 0, 0, 0, 'NIGHTBANE - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 9, 0, 'So be it! You will suffer for your insolence!', 14, 0, 100, 0, 0, 77531, 0, 0, 0, 'NIGHTBANE - DEATH');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114895, 10, 0, 'The strange chill of a dark presence winds through the air', 41, 0, 100, 0, 0, 0, 0, 0, 0, 'NIGHTBANE - START TIMER');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114904, 0, 0, 'Line them up!', 14, 0, 100, 36, 0, 0, 0, 123594, 0, 'Soul Engine Jailer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114904, 1, 0, 'Ah, look, another prisoner!', 12, 0, 100, 0, 0, 0, 0, 123595, 0, 'Soul Engine Jailer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114904, 2, 0, 'Just in time for the next ferry. It\'s off to the Soul Engine with you!', 12, 0, 100, 0, 0, 0, 0, 123746, 0, 'Soul Engine Jailer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114926, 0, 0, 'HALT. Inspection required.', 12, 0, 100, 0, 0, 0, 0, 119793, 0, 'Arcane Sentinel to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 0, 0, 'There you are! Over here!', 12, 0, 100, 0, 0, 78391, 0, 124353, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 1, 0, 'Elisande knows about Vanthir\'s involvement in the rebellion. He is gone.', 12, 0, 100, 0, 0, 78392, 0, 123461, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 2, 0, 'I will move ahead and clear out a safe haven for the survivors. Stay strong, my friend.', 12, 0, 100, 0, 0, 78360, 0, 124361, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 3, 0, 'Now all we need is a to make contact with--', 12, 0, 100, 0, 0, 78554, 0, 123716, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 4, 0, 'Thalyssra?!', 12, 0, 100, 0, 0, 65777, 0, 124165, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 5, 0, 'What manner of spell is this?', 12, 0, 100, 0, 0, 78524, 0, 124166, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 6, 0, 'Fear not, First Arcanist. I will see to it that our friend here comes to no harm.', 12, 0, 100, 0, 0, 78594, 0, 124160, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114948, 7, 0, 'I am sadly familiar with Vanthir\'s captor.', 12, 0, 100, 0, 0, 78595, 0, 124161, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114958, 0, 0, 'Elisande must be stopped!', 12, 0, 100, 0, 0, 79245, 0, 123450, 0, 'Duskwatch Subjugator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114958, 1, 0, 'This is too much... if I must be labeled a traitor then so be it!', 12, 0, 100, 397, 0, 79245, 0, 123451, 0, 'Duskwatch Subjugator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114958, 2, 0, 'I knew there were others! I will no longer bloody my hands for Elisande!', 12, 0, 100, 0, 0, 79245, 0, 123449, 0, 'Duskwatch Subjugator to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114978, 0, 0, 'Oh, thank you! This masterpiece is worth my life a hundred times over.', 12, 0, 100, 603, 0, 0, 0, 123472, 0, 'Master Devlyn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114983, 0, 0, 'Behind you!', 12, 0, 100, 0, 0, 0, 0, 124769, 0, 'Thorvos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114983, 1, 0, 'I am right behind you! Go!', 12, 0, 100, 603, 0, 0, 0, 124770, 0, 'Thorvos to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114984, 0, 0, 'F-father... Run...', 12, 0, 100, 396, 0, 0, 0, 123479, 0, 'Valanth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114985, 0, 0, '%s spits on the corpse of the demon.', 16, 0, 100, 0, 0, 0, 0, 123473, 0, 'Scarleth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114985, 1, 0, 'I know where to find Shal\'Aran. Come, ladies. We are no longer safe in the city.', 12, 0, 100, 396, 0, 0, 0, 123476, 0, 'Scarleth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 0, 0, 'Let there be no slack in their chains!', 14, 0, 100, 0, 0, 53329, 0, 123540, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 1, 0, 'More come from Suramar every hour! Move out! Make room!', 14, 0, 100, 0, 0, 0, 0, 123543, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 2, 0, 'We must not leave the Soul Engine waiting!', 14, 0, 100, 0, 0, 78451, 0, 123542, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 3, 0, 'Ha! You walk willingly to your death, mortal!', 12, 0, 100, 0, 0, 78444, 0, 124519, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 4, 0, 'I will take great pleasure in grinding your soul to nothing!', 12, 0, 100, 0, 0, 78445, 0, 124520, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 5, 0, 'Shaza-kiel!', 12, 0, 100, 0, 0, 78446, 0, 124521, 0, 'Aargoss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 6, 0, 'You are nothing! Insignificant gnat!', 12, 0, 100, 0, 0, 78447, 0, 124522, 0, 'Aargoss');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (114998, 7, 0, 'No! ... I will be... fuel...', 12, 0, 100, 0, 0, 78448, 0, 124523, 0, 'Aargoss to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115014, 0, 0, 'Hmm... medium or well-done?', 12, 0, 100, 0, 0, 53329, 0, 102137, 0, 'Felsoul Imp to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115014, 1, 0, 'Tasty meats for me to eats.', 12, 0, 100, 0, 0, 55207, 0, 96658, 0, 'Felsoul Imp to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115018, 0, 0, 'I hope she sees this...', 12, 0, 100, 0, 0, 78550, 0, 124501, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115018, 1, 0, 'This way! Everyone into the boat!', 12, 0, 100, 0, 0, 78551, 0, 124502, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115018, 2, 0, 'Excellent timing as usual, Deline.', 12, 0, 100, 0, 0, 78552, 0, 124503, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115038, 0, 0, 'You\'ve got my attention, dragon. You\'ll find I\'m not as easily scared as the villagers below.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_MEDIVH_REPLY');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115038, 1, 0, 'You dare challenge me at my own dwelling? Your arrogance is astounding, even for a dragon.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_MEDIVH_REPLY');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115038, 2, 0, 'I do not know what you speak of, dragon... but I will not be bullied by this display of insolence. I\'ll leave Karazhan when it suits me!', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_MEDIVH_REPLY');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115038, 3, 0, 'He should not have angered me. I must go... recover my strength now...', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_MEDIVH_REPLY');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115049, 0, 0, 'See that these people escape the city, Deline. $n, stay with me.', 12, 0, 100, 0, 0, 78557, 0, 123713, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115049, 1, 0, 'This way!', 12, 0, 100, 397, 0, 78553, 0, 123714, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115078, 0, 0, 'He will be fine. The fruit will bring him back.', 12, 0, 100, 0, 0, 78582, 0, 124539, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115166, 0, 0, '|TInterface\\Icons\\INV_Icon_ShadowCouncilOrb_purple.blp:20|t An |cFFFF0000|Hspell:227920|h[Orb of Corruption]|h|r is following you!', 42, 0, 100, 0, 0, 0, 0, 123580, 0, 'Orb of Corruption to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115213, 0, 0, 'Your dabbling in the arcane has gone too far, Medivh. You\'ve attracted the attention of powers beyond your understanding. You must leave Karazhan at once!', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_OF_ARCANAGOS');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115213, 1, 0, 'A dark power seeks to use you, Medivh! If you stay, dire days will follow. You must hurry, we don\'t have much time!', 14, 0, 100, 0, 0, 77690, 0, 0, 0, 'IMAGE_OF_ARCANAGOS');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115213, 2, 0, 'You leave me no alternative. I will stop you by force if you won\'t listen to reason.', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_OF_ARCANAGOS');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115213, 3, 0, 'What have you done, wizard? This cannot be! I\'m burning from... within!', 14, 0, 100, 0, 0, 58641, 0, 0, 0, 'IMAGE_OF_ARCANAGOS');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115243, 0, 0, 'I have urgent news, Thalyssra.', 12, 0, 100, 0, 0, 78611, 0, 124022, 0, 'Image of Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115243, 1, 0, 'The portal to my estate is secure. You will want to hear this in person.', 12, 0, 100, 0, 0, 78612, 0, 124023, 0, 'Image of Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115247, 0, 0, 'Welcome home, First Arcanist. Thank you for coming on such short notice.', 12, 0, 100, 0, 0, 0, 0, 124055, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115247, 1, 0, 'I have heard terrible rumors about our friend Vanthir.', 12, 0, 100, 0, 0, 78616, 0, 124063, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115247, 2, 0, 'He is to be made an example for all to see.', 12, 0, 100, 0, 0, 78617, 0, 124535, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115247, 3, 0, 'I do not know. I only hope we are not too late.', 12, 0, 100, 0, 0, 78619, 0, 124537, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115247, 4, 0, 'Be safe, Thalyssra. I must return to the palace.', 12, 0, 100, 0, 0, 78406, 0, 124075, 0, 'Ly\'leth Lunastre to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115248, 0, 0, 'Thank you, Advisor. What news?', 12, 0, 100, 0, 0, 78615, 0, 124056, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115248, 1, 0, 'How much time do we have?', 12, 0, 100, 0, 0, 78618, 0, 124536, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115248, 2, 0, 'Keep your voice down, Silgryn. It is good to see you too.', 12, 0, 100, 0, 0, 78408, 0, 124164, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115248, 3, 0, 'One that will find our friend.', 12, 0, 100, 0, 0, 78525, 0, 124167, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 0, 0, 'I will find him. Preferably without being seen.', 12, 0, 100, 0, 0, 78416, 0, 123637, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 1, 0, 'This must be where he took many meals...', 12, 0, 100, 0, 0, 78409, 0, 123698, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 2, 0, 'There it is. We are coming, Vanthir.', 12, 0, 100, 0, 0, 78410, 0, 123699, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 3, 0, 'This garden likely gave him many hours of respite. His essence is strong and bright, here.', 12, 0, 100, 0, 0, 78411, 0, 123700, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 4, 0, 'I can feel him! He is still alive, at least.', 12, 0, 100, 0, 0, 78412, 0, 123701, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 5, 0, 'One of his suppliers must have served him here. Reminders of a simpler time.', 12, 0, 100, 0, 0, 78413, 0, 123702, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115277, 6, 0, 'Perfect. This will be enough to find him.', 12, 0, 100, 0, 0, 78414, 0, 123703, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115279, 0, 0, 'Is it true? We can be free of the Nightwell?', 12, 0, 100, 0, 0, 0, 0, 123650, 0, 'Shal\'dorei Insurgent to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115445, 0, 0, 'Free at last! Do what you can for the others, I\'ll be fine...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Shipwrecked Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115453, 0, 0, 'Freedom? I will not forget this!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Shipwrecked Grunt to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 0, 0, 'Talk to me, Vanthir...', 12, 0, 100, 0, 0, 78526, 0, 124129, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 1, 0, 'The moment he was taken from the Waning Crescent he felt such terrible fear... but not for himself.', 12, 0, 100, 0, 0, 78527, 0, 124130, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 2, 0, 'Fear for those that he sheltered, those he sacrificed for.', 12, 0, 100, 0, 0, 78528, 0, 124131, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 3, 0, 'He was taken somewhere dark and isolated. He began to feel hunger settling its roots in his gut.', 12, 0, 100, 0, 0, 78529, 0, 124132, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 4, 0, 'Hunger that did not pass, but grew...', 12, 0, 100, 0, 0, 78530, 0, 124133, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 5, 0, '... Until he could think of nothing else!', 12, 0, 100, 0, 0, 78531, 0, 124134, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 6, 0, 'He is near to withering. His thoughts are jumbled, frantic... I just need to...', 12, 0, 100, 0, 0, 78532, 0, 124135, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 7, 0, 'Focus! There!', 12, 0, 100, 0, 0, 78533, 0, 124136, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 8, 0, 'He is being held at the Terrace of Order! It must be time for the display...', 12, 0, 100, 0, 0, 78534, 0, 124137, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 9, 0, 'That took more out of me than I thought it would. You will have to free Vanthir without me. I am sorry.', 12, 0, 100, 0, 0, 78580, 0, 124154, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115557, 10, 0, 'Thank you, Silgryn. Be careful.', 12, 0, 100, 0, 0, 78581, 0, 124162, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 0, 0, 'Vanthir struggles to his feet.', 16, 0, 100, 0, 0, 0, 0, 125238, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 1, 0, 'Relief... at last...', 12, 0, 100, 0, 0, 78596, 0, 124540, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 2, 0, 'Aran\'ethil... The nightmare is over!', 12, 0, 100, 0, 0, 78597, 0, 124541, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 3, 0, 'They wanted to make an example. We have stolen their message and made it ours.', 12, 0, 100, 0, 0, 78568, 0, 124543, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 4, 0, 'All will see... hope... in the Dusk Lily.', 12, 0, 100, 0, 0, 78569, 0, 124544, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115604, 5, 0, '<Cough.> I need... to rest now. Thank you. Thank you for everything.', 12, 0, 100, 0, 0, 78566, 0, 124545, 0, 'Vanthir to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115607, 0, 0, 'You think to stop this demonstration? Very well. I will make an example of you first!', 12, 0, 100, 0, 0, 78599, 0, 124260, 0, 'Thoramir');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115607, 1, 0, 'She... was right... about you...', 12, 0, 100, 0, 0, 78607, 0, 124269, 0, 'Thoramir to Arluelle');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115608, 0, 0, 'I know.', 12, 0, 100, 0, 0, 78608, 0, 124270, 0, 'Silgryn to Arluelle');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115608, 1, 0, 'Lay still, my friend. We will get you out.', 12, 0, 100, 0, 0, 78610, 0, 124538, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115608, 2, 0, 'Welcome back, old friend.', 12, 0, 100, 0, 0, 78598, 0, 124542, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115608, 3, 0, 'We will see him back to safety. Go inform Thalyssra of our success.', 12, 0, 100, 0, 0, 78567, 0, 124546, 0, 'Silgryn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115631, 0, 0, 'Hurry! We\'re not safe here!', 12, 0, 100, 0, 0, 0, 0, 124196, 0, 'Desperate Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115631, 1, 0, 'I am safe... thank you.', 12, 0, 100, 0, 0, 0, 0, 124195, 0, 'Desperate Refugee');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115691, 0, 0, 'Your death is fated!', 12, 0, 100, 0, 0, 65814, 0, 116703, 0, 'Felborne Magus to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115691, 1, 0, 'I shall dispose of you, lowborn!', 12, 0, 100, 0, 0, 66024, 0, 116692, 0, 'Felborne Magus to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115697, 0, 0, 'There are demons everywhere...', 12, 0, 100, 0, 0, 55370, 0, 124198, 0, 'Desperate Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115697, 1, 0, 'This city has devolved to madness...', 12, 0, 100, 0, 0, 0, 0, 124194, 0, 'Desperate Refugee');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115703, 0, 0, 'We... we were almost killed!', 12, 0, 100, 0, 0, 0, 0, 124193, 0, 'Desperate Refugee');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115710, 0, 0, 'No need to delay. Onto the pad with you.', 12, 0, 100, 25, 0, 78389, 0, 123457, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115710, 1, 0, 'I will get you as close to the Crescent as I can. We will be in touch.', 12, 0, 100, 0, 0, 78390, 0, 124348, 0, 'Chief Telemancer Oculeth to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115736, 0, 0, 'She is waiting for you just outside.', 12, 0, 100, 1, 0, 78535, 0, 124504, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115736, 1, 0, 'Come. I do not want to keep her waiting.', 12, 0, 100, 0, 0, 78613, 0, 124024, 0, 'First Arcanist Thalyssra to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115991, 0, 0, 'Father!!', 12, 0, 100, 1, 0, 0, 0, 124735, 0, 'Emille to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (115992, 0, 0, 'I\'m going!', 12, 0, 100, 1, 0, 0, 0, 124736, 0, 'Brigitte to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116001, 0, 0, 'Praise all that is good... you are both safe. Come, let us get out of here!', 12, 0, 100, 0, 0, 0, 0, 123469, 0, 'Eneas to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116013, 0, 0, 'You think you can hide from Elisande? Die, traitors!', 12, 0, 100, 0, 0, 0, 0, 124768, 0, 'Duskwatch Punisher to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116050, 0, 0, 'The shal\'dorei will rule this world!', 12, 0, 100, 0, 0, 66019, 0, 116430, 0, 'Duskwatch Conjurer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116085, 0, 0, 'Arluelle places a dusky purple flower before the little planter.', 16, 0, 100, 0, 0, 0, 0, 124894, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116085, 1, 0, 'Do you remember me, outlander?', 12, 0, 100, 1, 0, 78536, 0, 124505, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116085, 2, 0, 'My brother rests here. My name is Arluelle.', 12, 0, 100, 1, 0, 78537, 0, 124507, 0, 'Arluelle to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116166, 0, 0, 'Look into my eyes... Find your death within...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Eye of Gurgh to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116206, 0, 0, 'My life for the Legion.', 12, 0, 100, 0, 0, 55364, 0, 98760, 0, 'Felsoul Wrathguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116291, 0, 0, 'You are doomed to fail!', 14, 0, 100, 0, 0, 82703, 0, 0, 0, 'Lord Kalgorath');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116427, 0, 0, 'You should not be here.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Felbound Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (116468, 0, 0, 'Leave this place, $c!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Felbound Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (117412, 0, 0, 'This cannot be...', 12, 0, 100, 0, 0, 65692, 0, 116434, 0, 'Felborne Trapper to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (117430, 0, 0, 'Did you hear that? It came from over here!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Felblade Guardian to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (117430, 1, 0, 'Nothing gets past the Legion.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Felblade Guardian to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (117431, 0, 0, 'An intruder? Show yourself!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Dread Warbringer to Player');
