INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129544, 2, 0, 'His shield is down. Strike now!', 12, 0, 100, 0, 0, 0, 0, 140716, 0, 'Tacha to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 0, 0, 'Haha! Now dat was an impressive death!', 12, 0, 100, 0, 0, 109296, 0, 150822, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 1, 0, 'Ya not be dreaming now, mon.', 12, 0, 100, 603, 0, 109300, 0, 150847, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 2, 0, 'Let me guess... ya got in over ya head?', 12, 0, 100, 0, 0, 109293, 0, 150789, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 3, 0, 'What you be doin\' here, mon?  It not your time to pass on... yet.', 12, 0, 100, 1, 0, 94633, 0, 140766, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 4, 0, 'Ah, ya come ta spend some quality time with Bwonsamdi?', 12, 0, 100, 0, 0, 109297, 0, 150823, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 5, 0, 'Tsk, tsk. I did not expect ta be seein\' you here so soon.', 12, 0, 100, 0, 0, 109294, 0, 150794, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129545, 6, 0, 'No, no, no, ya no good to me dead. I be able to return ya to life... for a price.', 12, 0, 100, 661, 0, 98420, 0, 140767, 0, 'Bwonsamdi to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129547, 0, 0, 'WE\'VE GOT OURSELVES A BRAWL!', 14, 0, 100, 92, 0, 0, 0, 149857, 0, 'Blacktooth Knuckleduster');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 0, 0, 'Ouch! What in blazes was that for?!', 12, 0, 100, 5, 0, 101551, 0, 140798, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 1, 0, 'Bloody mainlanders...', 12, 0, 100, 661, 0, 101553, 0, 140799, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 2, 0, 'Hey! Watch where you\'re pointing that thing!', 12, 0, 100, 396, 0, 101555, 0, 140800, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 3, 0, 'Ow!', 12, 0, 100, 33, 0, 101559, 0, 140802, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 4, 0, 'Clumsy oaf.', 12, 0, 100, 33, 0, 101559, 0, 140801, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129575, 5, 0, 'The bloodflies are really biting these days, aren\'t they?', 12, 0, 100, 6, 0, 101561, 0, 140803, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129577, 0, 0, 'Zak\'rajan must pay for imprisoning us!', 12, 0, 100, 22, 0, 0, 0, 140780, 0, 'Released Spirit to Malfunctioning Golem');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129586, 0, 0, 'Zandalar holds many secrets. Perhaps we have stumbled upon one we were not meant to reveal.', 12, 0, 100, 1, 0, 0, 0, 140748, 0, 'Batu to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129590, 0, 0, 'All will melt.', 12, 0, 100, 0, 0, 0, 0, 152756, 0, 'Prince Rathaw to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129590, 1, 0, 'Life burns to ash...', 12, 0, 100, 0, 0, 0, 0, 152773, 0, 'Prince Rathaw to Boss Tak');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129603, 0, 0, 'Zak\'rajan has enslaved us for countless ages. Free us....', 12, 0, 100, 1, 0, 0, 0, 140791, 0, 'Released Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129603, 1, 0, 'Even de loa have abandoned us.', 12, 0, 100, 1, 0, 0, 0, 140792, 0, 'Released Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129603, 2, 0, 'Spirits be powerful weapons... if you can control \'em.', 12, 0, 100, 1, 0, 0, 0, 140790, 0, 'Released Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129603, 3, 0, 'Why does Bwonsamdi refuse to let us into de Other Side?', 12, 0, 100, 1, 0, 0, 0, 140786, 0, 'Released Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129620, 0, 0, 'Aaahhh!!', 14, 0, 100, 0, 0, 102550, 0, 140616, 0, 'Jo\'chunga to Hir\'eek');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129625, 0, 0, 'Ah, so Maynard sends his goons to solve his problems, as he always does. Well come, goon. I will take care of you as I did the others!', 12, 0, 100, 1, 0, 0, 0, 148365, 0, 'Witch of the Woods to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129625, 1, 0, 'You are a fool to get in my way!', 12, 0, 100, 0, 0, 0, 0, 141406, 0, 'Witch of the Woods to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129625, 2, 0, 'My husband\'s exploitation of his workers will never end...', 12, 0, 100, 0, 0, 0, 0, 141414, 0, 'Witch of the Woods to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129627, 0, 0, 'Give me an axe and I will chop!', 12, 0, 100, 1, 0, 0, 0, 152463, 0, 'Algerson Lumberjack');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129627, 1, 0, 'Show me wicker, show me beasts... I will fight them all!', 12, 0, 100, 1, 0, 0, 0, 152462, 0, 'Algerson Lumberjack');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129627, 2, 0, 'Kul Tirans never go down without a fight!', 12, 0, 100, 1, 0, 0, 0, 152461, 0, 'Algerson Lumberjack');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129641, 0, 0, 'Ugh, clumsy oaf.', 12, 0, 100, 6, 0, 101562, 0, 140801, 0, 'Corlain Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129641, 1, 0, 'The coven will take your little town!', 14, 0, 100, 274, 0, 97735, 0, 140838, 0, 'Unmasked Witch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129643, 0, 0, 'The word is given. Let us ride to Corlain!', 12, 0, 100, 0, 0, 97797, 0, 143565, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 0, 0, 'This guy don\'t seem too bright.', 12, 0, 100, 1, 0, 101039, 0, 141695, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 1, 0, 'Seems they got dragged thataway, up the glacier ta the northwest.', 12, 0, 100, 25, 0, 101041, 0, 141769, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 2, 0, 'One o\' our axes. We\'re on the right track.', 12, 0, 100, 1, 0, 101036, 0, 141594, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 3, 0, 'One o\' our rucksacks. Looks like we\'ve found the right place.', 12, 0, 100, 1, 0, 101037, 0, 141595, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 4, 0, 'This guy really ruffles m\' feathers!', 12, 0, 100, 1, 0, 101038, 0, 143217, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129655, 5, 0, 'Welcome ta the Roughnecks!', 12, 0, 100, 1, 0, 101040, 0, 143201, 0, 'Boss Tak to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129669, 0, 0, 'I\'m going to go check on the workers in the woods. If we\'re in danger here, I worry about their safety out there!', 12, 0, 100, 1, 0, 0, 0, 148188, 0, 'Benjamin Algerson to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129669, 1, 0, 'A witch... why is a witch targeting our operation?! I need to get out of here. I\'ll tell my father!', 12, 0, 100, 1, 0, 0, 0, 148364, 0, 'Benjamin Algerson to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129670, 0, 0, 'Algerson is tearing down the forests! We cannot let him continue his abuse of nature!', 12, 0, 100, 1, 0, 0, 0, 148781, 0, 'Lyssa Treewarden to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129672, 0, 0, 'Let my spirit go free. Please...', 12, 0, 100, 0, 0, 107073, 0, 153655, 0, 'Tortured Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129672, 1, 0, 'We are coming for you, Zak\'rajan...', 12, 0, 100, 0, 0, 107070, 0, 153654, 0, 'Tortured Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129672, 2, 0, 'Zak\'rajan betrayed us all!', 12, 0, 100, 0, 0, 107065, 0, 140929, 0, 'Tortured Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129672, 3, 0, 'De end... at last.', 12, 0, 100, 0, 0, 110685, 0, 153653, 0, 'Tortured Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129672, 4, 0, 'You\'ll be next, Zak\'rajan...', 12, 0, 100, 0, 0, 107071, 0, 153654, 0, 'Tortured Spirit to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129708, 0, 0, 'Back in the day, I could have handled a swarm of these slithering worms. They came at me in the dark. I was overrun! Their orders seem to be, boiled down, \"Hurry! Azshara wants her prize!\"', 12, 0, 100, 0, 0, 0, 0, 139353, 0, 'Rauloo the Hardened to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129709, 0, 0, 'It was just little murlocs here, then there was a storm, then naga streaming up the beach. They took me first, and I am rather embarrassed.', 12, 0, 100, 0, 0, 0, 0, 138799, 0, 'Crosh to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129710, 0, 0, 'These naga are afraid. Something else is driving them. This is not merely loyalty to Queen Azshara.', 12, 0, 100, 0, 0, 0, 0, 153372, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129712, 0, 0, 'The naga are looking for something here. It will be for Azshara. We must stop them, or obfuscate their goal.', 12, 0, 100, 0, 0, 0, 0, 139350, 0, 'Kono to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129714, 0, 0, 'Spiders... wooden beast... I\'ll take \'em all down!', 12, 0, 100, 1, 0, 0, 0, 140920, 0, 'Algerson Lumberjack to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129717, 0, 0, 'Oh my! This is just awful. These are orders to kill those loyal to Gral!', 12, 0, 100, 1, 0, 0, 0, 153657, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129717, 1, 0, 'Queen Azshara is planning something, and she is not alone.', 12, 0, 100, 0, 0, 0, 0, 153658, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129717, 2, 0, 'Gral got too close.', 12, 0, 100, 0, 0, 0, 0, 153714, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129717, 3, 0, 'The Zandalari do not know the horror before them. They must be warned.', 12, 0, 100, 1, 0, 0, 0, 153659, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129717, 4, 0, 'Get to Atal\'gral. I truly hope the temple still stands.', 12, 0, 100, 0, 0, 0, 0, 153713, 0, 'Akru to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129730, 0, 0, 'You will not escape me, Ateena! No matter what dark voodoo you command!', 12, 0, 100, 0, 0, 101749, 0, 140949, 0, 'Princess Talanji');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129730, 1, 0, 'She must be nearby. I will not give up, not when we are dis close to killing de leader of dese monsters.', 12, 0, 100, 0, 0, 101778, 0, 140950, 0, 'Princess Talanji');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129732, 0, 0, 'Yer doom be at hand!', 14, 0, 100, 0, 0, 98129, 0, 140366, 0, 'Skycap\'n Kragg');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129732, 1, 0, 'Bombs away!', 14, 0, 100, 0, 0, 98131, 0, 140369, 0, 'Skycap\'n Kragg');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129732, 2, 0, 'Watch out below, ye barnacle-bottomed swine!', 14, 0, 100, 0, 0, 98132, 0, 140379, 0, 'Skycap\'n Kragg');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129732, 3, 0, 'Don\'t mind Sharkbait. He just needs ta... empty his hold!', 14, 0, 100, 0, 0, 98130, 0, 140367, 0, 'Skycap\'n Kragg');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (129745, 0, 0, 'Spiders are one thing, but tree men? Count me OUT!', 12, 0, 100, 603, 0, 0, 0, 140912, 0, 'Entangled Lumberjack to Player');
