INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130824, 0, 0, 'To the abyss with you!', 12, 0, 100, 0, 0, 98671, 0, 142518, 0, 'Zeth\'jir Seacaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130824, 1, 0, 'Perish, landwalker!', 12, 0, 100, 0, 0, 98672, 0, 142519, 0, 'Zeth\'jir Seacaller to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130833, 0, 0, 'Dis one\'s on you, kid! Hop aboard!', 12, 0, 100, 1, 0, 0, 0, 143530, 0, 'Captain Grez\'ko to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130840, 0, 0, 'Even stone... breaks...', 12, 0, 100, 0, 0, 103690, 0, 139328, 0, 'Lei-zhi Oppressor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130840, 1, 0, 'It\'s not a very good island, anyway!', 14, 0, 100, 0, 0, 0, 0, 143162, 0, 'Lei-zhi Oppressor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130840, 2, 0, 'It\'s broken free! Get back!', 14, 0, 100, 0, 0, 0, 0, 143159, 0, 'Lei-zhi Oppressor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130840, 3, 0, 'We\'re doomed!', 14, 0, 100, 0, 0, 0, 0, 143161, 0, 'Lei-zhi Oppressor to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130844, 0, 0, 'Prepare yourself, $n. We have no idea what may be ahead.', 12, 0, 100, 0, 0, 101733, 0, 140958, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130844, 1, 0, 'By de loa... No! Now we see de true power of de blood trolls. Oh father... why did you ignore dem for so long?!', 12, 0, 100, 0, 0, 101734, 0, 140959, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130871, 0, 0, 'Don\'t blink, Alliance.', 14, 0, 100, 0, 0, 104056, 0, 0, 0, 'Skaggit to Skaggit');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130899, 0, 0, 'If you want something done right...', 12, 0, 100, 0, 0, 110743, 0, 150951, 0, 'Commander Wormwood to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130899, 1, 0, 'You will never... stop her...', 12, 0, 100, 0, 0, 110744, 0, 150956, 0, 'Commander Wormwood to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130904, 0, 0, 'Thank the tides not everyone has lost their damn mind. Half the house has gone mad!', 12, 0, 100, 1, 0, 109737, 0, 143268, 0, 'Samuel Williams to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130904, 1, 0, 'Don\'t have to tell me twice!', 12, 0, 100, 396, 0, 109736, 0, 143274, 0, 'Samuel Williams to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130905, 0, 0, '!Gracias a los dos por su ayuda! Preparate para ir a la cueva de Colatriste a ayudar a BenÂ´jin cuando esto funcione.', 12, 0, 100, 1, 0, 0, 0, 0, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130912, 0, 0, 'You will not take my head today!', 14, 0, 100, 7, 0, 97368, 0, 142721, 0, 'Shadowblade Razi');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130930, 0, 0, 'Thanks to you, if I die... I die with my honor intact...', 12, 0, 100, 0, 0, 0, 0, 143295, 0, 'Zaluto to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130933, 0, 0, 'I will do some scouting and meet you further down the path!', 12, 0, 100, 1, 0, 103328, 0, 143210, 0, 'Inquisitor Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 0, 0, 'Let us see this done.', 12, 0, 100, 1, 0, 108762, 0, 143233, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 1, 0, 'Those laborers... they\'re being held by tidesage magic! We must help them.', 12, 0, 100, 0, 0, 108784, 0, 143269, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 2, 0, 'Make your way to the shoreline on the other side of the yard. Find a young Proudmoore cadet--Taelia. Go!', 12, 0, 100, 0, 0, 104192, 0, 143272, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 3, 0, 'Sea to sea, ashes to ashes, tide to tide.', 12, 0, 100, 0, 0, 108751, 0, 143136, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 4, 0, 'Fly, you fools!', 12, 0, 100, 0, 0, 108779, 0, 143155, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 5, 0, 'The currents will carry ye, oh fallen.', 12, 0, 100, 0, 0, 108752, 0, 143138, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 6, 0, 'Tidemother cleanse your wicked flesh.', 12, 0, 100, 0, 0, 108753, 0, 143139, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 7, 0, 'May the abyssal depths welcome your weary soul.', 12, 0, 100, 0, 0, 108754, 0, 143140, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 8, 0, 'Run to your families. We will settle this.', 12, 0, 100, 0, 0, 108780, 0, 143156, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 9, 0, 'Fear not, brother. The waves judge not your surface.', 12, 0, 100, 0, 0, 108757, 0, 143143, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 10, 0, 'Good, this blade is still sharp. A sharp blade is a safe one.', 12, 0, 100, 0, 0, 108785, 0, 143127, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 11, 0, 'Wonder not for whom the lighthouse shines. It shines for thee.', 12, 0, 100, 0, 0, 108758, 0, 143152, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 12, 0, 'You are free now, brothers and sisters.', 12, 0, 100, 0, 0, 99518, 0, 143158, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 13, 0, 'Hear the storm\'s song, and know peace.', 12, 0, 100, 0, 0, 108759, 0, 143153, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 14, 0, 'Be gone! Your work is done here.', 12, 0, 100, 0, 0, 108782, 0, 143160, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 15, 0, 'It is not safe here. You must leave this place.', 12, 0, 100, 0, 0, 108783, 0, 143163, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 16, 0, 'Let your soul wash away from its mortal shell, and flow into the waters.', 12, 0, 100, 0, 0, 108760, 0, 143154, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 17, 0, 'Let your heart know hatred no longer.', 12, 0, 100, 0, 0, 108761, 0, 143278, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 18, 0, 'We must not let these lost souls wander any longer. I would have words with you, $p.', 12, 0, 100, 0, 0, 108749, 0, 143133, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 19, 0, 'I know not what darkness took your mind, sister, but it will torment our people no longer.', 12, 0, 100, 0, 0, 108793, 0, 143167, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 20, 0, 'I am glad the innocent are safe, but much remains to be done.', 12, 0, 100, 0, 0, 108777, 0, 143329, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 21, 0, 'The ocean\'s mists hide much, but this beacon shall let us reach them.', 12, 0, 100, 0, 0, 108786, 0, 143130, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 22, 0, 'A bit waterlogged, but still legible. These incantations will serve us well.', 12, 0, 100, 0, 0, 108787, 0, 143131, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130934, 23, 0, 'I will prepare the ritual to release these souls. Meet me by the shoreline just north of here.', 12, 0, 100, 0, 0, 108750, 0, 143282, 0, 'Brother Pike to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130947, 0, 0, 'I am not a fighter. I will meet you deeper in the gorge when you have freed my brethren.', 12, 0, 100, 0, 0, 0, 0, 145726, 0, 'Tsunga to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130948, 0, 0, 'Villain! You are as a candle, better when burnt out.', 12, 0, 100, 0, 0, 0, 0, 143380, 0, 'King Da\'ka to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130948, 1, 0, 'Thus falls... King Da\'ka.', 12, 0, 100, 0, 0, 0, 0, 143382, 0, 'King Da\'ka to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130952, 0, 0, 'House Stormsong will rule this land!', 12, 0, 100, 0, 0, 101362, 0, 143420, 0, 'Sister Ava to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130952, 1, 0, 'You will kneel before your queen, peasant!', 12, 0, 100, 0, 0, 101363, 0, 143422, 0, 'Sister Ava');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130952, 2, 0, 'I\'ll have you drawn and quartered for that!', 12, 0, 100, 0, 0, 101364, 0, 143424, 0, 'Sister Ava');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130952, 3, 0, 'She will... take you... too...', 12, 0, 100, 0, 0, 101360, 0, 143421, 0, 'Sister Ava to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130953, 0, 0, 'I will deal you a mighty thumping!', 12, 0, 100, 0, 0, 0, 0, 142973, 0, 'Da\'kani Pugilist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130953, 1, 0, 'By this hand, I will supplant your teeth!', 12, 0, 100, 554, 0, 0, 0, 142971, 0, 'Da\'kani Pugilist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130953, 2, 0, 'Hold, enough!', 12, 0, 100, 38, 0, 0, 0, 153186, 0, 'Da\'kani Pugilist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130953, 3, 0, 'Get you gone, you knave!', 12, 0, 100, 38, 0, 0, 0, 142974, 0, 'Da\'kani Pugilist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130953, 4, 0, 'Oh, I die...', 12, 0, 100, 38, 0, 0, 0, 153190, 0, 'Da\'kani Pugilist to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130954, 0, 0, 'I will beat you into clamorous whining!', 12, 0, 100, 38, 0, 0, 0, 142972, 0, 'Da\'kani Highbrow to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130993, 0, 0, 'For thousands of years, the Lightforged fought demons in the Twisting Nether. The Xenedar was the only home we knew.', 12, 0, 100, 1, 0, 0, 0, 141912, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130993, 1, 1, 'Now, for the first time in ages, we can walk beneath a sky not befouled by the Legion\'s madness... and feel the earth under our hooves again.', 12, 0, 100, 1, 0, 0, 0, 141913, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130993, 2, 2, 'But we must not grow complacent. One war ends, the next begins. Such is the life of a soldier.', 12, 0, 100, 1, 0, 0, 0, 141914, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130993, 3, 3, 'Your duty begins in Stormwind. Explore this world of Azeroth. Get to know the people and places we have sworn to defend.', 12, 0, 100, 1, 0, 0, 0, 141915, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (130993, 4, 4, 'And always walk in the Light, $p.', 12, 0, 100, 1, 0, 0, 0, 141916, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131014, 0, 0, 'I have everything I need. Speak to me when you\'re ready to begin the ritual.', 12, 0, 100, 0, 0, 0, 0, 152494, 0, 'Rikal to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131018, 0, 0, 'Zul... never said... I would lose...', 12, 0, 100, 0, 0, 103900, 0, 144042, 0, 'Warlord Kara-na');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131062, 0, 0, 'Retreat! Live to fight another day.', 12, 0, 100, 0, 0, 110727, 0, 156753, 0, 'King Rastakhan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131084, 0, 0, 'Abyss take you...', 12, 0, 100, 0, 0, 98687, 0, 144697, 0, 'Zeth\'jir Tideguard to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131085, 0, 0, 'For the queen!', 12, 0, 100, 0, 0, 98682, 0, 142515, 0, 'Zeth\'jir Tidelord to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131103, 0, 0, 'So Marshal Reade believes that you are the key to driving back the coven?', 12, 0, 100, 1, 0, 97774, 0, 143121, 0, 'Captain Joan Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131103, 1, 0, 'I have my doubts... but I will carry out my orders.', 12, 0, 100, 1, 0, 97775, 0, 143122, 0, 'Captain Joan Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131103, 2, 0, 'I will join the others and await your command, $c.', 12, 0, 100, 66, 0, 97776, 0, 143123, 0, 'Captain Joan Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131111, 0, 0, 'Heh, It\'s about time.', 12, 0, 100, 1, 0, 97773, 0, 143119, 0, 'Lieutenant Sterntide to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131112, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 25, 0, 0, 0, 1150, 0, 'Cutwater Striker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131132, 0, 0, 'You will never take me alive!', 12, 0, 100, 0, 0, 0, 0, 143304, 0, 'Mojoba to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131135, 0, 0, 'Fine, Vol\'dun has to be safer than dis place!', 12, 0, 100, 0, 0, 0, 0, 143303, 0, 'Razjuto to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131151, 0, 0, 'Nuq wgah uhnish kaaxth ', 12, 0, 100, 0, 0, 98462, 0, 0, 0, 'Faceless Destroyer to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131153, 0, 0, 'The end is upon you. G\'huun will be free. Azeroth will be ours.', 12, 0, 100, 0, 0, 0, 0, 143561, 0, 'Overlord Kraxis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131153, 1, 0, 'Too late. G\'huun cannot be contained. All will fall. All will die.', 12, 0, 100, 0, 0, 0, 0, 143562, 0, 'Overlord Kraxis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131208, 0, 0, 'You all did... WHAT? I\'m not missing this story, I\'ll head to Gloom Hollow.', 12, 0, 100, 0, 0, 0, 0, 142990, 0, 'Crez to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131209, 0, 0, 'Huh... well, I guess fighting a horde of blood trolls would be an interesting story. Fine, I\'ll go to Gloom Hollow.', 12, 0, 100, 0, 0, 0, 0, 142991, 0, 'Korkush to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131210, 0, 0, 'Fine, I\'ll go to Gloom Hollow and see how crazy this\'ll get.', 12, 0, 100, 0, 0, 0, 0, 142992, 0, 'Yash to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131213, 0, 0, 'What dat noise be? I\'ll guard da princess, you go investigate.', 12, 0, 100, 1, 0, 105508, 0, 133724, 0, 'Rokhan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131213, 1, 0, 'I be takin\' some of da tortollans and movin\' to Zul\'jan by foot. Good luck, you two.', 12, 0, 100, 1, 0, 105576, 0, 144355, 0, 'Rokhan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 0, 0, 'My Chosen! Your master needs you!', 12, 0, 100, 0, 0, 103524, 0, 143045, 0, 'Vol\'jamba');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 1, 0, 'Get away! I command you!', 12, 0, 100, 0, 0, 103525, 0, 143046, 0, 'Vol\'jamba');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 2, 0, 'You are strong... but my Sight is stronger!', 12, 0, 100, 0, 0, 103526, 0, 143047, 0, 'Vol\'jamba');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 3, 0, 'Dis... dis isn\'t happening!', 14, 0, 100, 0, 0, 103527, 0, 143048, 0, 'Vol\'jamba');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 4, 0, 'No! I will not die here! It is not my time!', 12, 0, 100, 0, 0, 103528, 0, 143050, 0, 'Vol\'jamba to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 5, 0, 'We both know i will not die by your hand. You cannot stop me.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Vol\'jamba');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131241, 6, 0, 'It is you who will die here!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Vol\'jamba to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131249, 0, 0, 'Listen up! We need to get you ready to fight, and quick.', 12, 0, 100, 1, 0, 111748, 0, 146234, 0, 'Taelia to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131253, 0, 0, 'Objective cleared. Moving to forward position. Deliver completion to new location.', 12, 0, 100, 0, 0, 104783, 0, 143322, 0, 'Titan Keeper Hezrel to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131256, 0, 0, 'ERROR... INTRUSION...DET-DETECTED... SCANNING FOR... CORRUPTION.', 12, 0, 100, 0, 0, 0, 0, 143092, 0, 'Titan Keeper Bolcan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131256, 1, 0, 'CORRUPTION RELEASE IMMINENT. ACT-ACTIVATING SEC-SEC-SECURITY PROTOCOLS. INTR-INTRUDER FOUND.', 12, 0, 100, 0, 0, 0, 0, 143093, 0, 'Titan Keeper Bolcan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131256, 2, 0, 'ELIMINATE INTRUDER, CL-CLEANSE. CLEANSE.', 12, 0, 100, 0, 0, 0, 0, 143094, 0, 'Titan Keeper Bolcan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131258, 0, 0, 'With my brethren freed, I will seek out the Zandalari in the village. There is nothing left for us here.', 12, 0, 100, 0, 0, 0, 0, 145727, 0, 'Tsunga to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131272, 0, 0, 'Help! Save us!', 12, 0, 100, 0, 0, 109259, 0, 153693, 0, 'Brennadam Villager to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131272, 1, 0, 'Not them, not again!', 12, 0, 100, 0, 0, 109273, 0, 153140, 0, 'Brennadam Villager');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131272, 2, 0, 'The villager groans in pain.', 16, 0, 100, 0, 0, 0, 0, 158243, 0, 'Brennadam Villager');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 1, 0, 'Rot and wither!', 14, 0, 100, 0, 0, 101026, 0, 148211, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 2, 0, 'G\'huun be everywhere!', 14, 0, 100, 0, 0, 101025, 0, 148212, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 3, 0, '|TINTERFACE\\ICONS\\INV_TikiMan2_Bloodtroll.blp:20|t Elder Leaxa begins to cast |cFFF00000|Hspell:264603|h[Blood Mirror]|h|r!', 41, 0, 100, 0, 0, 101025, 0, 157334, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 5, 0, 'Time for de sacrifice!', 14, 0, 100, 0, 0, 101029, 0, 148174, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 200, 0, 'For de glory of G\'huun!', 14, 0, 100, 0, 0, 101028, 0, 148173, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131318, 202, 0, 'My blood for G\'huun...', 14, 0, 100, 0, 0, 101030, 0, 148175, 0, 'Elder Leaxa');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131326, 0, 0, 'When our city faced its darkest hour, the heroes of Azeroth fought with us to drive out the Legion. Some did so reluctantly.', 12, 0, 100, 1, 0, 0, 0, 143276, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131326, 1, 1, 'But the sin\'dorei--the blood elves--showed us respect and understanding. Their kinship proved there were others in Azeroth who share our values.', 12, 0, 100, 1, 0, 0, 0, 143277, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131326, 2, 2, 'Now it is our turn to demonstrate the strength and honor we bring to the Horde.', 12, 0, 100, 1, 0, 0, 0, 143288, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131326, 3, 3, 'Go to Orgrimmar. Join with our allies and forge a new future together.', 12, 0, 100, 1, 0, 0, 0, 143293, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131326, 4, 4, 'As you travel the roads of Azeroth, tell those you meet of the nightborne\'s proud history... and show them that we live in seclusion no longer.', 12, 0, 100, 1, 0, 0, 0, 143292, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131331, 0, 0, 'Talanji! Talanji!', 12, 0, 100, 4, 0, 0, 0, 161493, 0, 'Casteless Zandalari to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131345, 0, 0, 'Some will tell you that the Light is the only path. The one weapon that can stop the enemies of Azeroth.', 12, 0, 100, 1, 0, 0, 0, 143203, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131345, 1, 0, 'But we see alternatives. Many roads--many possibilities--that are open to us.', 12, 0, 100, 1, 0, 0, 0, 143204, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131345, 2, 0, 'Use the lessons I have taught you to maintain control. Become a weapon for the Alliance... even as you skirt the edge of darkness.', 12, 0, 100, 1, 0, 0, 0, 143206, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131354, 0, 0, 'Is dat Tika?!?!', 12, 0, 100, 5, 0, 0, 0, 143428, 0, 'Beastmother Jabati to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131354, 1, 0, 'Oh, it be so good to see you little one! $n... Thank you.', 12, 0, 100, 5, 0, 0, 0, 143429, 0, 'Beastmother Jabati to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131354, 2, 0, 'Who be a good little face munching raptor baby? You are! You are!', 12, 0, 100, 0, 0, 0, 0, 143430, 0, 'Beastmother Jabati to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131359, 0, 0, 'So, you found us out. You must think yourself so clever.', 12, 0, 100, 1, 0, 97778, 0, 143377, 0, 'Suspicious Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131359, 1, 0, 'No matter. Soon your discovery will die with you. Attack, my pets!', 12, 0, 100, 1, 0, 97779, 0, 143378, 0, 'Suspicious Refugee to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131359, 2, 0, 'You\'re strong, but you will fall to the coven like everyone else!', 12, 0, 100, 1, 0, 97780, 0, 143379, 0, 'Sister Littlefield');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131359, 3, 0, 'But... I was promised... greatness...', 12, 0, 100, 1, 0, 97781, 0, 143381, 0, 'Sister Littlefield to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 1, 0, 'Corruption blooms!', 14, 0, 100, 0, 0, 109962, 0, 143953, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 4, 0, '|TInterface\\Icons\\ABILITY_EARTHEN_PILLAR.BLP:20|t %s targets you with |cFFFF0000|Hspell:259720|h[Upheaval]|h|r!', 42, 0, 100, 0, 0, 0, 0, 161956, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 5, 0, 'Grow!', 14, 0, 100, 0, 0, 109960, 0, 143951, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 200, 0, 'Harvest!', 14, 0, 100, 0, 0, 109965, 0, 143939, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 201, 0, 'Rot must spread!', 14, 0, 100, 0, 0, 109964, 0, 143938, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131383, 202, 0, 'Everything... decays...', 14, 0, 100, 0, 0, 109966, 0, 143960, 0, 'Sporecaller Zancha');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131409, 0, 0, 'I thought I was going to die out here!', 12, 0, 100, 603, 0, 0, 0, 142905, 0, 'Stranded Battalion Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131409, 1, 0, 'I will not forget your bravery.', 12, 0, 100, 1, 0, 98683, 0, 142906, 0, 'Stranded Battalion Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131409, 2, 0, 'You saved my life!', 12, 0, 100, 603, 0, 0, 0, 142909, 0, 'Stranded Battalion Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131409, 3, 0, 'These naga are monsters!', 12, 0, 100, 603, 0, 0, 0, 142902, 0, 'Stranded Battalion Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131409, 4, 0, 'Avenge those of us who could not be saved.', 12, 0, 100, 1, 0, 0, 0, 142903, 0, 'Stranded Battalion Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131442, 0, 0, 'Night\'s coming! Be sure to keep those watchfires going! Call out if you need more fuel.', 12, 0, 100, 1, 0, 103543, 0, 144198, 0, 'Leandro Royston');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131448, 0, 0, 'You put out those fires and I\'ll set up what we need in the lab. Oh, what fun this shall be!', 12, 0, 100, 1, 0, 103469, 0, 144771, 0, 'Warren Ashton to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131461, 0, 0, 'There is something out there. There is darkness. The naga\'s motives here were not self-driven. I will find out more. Grow in strength and wisdom, $n, we will need it.', 12, 0, 100, 0, 0, 0, 0, 147358, 0, 'Spirit of Gral to Player');
