INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131633, 1, 0, 'My power... my curse... it cannot be...', 12, 0, 100, 0, 0, 0, 0, 154954, 0, 'Summoner Mepjila to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 0, 0, 'Alright men, let us take this tower and drive the coven from Corlain!', 14, 0, 100, 5, 0, 99178, 0, 146288, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 1, 0, 'Glory to House Waycrest!', 14, 0, 100, 0, 0, 99179, 0, 146289, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 2, 0, 'Excellent work, Inquisitor! This tower will provide us a vantage over Corlain and all of its holdings.', 12, 0, 100, 5, 0, 99181, 0, 146291, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 3, 0, 'The barrier is down! Waycrest Manor lies ahead!', 14, 0, 100, 5, 0, 102093, 0, 147862, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 4, 0, 'Inquisitors! This is our chance to rescue Lord and Lady Waycrest... and put an end to this insufferable coven!', 12, 0, 100, 5, 0, 102972, 0, 149007, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 5, 0, 'Stick close, and do not stop your advance until we\'ve breached the manor gates!', 12, 0, 100, 1, 0, 102973, 0, 149009, 0, 'Marshal Everit Reade to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131636, 6, 0, 'For House Waycrest!', 12, 0, 100, 22, 0, 102974, 0, 149010, 0, 'Marshal Everit Reade to Player');
