INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 1, 0, 'Fight it, Marshal!', 12, 0, 100, 5, 0, 103807, 0, 149017, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 2, 0, 'Marshal, no...', 12, 0, 100, 661, 0, 103808, 0, 149020, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 3, 0, 'Mother... what have you done?', 12, 0, 100, 6, 0, 103809, 0, 149021, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 4, 0, 'Inquisitors, the final battle is upon you. May the winds be at your back.', 12, 0, 100, 5, 0, 108232, 0, 149022, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 5, 0, 'I will join you inside, Inquisitor.', 12, 0, 100, 1, 0, 108233, 0, 149025, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 6, 0, 'I may not be of much use in combat, but my mother must be brought to justice... and I must be there to see it through.', 12, 0, 100, 1, 0, 108234, 0, 149027, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131638, 7, 0, 'For my father, and for Drustvar!', 12, 0, 100, 6, 0, 108235, 0, 149026, 0, 'Lucille Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131639, 0, 0, 'I\'ll leave you to the mystery, then... while I see about cracking more coven skulls!', 12, 0, 100, 1, 0, 101048, 0, 147950, 0, 'Inquisitor Mace to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131639, 1, 0, 'You\'ve screeched at the wrong woman, witch!', 12, 0, 100, 0, 0, 104624, 0, 149013, 0, 'Inquisitor Mace to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131639, 2, 0, 'Aye ma\'am. We\'ll put the coven in the ground.', 12, 0, 100, 66, 0, 104625, 0, 149023, 0, 'Inquisitor Mace to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131639, 3, 0, 'Inquisitors, on me! Glory to House Waycrest!', 12, 0, 100, 22, 0, 104626, 0, 149024, 0, 'Inquisitor Mace to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131640, 0, 0, 'Thank you, $c! I was not looking forward to meeting the end of a pike.', 12, 0, 100, 5, 0, 104206, 0, 149097, 0, 'Inquisitor Notley to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131640, 1, 0, 'Keep moving, we\'ve got this! Go for the eyes, Winston!', 12, 0, 100, 0, 0, 104207, 0, 149012, 0, 'Inquisitor Notley to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131641, 0, 0, 'You found him! We should leave quickly. The coven has this place surrounded!', 12, 0, 100, 5, 0, 102567, 0, 146309, 0, 'Inquisitor Yorrick to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131641, 1, 0, 'Your alacrity is appreciated, $c. We will convene with Miss Waycrest.', 12, 0, 100, 1, 0, 104213, 0, 149095, 0, 'Inquisitor Yorrick to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131641, 2, 0, 'We\'ll cover the flanks. $n, stay with Lucille and the Marshal!', 12, 0, 100, 0, 0, 104214, 0, 149014, 0, 'Inquisitor Yorrick to Player');
