INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131863, 4, 0, 'Fresh meat!', 14, 0, 100, 0, 0, 0, 0, 137666, 0, 'Raal the Gluttonous');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131864, 0, 0, 'Death consumes all!', 14, 0, 100, 0, 0, 0, 0, 150395, 0, 'Gorak Tul');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131864, 1, 0, 'Rise!', 14, 0, 100, 0, 0, 0, 0, 150400, 0, 'Gorak Tul');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131864, 2, 0, 'Death... cannot hold me...', 14, 0, 100, 0, 0, 0, 0, 150396, 0, 'Gorak Tul');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131872, 0, 0, 'We\'re so close now! Just have to get my gear...', 12, 0, 100, 0, 0, 0, 0, 144251, 0, 'Sur\'jan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131872, 1, 0, 'I can feel it... my power returning!', 14, 0, 100, 0, 0, 0, 0, 136717, 0, 'Sur\'jan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131872, 2, 0, 'YES! It is time... we will bring down Urok!', 14, 0, 100, 0, 0, 0, 0, 136718, 0, 'Sur\'jan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131872, 3, 0, 'We can waste no more time. Urok must be put down!', 14, 0, 100, 0, 0, 0, 0, 136719, 0, 'Sur\'jan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 0, 0, 'Meddlin\' elf! These ruins belong to the Dark Iron!', 12, 0, 100, 0, 0, 0, 0, 144815, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 1, 0, 'Ye\'ll never find it...', 12, 0, 100, 0, 0, 0, 0, 144820, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 2, 0, 'Ye\'re too late. We found...', 12, 0, 100, 0, 0, 0, 0, 144822, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 3, 0, 'I\'m goin\' to enjoy buryin\' you.', 12, 0, 100, 0, 0, 0, 0, 144818, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 4, 0, 'We\'ll dig your feet out from under ye!', 12, 0, 100, 0, 0, 0, 0, 144817, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131877, 5, 0, 'An elf... diggin\'... hah!', 12, 0, 100, 0, 0, 0, 0, 144823, 0, 'Dark Iron Saboteur to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 0, 0, 'Right this way! Once I begin my scrying, all of my attention will be focused upon it.', 12, 0, 100, 25, 0, 0, 0, 144569, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 1, 0, 'I trust you can handle any interruptions, yes?', 12, 0, 100, 6, 0, 0, 0, 144570, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 2, 0, 'I must try again. You did well, though I had imagined our enemies to be somewhat taller.', 12, 0, 100, 273, 0, 0, 0, 144571, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 3, 0, 'These results are so confusing. If only these-- what would you call them? Beardlings?-- would let me concentrate!', 12, 0, 100, 5, 0, 0, 0, 144572, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 4, 0, 'No, no, this cannot be right. By my calculations it should be--!!', 12, 0, 100, 274, 0, 0, 0, 144573, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131878, 5, 0, 'Taken from under our very noses! The nerve!', 12, 0, 100, 5, 0, 0, 0, 144574, 0, 'Apprentice Telemancer Astrandis to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131879, 0, 0, 'I\'ll help reinforce the village\'s defenses. I will meet you there!', 12, 0, 100, 1, 0, 103331, 0, 144266, 0, 'Inquisitor Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (131879, 1, 0, '$n will assist you in whatever you might need, Master. I will go see if I can\'t help Mayor Royston.', 12, 0, 100, 1, 0, 103347, 0, 150544, 0, 'Inquisitor Cleardawn to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132017, 0, 0, 'Whoa, you\'re really strong!', 12, 7, 100, 0, 0, 0, 0, 147697, 0, 'Ancel Mildenhall to Flowing Honey');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132017, 1, 0, 'Take that!', 12, 0, 100, 0, 0, 0, 0, 147698, 0, 'Ancel Mildenhall to Flowing Honey');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132017, 2, 0, 'Wh... why would he leave his boot here? Unless he was eaten!?', 12, 0, 100, 669, 0, 0, 0, 146487, 0, 'Ancel Mildenhall to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132017, 3, 0, 'It didn\'t stand a chance!', 12, 7, 100, 0, 0, 0, 0, 147699, 0, 'Ancel Mildenhall to Bubbling Mead');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132017, 4, 0, 'Dad\'s been carrying that book with him everywhere lately...', 12, 0, 100, 669, 0, 0, 0, 146489, 0, 'Ancel Mildenhall to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132114, 0, 0, 'If Vol\'jin had had his trusty invulnerability totem wit\' him on da Broken Shore, he\'d still be wit\' us today. Don\'t be makin\' da same mistake!', 12, 0, 100, 1, 0, 0, 0, 144720, 0, 'Griftah to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132134, 0, 0, '$n, follow me. Everyone else, join Rokhan at de northern line.', 12, 0, 100, 1, 0, 103865, 0, 144930, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132134, 1, 0, 'Move out! For de future of Zandalar!', 12, 0, 100, 4, 0, 103867, 0, 144509, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132134, 2, 0, 'Let us go, champion.', 12, 0, 100, 0, 0, 103866, 0, 144673, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132160, 0, 0, 'My blood... feeds them.', 12, 0, 100, 0, 0, 104277, 0, 146466, 0, 'Vinemaster Smagh to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132161, 0, 0, 'No... we must... grow.', 12, 0, 100, 0, 0, 104279, 0, 146468, 0, 'Vinemaster Orgsteen to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132162, 0, 0, 'We will... never go back...', 12, 0, 100, 0, 0, 104281, 0, 146470, 0, 'Vinemaster Guff to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132163, 0, 0, 'All who fail to obey must be culled!', 12, 0, 100, 0, 0, 111124, 0, 145810, 0, 'Tideguard Pontus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132163, 1, 0, 'Gaze upon the shadow of your demise.', 12, 0, 100, 0, 0, 103714, 0, 149629, 0, 'Tideguard Pontus');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132163, 2, 0, 'They await... in the depths...', 12, 0, 100, 0, 0, 111125, 0, 145873, 0, 'Tideguard Pontus to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132189, 0, 0, 'We\'ve got it, lads! Move out!', 14, 0, 100, 0, 0, 0, 0, 147779, 0, 'Urda Direflame to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132189, 1, 0, 'I\'ll deliver the Eye AND your head to Moira!', 12, 0, 100, 0, 0, 0, 0, 147795, 0, 'Urda Direflame to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132189, 2, 0, 'Curse ye...', 12, 0, 100, 0, 0, 0, 0, 147796, 0, 'Urda Direflame to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 0, 0, 'Get back, or ye\'ll be meetin\' my hammer!', 12, 0, 100, 0, 0, 99197, 0, 146307, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 1, 0, 'Alright, ye jus\' lead the way...', 12, 0, 100, 1, 0, 99199, 0, 146457, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 2, 0, 'Just ye try! Ye\'ll taste my hammer, witch!', 12, 0, 100, 5, 0, 99202, 0, 146451, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 3, 0, 'Hmm. Ain\'t much here, but I got a notion how to maximize its potential.', 12, 0, 100, 1, 0, 99379, 0, 146381, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 4, 0, 'Let me work this fer a spell. Meanwhile, I believe I spied an old hand cannon in the repair heap over there.', 12, 0, 100, 0, 0, 99380, 0, 146382, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 5, 0, 'Fetch it fer me, an\' we\'ll see what we can do.', 12, 0, 100, 0, 0, 99381, 0, 146383, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 6, 0, 'That\'s the one! Jus\' toss it atop the table there.', 12, 0, 100, 1, 0, 99382, 0, 146384, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 7, 0, 'I\'ll need te modify this here hand cannon a mite first...', 12, 0, 100, 0, 0, 99384, 0, 146386, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 8, 0, 'Ah, there we go!', 12, 0, 100, 1, 0, 99385, 0, 146387, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132193, 9, 0, 'All that\'s left is to give the girl a name. How bout we call her \"Witchrend?\"', 12, 0, 100, 1, 0, 99386, 0, 146388, 0, 'Angus Ballaster to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132230, 0, 0, 'Puny runt! I gonna crush ya like a bug!', 14, 0, 100, 0, 0, 0, 0, 157025, 0, 'Warmother Molaka');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132230, 1, 0, 'Me be da one... crushed?', 12, 0, 100, 0, 0, 103862, 0, 157026, 0, 'Warmother Molaka to Princess Talanji');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132292, 0, 0, '<sigh> I\'m never getting out of here.', 12, 0, 100, 18, 0, 0, 0, 148771, 0, 'Raimond Mildenhall');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132315, 0, 0, 'You... already... lost...', 12, 0, 100, 0, 0, 98353, 0, 144275, 0, 'Bloodfire Battle-Slave to Princess Talanji');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132332, 0, 0, 'Come, champion of de Horde. Let us pay my father a visit. Zul... we will speak later.', 12, 0, 100, 0, 0, 102082, 0, 144868, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132338, 0, 0, '|TINTERFACE\\ICONS\\ABILITY_RACIAL_ROCKETBARRAGE.BLP:20|t You are targeted by |cFFFF0000|Hspell:260838|h[Homing Missile]|h|r!', 42, 0, 100, 0, 0, 0, 0, 0, 0, 'Homing Missile Stalker to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132355, 0, 0, 'Eureka! I\'m free!!', 12, 0, 100, 0, 0, 0, 0, 148769, 0, 'Raimond Mildenhall to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132363, 0, 0, 'Thank you, friend.', 12, 0, 100, 0, 0, 0, 0, 149849, 0, 'Peace-Loving Gorilla to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132374, 0, 0, 'Oh, thanks again for taking care of those spiders. Not sure how much longer I would\'ve lasted.', 12, 0, 100, 1, 0, 101567, 0, 146365, 0, 'Elsie Wright to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132374, 1, 0, 'If it\'s all the same to you, I\'m getting out of here before more of those things come from the mine!', 12, 0, 100, 1, 0, 101568, 0, 146366, 0, 'Elsie Wright to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132377, 0, 0, 'Thank you! I thought I was a goner.', 12, 0, 100, 1, 0, 0, 0, 152793, 0, 'Bleak Hills Miner to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132377, 1, 0, 'I\'m never stepping foot in this mine again!', 12, 0, 100, 5, 0, 0, 0, 152795, 0, 'Bleak Hills Miner to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132377, 2, 0, 'They came out of nowhere!', 12, 0, 100, 5, 0, 0, 0, 152794, 0, 'Bleak Hills Miner to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132382, 0, 0, 'There will be those who doubt you. Who question your resolve, your ability to harness powers that have caused the downfall of weaker wills.', 12, 0, 100, 1, 0, 0, 0, 143205, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132382, 1, 0, 'Together, we will prove them wrong.', 12, 0, 100, 1, 0, 0, 0, 143615, 0, '');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132394, 0, 0, 'I finally have enough for a hot meal!', 12, 0, 100, 1, 0, 0, 0, 146421, 0, 'Zandalari Vagrant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132394, 1, 0, 'Thank you, $pr. Loa watch over you.', 12, 0, 100, 1, 0, 0, 0, 146474, 0, 'Zandalari Vagrant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132394, 2, 0, 'Thank you, friend. You\'re very kind.', 12, 0, 100, 0, 0, 0, 0, 146420, 0, 'Zandalari Vagrant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132394, 3, 0, 'Better than nothing...', 12, 0, 100, 0, 0, 0, 0, 146419, 0, 'Zandalari Vagrant to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 0, 0, 'Go, my pets! Rip them apart!', 14, 0, 100, 1, 0, 104175, 0, 150290, 0, 'Sister Noella Briarwood to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 1, 0, 'Acolyte, tear that barricade down!', 14, 0, 100, 0, 0, 104176, 0, 150291, 0, 'Sister Noella Briarwood to Briarwood Eradicator');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 2, 0, 'My demolisher will rip you to shreds! You only forestall the inevitable!', 14, 0, 100, 0, 0, 104177, 0, 150292, 0, 'Sister Noella Briarwood to Briarwood Annihilator');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 3, 0, 'A minor inconvenience! Once my forces regroup, this miserable town will be ours!', 14, 0, 100, 0, 0, 104178, 0, 150418, 0, 'Sister Noella Briarwood to Briarwood Siegebreaker');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 4, 0, 'Interlopers! You will die!', 12, 0, 100, 0, 0, 104165, 0, 145949, 0, 'Sister Noella Briarwood to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132395, 5, 0, 'We will... have revenge...', 12, 0, 100, 0, 0, 104172, 0, 145945, 0, 'Sister Noella Briarwood to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132624, 0, 0, 'De ravine is ours! Regroup! We must plan de next assault.', 14, 0, 100, 0, 0, 103862, 0, 144810, 0, 'Princess Talanji to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132631, 0, 0, 'Ugh... I can still feel de touch of de Other Side.', 12, 0, 100, 5, 0, 109705, 0, 146446, 0, 'King Rastakhan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132631, 1, 0, 'My loa has called me back to de land of de living. Dere is work to be done.', 12, 0, 100, 25, 0, 109706, 0, 146447, 0, 'King Rastakhan to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132637, 0, 0, 'It is more than a simple prick of de flesh we are dealing with. Betrayal cuts down to de soul.', 12, 0, 100, 0, 0, 0, 0, 145894, 0, 'Jamil Abul\'housin to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132706, 0, 0, 'Dey are here to kill de king!', 12, 0, 100, 0, 0, 0, 0, 145996, 0, 'Zeb\'ahari Fishermon to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132708, 0, 0, 'Zeb\'ahari is overrun!', 12, 0, 100, 1, 0, 98963, 0, 145999, 0, 'Zeb\'ahari Mender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132708, 1, 0, 'I will go to de king!', 12, 0, 100, 0, 0, 0, 0, 146001, 0, 'Zeb\'ahari Mender to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132709, 0, 0, 'I am getting out of here!', 12, 0, 100, 5, 0, 0, 0, 146003, 0, 'Zeb\'ahari Villager to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132713, 0, 0, 'Shoulda... sprung... for the... ejector seat package...', 14, 0, 100, 0, 0, 97411, 0, 0, 0, 'Mogul Razdunk to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132717, 0, 0, 'Dis is not good!', 12, 0, 100, 603, 0, 0, 0, 145997, 0, 'Zeb\'ahari Beastkeeper to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132720, 0, 0, 'Leonard will provide you some back up. Just give him a whistle and he\'ll do what he does best.', 12, 0, 100, 0, 0, 0, 0, 146452, 0, 'Hawkmaster Lloyd to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132979, 0, 0, 'Zul gonna deliver us from de mad king! You are only gettin\' in de way!', 12, 0, 100, 0, 0, 98773, 0, 146059, 0, 'Loa-Speaker Kihara to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132979, 1, 0, 'Too late... he knows... de king... is here...', 12, 0, 100, 0, 0, 98788, 0, 146060, 0, 'Loa-Speaker Kihara to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132985, 0, 0, 'I will never forget dis!', 12, 0, 100, 4, 0, 0, 0, 146085, 0, 'Zandalari Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132985, 1, 0, 'Praise de loa! You are my hero!', 12, 0, 100, 4, 0, 0, 0, 146088, 0, 'Zandalari Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132985, 2, 0, 'Thank de loa, I am alive!', 12, 0, 100, 4, 0, 0, 0, 146089, 0, 'Zandalari Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132985, 3, 0, 'I owe you one, stranger. I will never return to dis accursed swamp again!', 12, 0, 100, 4, 0, 0, 0, 146087, 0, 'Zandalari Soldier to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132988, 0, 0, 'Okay, let\'s set up over here!', 14, 0, 100, 0, 0, 103862, 0, 146091, 0, 'Patch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132988, 1, 0, 'Just bring it in... easy... easy...', 12, 0, 100, 0, 0, 105736, 0, 146092, 0, 'Patch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132988, 2, 0, 'Okay... stop!', 12, 0, 100, 0, 0, 105737, 0, 146094, 0, 'Patch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132988, 3, 0, 'Whoa! You got their attention now! Big horde incoming!', 14, 0, 100, 0, 0, 105706, 0, 148006, 0, 'Patch to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 0, 0, 'While I am thankful for your help, I must ask that you not rummage through my daughter\'s undergarments...', 12, 0, 100, 1, 0, 103407, 0, 149816, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 1, 0, 'I don\'t believe I left them with Lucy\'s letters, but I suppose it\'s prudent to be sure.', 12, 0, 100, 6, 0, 103408, 0, 149817, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 2, 0, 'Oh, you\'ve found it! Thank the tides!', 12, 0, 100, 5, 0, 103409, 0, 149821, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 3, 0, 'Please inform the happy couple I will be down shortly.', 12, 0, 100, 1, 0, 103410, 0, 149822, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 4, 0, 'Oh, and do convey my apologies for the delay.', 12, 0, 100, 1, 0, 103411, 0, 149823, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 5, 0, 'Alexander, have you prepared the--wait, what was that!?', 12, 0, 100, 1, 0, 103418, 0, 149834, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (132994, 6, 0, 'An ambush! Lucy, get behind me! Guards, to arms!', 14, 0, 100, 5, 0, 103419, 0, 149837, 0, 'Lord Arthur Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133006, 0, 0, 'Why would my husband put his notebook in Lucille\'s pack? Are you daft?', 12, 0, 100, 5, 0, 105947, 0, 149818, 0, 'Lady Meredith Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133006, 1, 0, 'We are looking for a book. Don\'t you think we would have already checked the bookcase?', 12, 0, 100, 6, 0, 105948, 0, 149819, 0, 'Lady Meredith Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133006, 2, 0, 'Are you trying to waste our time?', 12, 0, 100, 5, 0, 105949, 0, 149820, 0, 'Lady Meredith Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133006, 3, 0, 'Idiot! There can be no witnesses!', 12, 0, 100, 5, 0, 105951, 0, 149841, 0, 'Lady Meredith Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133006, 4, 0, 'You had better!', 12, 0, 100, 5, 0, 105952, 0, 149843, 0, 'Lady Meredith Waycrest to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (133007, 200, 0, 'Devour! Consume! Spread!', 14, 0, 100, 0, 0, 102970, 0, 151959, 0, 'Unbound Abomination');
