INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 2, 0, 'Good! Next... hm, that\'s odd. We weren\'t expecting rain...', 12, 0, 100, 0, 0, 152718, 0, 184168, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 3, 0, 'Private Cole will run you through the rest of the drills. I need to discuss this storm with the helmsman.', 12, 0, 100, 0, 0, 152734, 0, 184106, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 4, 0, 'Everyone below decks! Now', 12, 0, 100, 0, 0, 152735, 0, 177674, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 5, 0, 'Well, he was right. Now, where\'s the rest of the expedition?', 12, 0, 100, 0, 0, 152749, 0, 178671, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 6, 0, 'Let\'s go. Those monsters have my son.', 12, 0, 100, 0, 0, 152747, 0, 178649, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 7, 0, 'I know. I raised him to overcome any challenge.', 12, 0, 100, 0, 0, 152748, 0, 191076, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 8, 0, 'We have to stop the sacrifice! But it would take an army to get through all of the undead...', 12, 0, 100, 0, 0, 152752, 0, 178699, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156280, 9, 0, 'We don\'t have any other choice. My son\'s life is on the line.', 12, 0, 100, 0, 0, 152753, 0, 178700, 0, 'Captain Garrick');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156295, 0, 0, 'Ха! Что-то подозрительное? Наши великолепные владыки других сообщений и не посылают. Тут я тебе не помогу, пилигрим.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Курьер Рокалай to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156333, 0, 0, 'Тысячу лет я готовился к этому бою.', 12, 0, 100, 0, 0, 152045, 0, 0, 0, 'Преобразованный тьмой ученик to Player');
INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156365, 0, 0, 'We cannot allow the Forsworn to turn us against each other!', 12, 0, 100, 669, 0, 0, 0, 0, 0, 'Burdened Aspirant to Player');
