INSERT IGNORE `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `SoundPlayType`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (156610, 0, 0, 'DNever seen a storm like that...', 12, 0, 100, 0, 0, 0, 0, 178945, 0, 'Austin Huxworth');
